css:
@-moz-document url-prefix("http://www.sailorfuku.com/") {
html {background: url('http://www.zupimages.net/up/15/43/553l.png') !important;
}
#game_header a#logo { background: url('http://zupimages.net/up/15/43/q1vk.png') no-repeat !important;  
    height: 100px;

}

#game_header { background-color: rgba(255, 248, 162, 0.79) !important;
        background-image: none;
    height: 50px;
}

#game_header #navigation li a { color: white;
      text-shadow: #FF83A1 1px 1px, #FF83A1 -1px 1px, #FF83A1 -1px -1px, #FF83A1 1px -1px;
    font-size: 13pt;
    font-family: Times, serif !important;
    font-variant: small-caps;
    padding-top: 10px; 
}

#game_header a:hover {
  background-color: white;
    height: 40px;
}

.menu-links a {
    background: rgb(150, 232, 241);
    color: white;
    background-image: none;
     box-shadow: white 2px 2px, white -2px 2px, white -2px -2px, white 2px -2px;
    border-radius: 6px 6px 0px 0px ;
}
.menu-links a:hover {
    background: #10A8F5;
    color: white;
}

.menu-links a.active { 
    background: #EDF4F4;
    color: #66ABCE;
}


#contentContainer {
   background-color:rgba(255, 251, 201, 0.82);
}


#world_map #map_place_name {
    background: rgba(255, 255, 255, 0.49);
}

#notifications .notification {
     background: rgb(204, 243, 122);
    color: white;
    box-shadow: white 2px 2px, white -2px 2px, white -2px -2px, white 0px 0px;
    margin: 1px;
    letter-spacing: 1px;
    border-radius: 3px 3px 0 0;
}
#notifications .notification:hover {
    color: #71C73F;
}

#notifications .notification .legend {
    letter-spacing: 1px;
}

   .menu-ressources {
          background: rgb(246, 204, 248);
          background-image: none;
         box-shadow: white 2px 2px, white -2px 2px, white -2px -2px, white 2px -2px;
}

#notifications .filler { display: none;}

a {
    color: #A94DBB;
}

div.tendances div.excellent {
    border-color: #6DC944;
    background: rgba(236, 241, 163, 0.89);
}

div.tendances div.excellent:hover {
    background: rgba(254, 255, 246, 0.89);
       border-color: #6DC944;
}


div.bubble .text {
    color: #BA6B9E;
      background-color: rgba(255, 255, 255, 0.62);
     border: 2px #FFB751 solid;
}

div.bubble .triangle {display:none;}

.avatar_resized { border-radius: 5px;}
DIV.inew {
  BORDER-COLOR: #14A110 #4DC5AA #145E25 #69CDAF;
}


.profilRessources {
      background-color: rgba(235, 243, 116, 0.7);
    border: 2px solid #73CC41;
    background-image: none;
    border-radius: 10px;
}

.profilRessources h4 {
  display: none;
}

.articleCadre .data {
      background: #73CC41;
    color: white;
}
.wishlist .article .remove {
        background-color: #FFDC5F;
}
.wishlist .article .remove:hover {
        background-color: #FFB74F;
}

.shop .article:hover {
    box-shadow: 0 0 9px #FF5574;
}

.shop .article {
        border: 5px solid #F4E0F5;
    background:#F4E0F5;
}

span.spch-bub-inside em {
    background: #10A8F5;
}

input.button {
    background-color: #15A8F1;
    border: #CBEFFF solid 2px;
}
input.button:hover {
    background-color: #1F7DAD;
    border: #CBEFFF solid 2px;
}

#actualiteDetails {
    background: #EBF66F;
    border-color: #77CA42;
}

textarea {
    background: #EFF2F0;
    border: 2px solid #287CA7;
}

.actualite:hover {
    background: #E3ED6D;
}
.actualite:hover .fleche {
    color: #FFFFFF;
}
    

#world_map #map_place_name .map_place_name {
    color: rgb(219, 122, 144);
    font-family: 'Time New';
}

.menuDecoration {
  background: rgba(164, 255, 79, 0.21);
}

#game_status {
    background: none;
    box-shadow: none;
}

#amies2 .amie {
    background-color: #FFB3C9;
    border: 3px double #FFB2C9;
    color: white;
}

#amies2 .amie:hover { 
background-color: #DBA4E4;
    border: 3px double #DBA4E4;
    color: white;
}

#amies2 h4 {
    color: #D084DF;
}

#amies2 .amiesListe { 
background-color: rgb(255, 227, 237);
    border: 2px solid rgb(214, 129, 230);;
    color: white;
}


#amies2 a.infos {
    background-color: rgba(219, 164, 228, 0.66);
    color: white;
}

#amies2 .changement {
    color: white;
}

#amies2 .amiesListe .data {
    background: #DD9FEA;
}

a {
    color:#207DAF;
}
a:hover {
    color:#023C5D;
}
h2 { 
    color: #1BAD70;
}
#jeuContenu {
        background: rgb(236, 243, 113);
        box-shadow: 2px 2px 5px #6DC83F;   
}

#connection input {
    color: #12A7F7;
    background: #F9DFEF;
}
#blocAnnonces a.annonce .intitule {
color:#016192
}

#blocAnnonces {
    background-color: rgba(215, 244, 255, 0.91);
}
#connection .submit, #inscription .submit {
    background-color: #E3F27C !important;
    color: #1FC37F !important;
    border: #1FC37F solid 2px;
}
 
#connection .submit:hover, #inscription .submit:hover {
    background-color: #E3F27C !important;
    color: white;
    border: #1FC37F solid 2px;
}
TD.tc3 {
    background: rgba(255, 251, 201, 0.82);
}
TD.tc2 {
    background: rgba(255, 251, 201, 0.82);
}

.msg {
      background-color: #CDF4D0;
}

input.longinput {
      background: #EFF2F0;
    border: 2px solid #287CA7;
}

 .conl input {
    background-color: #15A8F1;
    border: #CBEFFF solid 2px;
}
.con1 input:hover {
    background-color: #1F7DAD !important;
    border: #CBEFFF solid 2px;
}  
div.blockform input[type="submit"] {
background-color: #15A8F1;
    border: #CBEFFF solid 2px;
}
div.blockform input[type="submit"]:hover {
  background-color: #1F7DAD;
    border: #CBEFFF solid 2px;
}  
.menu-links a .spch-bub-inside .point {
    border-left-color: black;
}
.menu-links a .spch-bub-inside em {
    background: black;
}


.menu-bottom .profile-interaction button.like {
  height: 50px;
  background-image: -webkit-linear-gradient(#FED2FF,#DAA7E2);
    border: 2px solid #CC88D6;
    text-shadow: none;
    
}
.menu-bottom .profile-interaction button.like:hover  {
  height: 50px;
  background-image: -webkit-linear-gradient(#10A8F5,#CAEEFE);
    border: 2px solid #11A9F6;
    text-shadow: none;
}

.menu-bottom .profile-interaction button {
  background-image: -webkit-linear-gradient(#FED2FF,#DAA7E2);
    border: 2px solid #CC88D6;
    text-shadow: none;
}
.menu-bottom .profile-interaction button:hover {
  background-image: -webkit-linear-gradient(#10A8F5,#CAEEFE);
    border: 2px solid #11A9F6;
    text-shadow: none;
}
    
}

