css:
@-moz-document domain("twitter.com"), domain("tweetdeck.twitter.com") {
.hearty .prf .lst-profile .icon-favorite:before,
.hearty .tweet-action .icon-favorite:before,
.hearty .tweet-detail-action .icon-favorite:before {
	-webkit-filter: grayscale(1) brightness(2.4);
	filter: grayscale(1) brightness(2.4);
}

.hearty .prf .lst-profile a:hover .icon-favorite:before,
.hearty .prf .lst-profile a:focus .icon-favorite:before,
.hearty .tweet-action:hover .icon-favorite:before,
.hearty .tweet-action:focus .icon-favorite:before,
.hearty .tweet-detail-action:hover .icon-favorite:before,
.hearty .tweet-detail-action:focus .icon-favorite:before {
	-webkit-filter: grayscale(1) brightness(1.2);
	filter: grayscale(1) brightness(1.2);
}

.HeartAnimationContainer *,
.Icon.Icon--heartBadge {
	visibility: hidden;
}

.HeartAnimationContainer:after {
	-webkit-filter: grayscale(1) brightness(3);
	filter: grayscale(1) brightness(3);
}

.hearty .is-favorite .tweet-action .icon-favorite:before,
.hearty .is-favorite .tweet-detail-action .icon-favorite:before,
.ProfileTweet-action--favorite .ProfileTweet-actionButton:hover .HeartAnimationContainer:after,
.ProfileTweet-action--favorite .ProfileTweet-actionButton:focus .HeartAnimationContainer:after,
.favorited .ProfileTweet-action--favorite .ProfileTweet-actionButtonUndo .HeartAnimationContainer:after {
	-webkit-filter: none;
	filter: none;
}

.favorited .ProfileTweet-action--favorite .ProfileTweet-actionButtonUndo .HeartAnimationContainer:after {
	animation: toronto-bounce-in 500ms;
}

@keyframes toronto-bounce-in {
  40% {
    -webkit-transform: scale(1.35);
            transform: scale(1.35); }
}

.hearty .icon-favorite:before,
.HeartAnimationContainer:after,
.activity-type.activity-type-favorited_mention,
.activity-type.activity-type-favorite {
	background-size: 1em 1em;
	background-repeat: no-repeat;
	display: block;
	width: 1em;
	height: 1em;
	content: "";
	background-image: url("data:image/svg+xml;base64,");
}
}
