css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dm.tsdm.tv") {
  body {
	color: #d4d4d4;
	background: #101010;
}
  .box960-mid-box {
	border-left: 0px;
	border-right: 0px;
	background: transparent;
}
  .box960-mid-minfo {
	border-left: 0px;
	border-right: 0px;
	background: transparent;
}
  .box960-mid {
	height: 12px;
	background-position: 0 -450px;
	display: none;
}
  .box960-mid {
	display: none !important;
}
  .box960-top {
	display: none !important;
}
  .uInfo {
	top: 70px;
	left: 10px;
}
  a {
	color: #d4d4d4;
}
  .playurl .bfdz li a {
	background: transparent;
}
  #forum_btn {
	display: none !important;
}
  .m-intro .ctext p {
	color: #d4d4d4;
}
  .reply {
	background: transparent;
}
  #talk div {
	background: transparent;
}
  #talk .tc textarea {
	background: transparent;
	color: #d4d4d4;
}
  .box960-bot {
	display: none !important;
}
  .search-form input.text {
	color: #d4d4d4;
	background: #666;
}
  .here {
	color: #d4d4d4;
	background: transparent;
}
  .m-info .mtext span {
	color: #d4d4d4;
}
  input, button, textarea, select {
	background-color: transparent;
	color: inherit;
}
  #head #navigation li a:hover, #head #navigation li.selected a {
	background: transparent;
}
  #head #navigation li a {
	color: #000;
}
  #head #submenu {
	background: transparent;
}
  #head #submenu big {
	color: inherit;
}
  #head {
	opacity: 0.8;
}
  .titbar {
	background: transparent;
}
  .titbar span {
	display: none !important;
}
  #anime_w1 {
	background-color: transparent !important;
}
  #anime_w2 {
	background-color: transparent !important;
}
  #anime_w3 {
	background-color: transparent !important;
}
  #anime_w4 {
	background-color: transparent !important;
}
  #anime_w5 {
	background-color: transparent !important;
}
  #anime_w6 {
	background-color: transparent !important;
}
  #anime_w7 {
	background-color: transparent !important;
}
  #anime_w8 {
	background-color: transparent !important;
}
  #shell a:link {
	COLOR: inherit;
}
  #shell a:hover {
	COLOR: #d4d4d4;
}
  img {
	opacity: 0.8;
}
  .m-info .mtext em {
	color: inherit;
}
}
