css:
@-moz-document domain("web.whatsapp.com"){
  body {
      background: rgba(32, 32, 32, 255) url(http://orig09.deviantart.net/04cb/f/2011/307/b/8/peregrine_falcon__library_by_siamon89-d4ewnjx.jpg) no-repeat center center;
      background-size: 1920px 1080px;
  }
  .app-wrapper::after {
      position: absolute;
      width: 0px;
      height: 0px;
  }
  .pane-chat-tile {
      background: rgba(0, 0, 0, 255) url(http://orig09.deviantart.net/04cb/f/2011/307/b/8/peregrine_falcon__library_by_siamon89-d4ewnjx.jpg) no-repeat center center;
      background-position: 67.5% 50%;
      background-size: 1920px 1080px;
      opacity: 0.25;
  }
  .pane-chat-tile-container {
      -webkit-box-flex: 1;
      -webkit-flex: 1 1 0;
      -ms-flex: 1 1 0;
      flex: 1 1 0;
      position: relative;
      background: linear-gradient(to bottom, #323232, #323232 100%);
  }
  .drawer-container-mid,
  .drawer-container-right,
  .pane-chat,
  .pane-intro {
      opacity: 1.0;
  }
  .subheader-search {
      background-color: #484848;
      height: 49px;
      -webkit-transition: box-shadow .18s ease-out;
      transition: box-shadow .18s ease-out;
      -webkit-transition-delay: .12s;
      transition-delay: .12s;
      padding: 0;
  }
  .pane-list-body {
      background-color: #323232;
      overflow-y: auto;
  }
  .pane-header {
      background-color: #484848;
      color: orange;
  }
  .dropdown {
      background-color: #585858;
  }
  .dropdown .menu-item a {
      color: orange;
  }
  .dropdown .menu-item a:hover {
      background-color: #505050;
      color: orange;
  }
  .drawer-body {
      background-color: #484848;
  }
  .chat-title {
      color: orange;
  }
  .chat-status {
      color: #CC8400;
  }
  .chat-meta {
      color: #CC8400;
  }
  .chat {
      background-color: #484848;
      border-bottom: 1px solid #323232;
  }
  .chat-status,
  .header-secondary,
  .location-secondary {
      color: #CC8400;
  }
  .chatlist .is-.chat-title, .chatlist .is-unread .location-main, .chatlist .is-unread .header-title, .chatlist .unread .chat-title, .chatlist .unread .location-main, .chatlist .unread .header-title {
      color: #CC8400;
  }
  .chatlist .is-unread .chat-status, .chatlist .is-unread .location-secondary, .chatlist .is-unread .header-secondary, .chatlist .unread .chat-status, .chatlist .unread .location-secondary, .chatlist .unread .header-secondary {
      color: #B37300;
  }
  .chatlist .active.location,
  .chatlist .chat.active {
      background-color: #565656;
      border-bottom: 1px solid #404040;
      color: #CC8400;
  }
  .chatlist .active.location:hover,
  .chatlist .chat.active:hover,
  .infinite-list-item:hover {
      background-color: #565656;
      border-bottom: 0px solid #404040;
      color: #CC8400;
  }
  .chatlist .chat:after,
  .chatlist .list-title:after,
  .chatlist .location:after {
      border-bottom: 0px solid #404040;
      color: #CC8400;
  }
  .chatlist .chat:hover,
  .chatlist .location:hover {
      background-color: #565656;
      border-bottom: 1px solid #404040;
      color: #CC8400;
  }
  .cont-input-search {
      width: calc(100% - 35px)
  }
  .cont-input-search,
  .input-search,
  .block-compose .input-container {
      background-color: #646464;
      border: none
  }
  .message-in {
      background-color: #545454;
  }
  .message-out {
      background-color: #545454;
  }
  .message-in:before,
  .message-out:before {
      background-image: none;
  }
  .message-text {
      color: #E69500;
  }
  .message-text a {
      color: orange;
  }
  .message-meta {
      color: #B37300;
  }
  .message-system {
      background: rgba(0 0 0 0);
  }
  .message-system-body {
      background-color: #484848;
      color: #B37300;
      text-shadow: none;
  }
  .pane-chat-footer {
      padding: 0;
      background-color: #484848;
      box-sizing: border-box;
      border-left: 1px solid rgba(0, 0, 0, .08);
  }
  .input-placeholder {
      color: #CC8400;
  }
  .input {
      color: orange;
  }
  *::-webkit-scrollbar-track {
      background: #484848;
  }
  *::-webkit-scrollbar {
      width: 6px!important;
      background: #323232;
  }
  *::-webkit-scrollbar-thumb {
      background-color: #383838;
  }
  .chat.active .chat-status,
  .active.location .chat-status,
  .chat.active .location-secondary,
  .active.location .location-secondary,
  .chat.active .header-secondary,
  .active.location .header-secondary {
      color: #CC8400;
  }
  .icon-smiley,
  .icon-hide,
  .block-compose .icon-ptt,
  .block-compose .icon-send,
  .icon-menu,
  .icon-chat,
  .icon-clip,
  .menu-horizontal>.menu-item,
  .menu-horizontal>span>.menu-item {
      background-color: #565656;
      border-radius: 55px;
      padding: 6px
  }
}
