css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

body,#globalWrapper,ul#filetoc,div.thumbinner {background:#284051 !important;}

p{color:#fff !important;}

a{color:#fff !important;
text-decoration:underline !important;
}

#searchGoButton,input.searchButton {
color:#000 !important ;
}

td.os-suggest-result, td.os-suggest-result-hl {
background-color:#5D5D5D !important;
color: #ffffff !important;
}


#searchInput {
color:#000 !important;
}


*{color:#fff !important;
}

a:hover{color:#FF9900 !important;}


.ns-0 #content,#content{
background-color:#4D778B !important;
}

 .ns-0 #p-cactions li.selected a,#p-cactions li a{
background-color:#4D778B !important;
background-image:url(http://cdn.netvibes.com/themes/exposition-blueberry/img/tabsel.png) !important;
color:#FFFFFF !important;
opacity:0.99 !important;
}

.anonnotice .notice-collapsed-wrapper {
background:#272727 !important;
color:#eee !important;
}

h1, h2, h3, h4, h5, h6 {
color:#eee !important;
}


#bodyContent h1, #bodyContent h2 {
background:#5D5D5D url(http://cdn.netvibes.com/themes/exposition-blueberry/img/m/moduleheader.png) repeat-x scroll center top !important;
border:1px solid #494949 !important;
}


table{
background-color:#4D778B !important;
}

.portlet h5 {
background:#5D5D5D url(http://cdn.netvibes.com/themes/exposition-blueberry/img/m/moduleheader-blue.png) repeat-x scroll center top !important;
border:1px solid #494949 !important;
}

.pBody {
background-color:#4D778B !important;
color:black !important;
}

#p-cactions .pBody,#p-personal .pBody {
background-color:transparent !important;
color:inherit !important;
}


#toc, .toc, .mw-warning,.infobox,table.navbox tr td,.catlinks {
background:#5D5D5D !important;
}

#footer,#footer li{
background-color:transparent !important;
color: #FEFEFE !important;
}

.navbox-title, table.navbox th.portlet h5 {
background:#5D5D5D url(http://cdn.netvibes.com/themes/exposition-blueberry/img/m/moduleheader-red.png) repeat-x scroll center top !important;
border:1px solid #494949 !important;
}


}
