    text-decoration: none;
    box-shadow: 0px 1px 2px rgb(0, 0, 0), 0px -1px 2px rgb(255, 255, 255) inset;
    border-radius: 1px;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    color: #FFF;
    border: 1px solid rgb(0, 0, 0);
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #555555, #000000) repeat scroll 0% 0%;
}
.tabbar a.button {
    margin: 0px 0px 0px 0px;
}
.setchart {
    border: 1px solid rgb(0, 0, 0);
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #555555, #000000) repeat scroll 0% 0%;
    border-radius: 5px;
    padding: 2px 0px 0px 3px;
    height: 127px;
    width: 626px;
    margin-top: 8px;
    box-shadow: 1px 1px 0px rgba(255, 255, 255, 0.6) inset;
}
.tournament-status, .tournament-toggle {
    position: absolute;
    top: 0px;
    padding: 0px 0.5em;
    font-weight: normal;
    font-size: 80%;
    background-color: rgba(0, 0, 0, 0.9);
    border-right: 1px solid #FFF;
    color: white;
}
.setchart-nickname {
    position: absolute;
    left: -4px;
    top: 5px;
    border: 1px solid #FFF;
    background: #000 none repeat scroll 0% 0%;
    border-radius: 5px;
    padding: 1px 0px 0px;
    width: 110px;
    color: white;
}
.setchart label, .setchart-nickname label {
    display: block;
    font-size: 8pt;
    font-weight: bold;
    color: #FFF;
    padding-left: 2px;
    text-shadow: 1px 1px 1px #000;
}
.teambuilder-results h3, .teambuilder-results .utilichart h3 {
    background: #000 none repeat scroll 0% 0%;
    margin: 6px 0px;
    padding: 2px 5px;
    font-family: Verdana,sans-serif;
    font-size: 9pt;
    text-align: left;
    border-width: 1px 0px;
    border-style: solid none;
    border-color: #000 -moz-use-text-color;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    text-shadow: 1px 1px 0px rgba(0, 0, 0, 0.6);
    box-shadow: 1px 1px 0px rgba(0, 0, 0, 0.6) inset, 0px 2px 2px rgba(0, 0, 0, 0.15);
}
.setmenu button {
    cursor: pointer;
    background: transparent none repeat scroll 0% 0%;
    border: 0px none;
    border-radius: 4px;
    padding: 3px 6px;
    color: white;
}
.teambar button {
    display: block;
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #555555, #000000) repeat scroll 0% 0%;
    color: #FFF;
    border: 1px solid #FFF;
    padding: 0px;
    margin: -1px 2px 0px 0px;
    height: 42px;
    float: left;
    width: 88px;
    cursor: pointer;
    white-space: pre;
    font-size: 8pt;
    font-family: Verdana,Arial,sans-serif;
}
.select:disabled {
    border-color: #FFF;
    background: rgba(255, 255, 255, 0) none repeat scroll 0% 0%;
    color: #FFF !important;
    box-shadow: none;
}
.ps-room.ps-room-opaque {
    color: rgb(255, 255, 255);
}
