css:
/* Широкоформатный Яндекс v2.2 (13.10.13)   */
/* Автор - PoZitron                         */
/* Для широких экранов                      */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.yandex.ru"),
               domain("yandex.ru"),
               domain("yandex.ua"),
               domain("yandex.kz"),
               domain("yandex.by")

{
/* Главная страница яндекса чуть шире */
.b-line { max-width: 1400px !important; }

/* Поисковая выдача на всю ширину */
/* Строка поиска */
.b-head__wrap { max-width: 85% !important; }

/* Основная часть поисковой выдачи */
.b-page-layout { max-width: 100% !important; }
.b-body-items { max-width: 85% !important; }
.b-page-layout__column-proxy{ max-width: 100% !important; }
/* Мои добавления к изначальному шаблону */
#banner { display: none; }
.b-promo { display: none; }
.b-yabrowser-promo { display: none; }
.input__samples { display: none; }
.b-yabrowser-promo__wrap { display: none; }
.b-line_bar { max-height: 50px !important; }
.b-page { max-height: 800px !important; }
#widgets-3-0 { max-height: 0px !important; }
#widgets-4-0 { max-height: 50px !important; text-align: left; }
#widgets-5-0 { max-height: 50px !important; text-align: left; }
.b-region_map { max-height: 50px !important; }
.b-region__icons { display: none; }
div.b-content-item__title:nth-child(1) { display: none; }
div.b-region__links__item:nth-child(2) > a:nth-child(1) { display: none; }
.b-region__links { display: none; }  
.b-region > h2:nth-child(1) { display: none; }
.b-region { max-height: 50px !important; }
.b-weather > h2:nth-child(1) { display: none; }
div.b-line:nth-child(2) { max-height: 50px !important; }
td.b-table__cell:nth-child(2) > div:nth-child(1) { max-height: 50px !important; }
.b-footer_spacer { max-height: 30px !important; }
.b-design > div:nth-child(1), a.b-footer__level { display: none; }
td.b-footer__col:nth-child(2) { text-align: left; }
td.b-footer__col:nth-child(3) { text-align: center; }
.themes-promo__link { display: none; }
.themes-promo__wrapper-link { display: none; }
div.b-footer__link_business:nth-child(3) > a:nth-child(1) { display: none; } 
td.b-footer__col:nth-child(2) {display: none;}
div.b-footer__link:nth-child(3) {display: none;}
div.b-footer__link:nth-child(4) {display: none;}
div.b-footer__link:nth-child(5) {display: none;}
div.b-footer__link:nth-child(6) {display: none;}
  div.b-footer__level:nth-child(7) {display:  -moz-inline-box;}
  .b-footer-links {display: none;}
  .b-sethome__icon {display: none;}
  .b-sethome {display: none;}
  .b-line_bar {max-height: 35px !important;}
  .foot__row {display: none;}
.teaser__content {display: none;}
.teaser__title {display: none;}
.yabrowser-promo__icon-link {display: none;}
.yabrowser-promo__text {display: none;}
div.row:nth-child(5) {display: none;}
div.row:nth-child(6) {display: none;}
div.rows__row:nth-child(2) {max-height: 15px !important;}
.search2__input {max-height: 40px !important;}
.home-arrow {max-height: 45px !important;}
div.rows__row:nth-child(2) {max-height: 5px !important;}
.first {max-height: 260px !important;}
  #y027529192 {display: none;}
  .page-content__right {display: none;}
/* Блок рекламы под поисковой строкой, 2018-03-01, строка внизу */
div.uy.uy_cM {display: none;}
div.o_LP {display: none;}
div.stream-promo-popup.stream-promo-popup_general_yes.stream-promo-popup_fixed_yes.stream-promo-popup_button_no.stream-control.i-bem.stream-promo-popup_js_inited {display: none;}

div.container.container__banner.container__line {display: none;}
div.Pl:nth-child(7) {max-height: 0px !important;}

  /* Конец моих добавлений к изначальному шаблону */
}


@-moz-document domain("news.yandex.ru"),
               domain("news.yandex.ua"),
               domain("news.yandex.kz"),
               domain("news.yandex.by")

{
/* Новости */
.l-head__c { width: auto !important; }
.l-page__left { width: auto !important; }
.l-wrapper { max-width: 100% !important; }
.b-page-content_layout_2columns .b-page-content__column_pos_left { max-width: 100% !important; }
}

@-moz-document domain("market.yandex.ru"),
               domain("market.yandex.ua"),
               domain("market.yandex.kz"),
               domain("market.yandex.by")

{
  .b-max-width { max-width: 100% !important; }
}

@-moz-document domain("pogoda.yandex.ru"),
               domain("pogoda.yandex.ua"),
               domain("pogoda.yandex.kz"),
               domain("pogoda.yandex.by")
{
/* сдвигаем основу в центр */
.b-forecast-detailed
{
  max-width: 1600px !important;
  margin: 0 auto 0 auto !important;
  padding: 0 2% 0 2% !important;
}

.b-hours-line, .b-forecast-scroll, .b-forecast, .b-bandoso
{
  max-width: 100% !important;
}

/* Климат */
.b-forecast-climate {
  margin: 1em 3em 1em 3em !important;
  min-width: 900px !important;
  max-width: 100% !important;
  text-align: center !important;
 }
 
 /* Карта погоды */
.b-maps-monitor { width: 95% !important; }
.b-maps-monitor__viewport { width: auto !important; }
.YMaps { width:100% !important; }
}
