css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kageshi.com") {
#content {
    margin-top: -22px;
}	
	
	.nav_left {
    margin-left: 3px;
}
.nav_li {
    display: inline-block;
    line-height: 3px;
    margin: 3px 3px;
}

	#header {
    z-index: 100;
    width: 88%;
    min-width: 400px;
    height: 33px;
    border-bottom: 10px;
    background: transparent url("http://kageshi.com/images/room_bg/94066264248817923772_bg.jpg") repeat fixed 0% 0%;
}
	
#room {
	width:100%;
}

#room_header {
	margin-top:0;
	min-height:16px;
	max-height:16px;
	color:#F0F0F0;
	font-size:.6em;
	line-height:.6em;
	border-top:0;
	
}
#room_header a {
	color:#eeeeff;
}
#room_header a:visited {
	color:#aaaacc;
}

#swf_chat_normal, #room_header, #room_footer {
	width:66%;
	
}

#swf_chat_normal {
	height:866px;
	border-top:0;
}

#room_header_info {
	margin:0px 0 0px 0px;
	overflow:hidden;
}

#footer p, #footer a {
    padding: 0px 5px;
    color: white;
    line-height: 1.4em;
    vertical-align: middle;
	
}
    

#room_icon_img {
    height: 44px;
    width: 44px;
    margin-right: 0px;
    border-width: 0px;
} 
}
