css:
@-moz-document domain("pshacks.ru") {
.sectionMain {
	background-color: #353535;
    border: 1px solid #353535;
}
.discussionList {
    background:#3b3b3b;
}
.navTabs {background:none;}
.navBarHelper, .subHeading, .heading, .discussionList .sectionHeaders {
	box-shadow: 0 0 0 1px #585858 inset;
    border: 1px solid #242424;
    background: linear-gradient(to bottom,#505050 0%,#3b3b3b 101%);
}
.nodeList .categoryStrip, .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .BRMSContainer.BRMSContainerDark ul.brmsTabNav>li, .BRMSContainer.BRMSContainerDark .brmsConfigList .brmsConfigBtn {
    background: linear-gradient(to bottom,#505050 0%,#3b3b3b 101%)!important;
}
.BRMSContainer.BRMSContainerDark ul.brmsTabNav>li:hover {
    background:#505050;
    margin-top: 1px;
}
#taigachat_full.taigachat_alt.taigachat_reverse #taigachat_box {
    margin-top: 20px;
    background-color: rgb(51,50,50);
}
#taigachat_full.taigachat_alt.taigachat_reverse div /* <- ИСПРАВИТЬ как только будет добавлен стиль соответствующему блоку! */ {
    background:#3b3b3b;
}
.nodeList .pageNodeInfo {
    background:#3b3b3b!important;
    margin-top:1px;
}
.nodeList .categoryText,.nodeList .switch,.nodeList .switch .room-switch-sidebar, .taigachat_messagetext  {
    background:none!important;
}
#taigachat_controls .button {
    margin: -3px 0px 3px;
}
.nodeList .categoryStrip, .widget-container .secondaryContent h3, .sidebar .section .secondaryContent h3 {
	border-bottom: 1px solid rgba(0,0,0,0.6) !important;
    box-shadow: 0 1px 0 rgba(255,255,255,0.2);
}
.nodeList .categoryStrip .nodeDescription {
    position: relative;
    margin-top: -25px;
    bottom: -28px;
}
/*.navTabs .navTab {
	border-right: 1px solid #242424;
}
.navTabs .navTab:nth-child(7), .navTabs .navTab:last-child {
    border-right: none;
}*/
.navTabs .navTab {
    padding-bottom: 2px;
    border-left:none;
    border-right:none;
}
.navTabs .navTab:hover .navLink  {
	border-right: 1px solid #585858!important;
    border-left: 1px solid #585858!important;
    margin-top: 1px;
    margin-bottom: -1px;
    height: 46px;
}
.navTabs .navTab:hover {
    background:#272727!important;
}
.navTabs .navTab:hover:first-child  .navLink, .blockLinksList, .noItems{
    border:none!important;
}
.navTabs .navTab:hover:nth-child(2) .navLink {
    border-left:none!important;
}
html .navTabs .navTab.selected .navLink {
    padding-bottom: 3px;
    margin-top: 0px;
}
.navTabs .navTab.selected .tabLinks {
    top: 48px;
}
.navTabs .SplitCtrl {
    padding-top: 2px;
    margin-bottom: -2px;
}
.navTabs .navTab.selected .blockLinksList li:hover {
    background: rgba(0,0,0,.6);
}
html #QuickSearch {
    top: -35px;
}
.BRMSContainer.BRMSContainerDark .itemContent .counter.counter_1 .countNumber, .BRMSContainer.BRMSContainerDark .itemContent .counter .countNumber {
	border-radius: 0;
    height: 18px;
    padding-top: 3px;    
}
.BRMSContainer.BRMSContainerDark .brmsTabContent {
    border: 1px solid #353535;
    background-color: #3b3b3b;
    box-shadow: inset 0 0 35px 5px #353535;
}
.BRMSContainer.BRMSContainerDark .brmsTabContent ol li {
    margin-bottom: 2px;
    border-top: none;
    /* border-bottom: 1px dashed #CCC; */
}
.navTabs .visitorTabs .navLink .miniMe {
    top: 6px;
    width: inherit;
    height: 30px;
}
.BRMSContainer.BRMSContainerDark ul.brmsTabNav>li.current {
    background: #353535!important;
    box-shadow: inset 0 0 35px 5px #3b3b3b;
}
.BRMSContainer.BRMSContainerDark .itemContent:hover .counter .countNumber {
    background-color: #FFC909;
}
/*.taigachat_alt,  .taigachat_reverse {padding:0px;} */
.BRMSContainer .itemContent .itemDetailName {padding-right: 5px;}
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
	background-color: #353535;
    border-bottom: 1px solid #3b3b3b;    
}
.node.level_1 .nodeList, .formPopup .controlsWrapper {
	background-color: #3b3b3b;
}
.node .nodeLastPost {
    background-color: #3b3b3b;
    border-bottom: 1px solid #353535;
    border: 1px solid #353535;
}
.secondaryContent  {
    background-color: #353535;
}
.formPopup, #QuickSearchQuery, .textCtrl {
    background:#353535;
}
#QuickSearch.active {
    background-color: rgb(44,44,44);
    border: 1px solid rgb(38,38,38);
}
.navigationSideBar a:hover {
    color:#fff;
}
.nodeList .node.level_1 {
    border: 1px solid rgba(0,0,0,0.6)
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    margin: -10px -10px 7px;
}
.brmsIco:hover {
    background:url('http://pshacks.ru/styles/brivium/ModernStatistic/dark-sprite.png')!important;
}
.navigationSideBar .secondaryContent {
    border:none;
}
.navigationSideBar {
    width:155px;
}
.widget {border: 1px solid rgba(0,0,0,0.6);}
}
