css:
@-moz-document domain("last.fm") {
.header .header-background--has-image {
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
    filter: grayscale(100%);
    /* Firefox 4+ */
        filter: gray;
    /* IE 6-9 */
    
    background-position: -20px 0px; 
}
.header .header-background:before {
    background-color: rgba(255, 255, 255, 0.75)!important;
    background-image: url('http://i.imgur.com/fsPFxB0.png');
}
p.header-title-secondary span {
    color: #888!important
}
.header h1 {
    color: #000!important
}
.featured-track-body a.metadata-display {
    color: #000!important
}
a.featured-track-subtitle {
    color: #555!important
}
h4.metadata-title {
    color: #999!important
}
.header .header-info-truncate .header-tags-add {
    color: #888!important
}
.header a.header-crumb {
    color: #555!important
}
.masthead .search-form .search-field {
    border-color: rgba(0, 0, 0, 0.15)!important;
    color: rgba(255, 255, 255, 0.5);
}
.header .header-info-truncate,
.search-field {
    color: #222!important
}
.masthead .masthead-nav-links a,
.masthead .masthead-nav-links .mimic-link {
    color: #222!important;
}
.masthead .masthead-nav-links a:hover {
    color: #b90000!important;
}


.masthead {
    background-color: rgba(240, 240, 240, 0.3);
}

ul.secondary-nav-list {
    color: #222!important
}
input.search-field {
    color: #222!important
}
.header--overview, .header--sub-page {
  color: #222!important
}
}
