css:
@-moz-document domain("insanejournal.com") {

body { background-color: #f7f7f7 !important; }

a:link,.link{color:#c1272d}
a:visited{color:#8b0000}
a:hover,a:active,.link:hover{color:#f4717a}

.dropdown #IJ-smallfont { font-size: 100% !important; }

.dropdown #IJ-sitepage-login a{
font-family:verdana !important;
font-size:12px !important;
line-height: 9pt !important;
}

.dropdown #IJ-sitepage-userpic {
width: 150px !important;
}

.dropdown #IJ-sitepage-tweak {
width: 400px !important;
}

.dropdown #tweak-says {
font-family:verdana !important;
font-size:10pt !important;
}

#IJ-sitepage-freqlinks {
    background: #ededed !important;
	border-top: none !important;
	border-bottom: none !important;
    font-size: 12px !important; }

#IJ-sitepage-freqlinks a { color: #8b0000 !important; }
	
#IJ-sitepage-freqlinks a:hover { 
    color: #f4717a !important;
    background: #ededed !important; }

.dropdown #freqlinks a {
font-family:verdana !important;
font-weight:bold !important;
text-decoration:none !important;
}

.dropdown #top-search {
font-family:verdana !important;
font-size: 8pt !important;
line-height: 9pt !important;
}

#IJ-sitepage { color: #111111;
	background: #f7f7f7 !important; 
	margin: 0 auto !important;
	padding: 0 !important;
    max-width: 60% !important; 
    text-align: justify !important; 
    position:relative !important;
    z-index:1 !important; }

.dropdown #IJ-sitepage-content, #IJ-sitepage-content td{
font-family:verdana !important;
font-size:10pt !important;
line-height: 130% !important;
}

.dropdown #IJ-sitepage-content {
margin: 0 0 0 0px !important;
padding-right: 50px !important;
}

.dropdown #IJ-sitepage-nav {
font-family:verdana !important;
font-size:12px !important;
line-height: 200% !important;
}

.dropdown #IJ-sitepage-nav { 
        color: #fff !important;
		background: #222 !important;
		width: 100% !important; 
		height: 2.5em !important;
		border-bottom: none !important;
        border-bottom: none !important;
		border-top: none !important; } 
	
	.dropdown #nav, .dropdown #nav ul { 
        padding: 0 !important;
		margin: auto !important;
		width: 100% !important;
		list-style: none !important;
		text-align: left !important; }
	
	.dropdown #nav a { 
        display: block !important;
		color: #ffffff !important;
		text-decoration: none !important;
		padding: 0 .50em !important;
		font-size: small !important; }
	
	.dropdown #nav .notlink { padding: 0 .50em !important;
		display: block !important; } /* so the headers have the same padding as the links and are not overrun by the dropdowns */
		
	.dropdown #nav li { float: left !important;
		width: 7em !important;
		height: 2em !important;
		background: #222 !important;
		padding: 0.25em 0 !important; 
		margin: 0 !important; }
	
	.dropdown #nav li ul { position: absolute !important;
		width: 10em !important;
		height: auto !important;
		left: -999em !important; 
		margin: 4 0 0 0 !important;
		border: none !important;
		z-index: 100 !important; }
	
	.dropdown #nav li li { width: 11em !important; }

	.dropdown #nav li:hover ul, .dropdown #nav li.sfhover ul { left: auto !important; }
	
	.dropdown #nav li:hover,
	.dropdown #nav li.sfhover,
	.dropdown #nav li li:hover a,
	.dropdown #nav li li.sfhover a {background: #c1272d !important; 
		color: #ffffff !important; }

.dropdown #IJ-sitepage-content-inner .lesstop{
border-top: 1px solid #9a9a9a !important;
border-bottom: none !important;
font-size:10pt !important;
padding: 15px 0 !important;
}

.dropdown #IJ-sitepage-content hr, .dropdown #IJ-sitepage-footer-inner hr{
display:none !important;
}

.dropdown #IJ-sitepage-footer-inner a{
font-family:verdana !important;
font-size:12px !important;
line-height:9pt !important;
}

.dropdown #IJ-sitepage-footer {
margin-left: 150px !important;
padding-top: 50px !important;
}

.dropdown #EntryForm #infobox table {
background-color: #fff !important;
border: none !important;
padding: 10px !important;
}

.dropdown #EntryForm #infobox table td {
border: none !important;
}

.dropdown #EntryForm #MetaInfo th{
font-size: 13pt !important;
line-height:10pt !important;
}

.dropdown .section_head {
background: #dbd5b7 !important;
}

.appwidget-gettingstarted {
display: none !important;
}

#journal-title {
font-size:20px !important;
font-family:verdana !important;
}

table.IJ-AccentBox1 { border: none !important; }
	
table.IJ-AccentBox2 { background: #FFAFB6;
    border: 1px solid #c1272d !important; }

.IJ-AccentBox2 td { background-color: #FFAFB6 !important; 
border: none !important; }
.talk-comment td+td { background-color: #C0C0C0 !important;
margin: 0 !important;
padding: 0 !important;
border-bottom: 1px solid #c1272d !important;
border-right: 1px solid #c1272d !important; }
}
