@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("usps.com"){
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    .left-nav {
	background-color: #000;
	float: left;
	width: 235px;
}
    .menu--tier-two {
	background-color: #000;
	border-radius: 0px 0px 5px 5px;
	box-shadow: 0px 2px 2px rgba(1, 2, 2, 0.2);
	min-width: 220px;
	position: absolute;
	z-index: 9999;
}
    .menu--tier-two .menu--tier-two-link {
    color: #000;
}
    .menu--tier-one .menu--tier-one-link::after {
    background: #000 none repeat scroll 0 0;
    bottom: 0;
    content: "";
    left: 0;
    opacity: 1;
    position: absolute;
    right: 0;
    top: 0;
    transition: opacity 300ms ease 0s;
    z-index: -1;
}
    .zebra-table {
	background: #000 none repeat scroll 0% 0%;
	border: 5px solid #004DA4;
}

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}
.tracking .cap-box {
	box-sizing: border-box;
	background-image: -moz-linear-gradient(center bottom , #000 0%, #000 100%);
	background-size: 100% auto;
	border: 1px solid #000;
	border-radius: 4px;
}
select, button, #utility-bar, #results, .cap-middle, #main-inner, #navigation-list ul li, .list-wrapper, .dropdown, #navigation li.hover h3 a

{
background: black !important;
}
.cap-box-inverse {
	background: #000 -moz-linear-gradient(center bottom , #000 0%, #000 100%) repeat scroll 0% 0%;
	border-radius: 6px;
}
div, span

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}
  
  .cns-cap-box.cap-box {
    margin-bottom: 30px;
    padding: 30px;
    position: relative;
    top: -1px;
}
.cap-box {
    box-sizing: border-box;
    border: 1px solid #00FFED;
    background: rgba(0, 0, 0, 0) -moz-linear-gradient(center bottom , #000 0%, #000 100%) repeat scroll 0% 0%;
    border-radius: 4px;
    padding: 10px;
}
  
 #for-cTrackingNotificationTo {
    font-size: 24px;
   line-height: 30px;
     color: #00ccff !important;

}
  .input {
    position: relative;
    box-sizing: border-box;
    border-radius: 4px;
    border: 1px solid #00E4FF;
    height: 27px;
    padding: 0px 5px;
    font: 11px/1 Arial,"Helvetica Neue",Helvetica,sans-serif;
    box-shadow: 0px 3px 5px rgba(0, 0, 0, 0.043) inset;
    width: 100%;
    background: rgba(0, 0, 0, 0) linear-gradient(#000, #000 65%) repeat scroll 0% 0%;
}
  
  .scroller {
    padding: 5px 15px 5px 5px;
    border: 2px solid rgb(0, 255, 237);
}
  
  .cns-cap-box.cap-box #services-container table#service-table-header, .cns-cap-box.cap-box .cns-section-wrapper #services-container table#service-table-header {
    margin-top: 20px;
}
#service-table-header {
    padding-bottom: 0px !important;
}
table.cns-table.scrolling-table-header, div.cns-table.scrolling-table-header {
    border-top-right-radius: 6px;
    border-top-left-radius: 6px;
    border-left: 2px solid #00FFE4;
    border-right: 2px solid #00FFC9;
    border-top: 2px solid #00FFF6;
}
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  

