css:
@-moz-document domain("forums.pokemmo.eu") { 
/* COLORS  
green #3b7371  
dark purple #5E405A
light purple #ad75a8 
light pink #c9b7c3
light grey #7B8490
           #444851 
dark grey #222428
  
*/  
  /*====================
    MAIN HEADER SHIT 
  ======================*/
  #logo > A > IMG{
  display: none !important;
  /* Replace the mixtape.moe link with a link to your own image to replace the log image (Don't forget to delete the display: none section too)*/  
  /*
    width:0;
    height:63px;
    padding-right:358px;
    background:transparent url("https://my.mixtape.moe/szigum.png") bottom no-repeat !important;
  */
  }

  /* remove logo and those pesky maintitlehovers */  
  html, body {
    /*background-color: #1d1f21;*/
  }
  #announcements td {
    border-bottom: 1px solid #1d1f21;
  }
 .maintitle:hover .toggle {
  display: none !important;
 }
  
  #branding {
    background: #1d1f21;
    border-bottom: 0px solid transparent; 
    min-height: 0px;
    max-height: 40px;
  }
  /* for things with tabs*/ 
  #primary_nav, #header_bar { 
   background: #1d1f21 !important;
   padding: 40px 0px 0px; 
  }
  /*====================================
    = SEARCH BAR 
    ====================================*/ 
    #search .submit_input {
    background: #5E405A url("https://forums.pokemmo.eu/public/style_images/master/search_icon.png") no-repeat scroll 50% center;
    text-indent: -3000em;
    padding: 0px;
    border: 1px solid #222428;
    display: block;
    width: 26px;
    height: 26px;
    position: absolute;
    right: 0px;
    top: 0px;
    bottom: 0px;
    border-radius: 0px 3px 3px 0px;
    box-shadow: none;
  }
  #search_options {
    font-size: 10px;
    height: 20px;
    line-height: 20px;
    margin: 3px 3px 3px 0px;
    padding: 0px 6px;
    border-radius: 3px;
    background: #282a2e none repeat scroll 0% 0%;
    display: inline-block;
    float: right;
    max-width: 80px;
    text-overflow: ellipsis;
    overflow: hidden;
  }
  #search_wrap {
    position:  relative;
    background: #282a2e none repeat scroll 0% 0%;
    display: block;
    padding: 0px 26px 0px 4px;
    height: 26px;
    line-height: 25px;
    border-radius: 3px 4px 4px 3px;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);
    min-width: 230px;
  }
  
  /* buttons at the top */ 

  #community_app_menu > li > a {
    color: #4E4E4E;
    background: #242529 !important;
    display: block;
    padding: 6px 15px 8px;
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5);
  }
  #community_app_menu > li > a:hover { 
   color: #5E405A !important;
  }
  #community_app_menu > li.active > a {
    background: #282a2e none repeat scroll 0% 0%;
    color: #5E405A;
    font-weight: bold;
    margin-top: 0px;
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5);
  }

  #user_navigation a {
    color: #7b8490;
  }
  /* For the search bar */ 
  #search {
    margin: 6px 0px;
  }
  
  /* THE SIDEBARS */ 
  #ipbwrapper { 
   background: #1d1f21 !important;
  }
  
  .ipbmenu_content, .ipb_autocomplete {
    border: 1px solid #5E405A;
  }
  /*=======================================
    = INNER PARTS OF THE GUI 
    =======================================
  /* inside of sidebars */ 
  #content, .unread { 
   background: #282a2e !important;
  }
  
  /* just inside of content */ 
  .ipsBox.table_wrap { 
   background: #1d1f21 !important;
  }
  
  .ipsBox_container, table.ipb_table td { 
   background: #282a2e !important;
   border: 1px solid #222428;  
  }
  
  /* MAIN GRADIENT BARS */ 
  .maintitle, .popupInner h3 {
    background: #282a2e repeat-x scroll center top;
    color: #ad75a8 !important;
    /*padding: 10px 10px 11px;*/
    font-size: 16px;
    font-weight: 300;
    border-radius: 4px 4px 0px 0px;
    /*box-shadow: 0px 1px 0px #5E405A inset;*/ 
    border-width: 1px 1px 0px;
    border-color: #1d1f21;
    border-style: solid;
    box-shadow: #171717 0 0 5px !important;
    /*background: transparent -moz-linear-gradient(center top , #383B40 0%, #282a2e 100%) repeat scroll 0% 0%;*/ 
  }
  /* Text of Main Title */ 
    .maintitle a{
      background: #1d1f21 repeat-x scroll center top;
      color: #ad75a8;
      padding: 0px 10px 9px;
      font-size: 16px;
      font-weight: 300;
      border-radius: 4px 4px 0px 0px;
      box-shadow: 0px 1px 0px #1d1f21 inset;
      border-width: 1px 1px 0px;
      border-color: #1d1f21;
      border-style: solid;
      text-shadow: 0px 1px 1px #000000;
    }   

  /*================================
    = VARIOUS FONTS 
    ================================*/ 
   A {
    color: #ad75a8;
    text-decoration: none;
   }
  a:hover {
    color: #5E405A;
  }
  
  #category_3 { 
   color: #444851 !important;
  }
  
  #subforums_18 > LI > A { 
   background: transparent !important;
  }
  
  .desc.lighter, .desc.lighter.blend_links a { 
   color: #5E405A !important;
  }
  
  /*==================================
    = RECENT UPDATES APPLET 
    ==================================*/ 
  .ipsSideBlock {
   background: #1d1f21 !important;
    
  }
  
  .ipsSideBlock h3 { 
   background: #282a2e !important;
   box-shadow: #171717 0 0 5px !important;
    color: #7B8490 !important;
  }
  
  
  
  /*=====================================
    = SHIT AT THE BOTTOM 
    =====================================*/
  #board_stats .value {
    display: inline-block;
    background: #282A2E none repeat scroll 0% 0%;
    color: #7B8490;
    padding: 2px 6px;
    font-weight: bold;
    border-radius: 2px;
    margin-right: 3px;
    box-shadow: #171717 0 0 5px !important;
  }
  
  #copyright a, #copyright {
    color: #5E405A;
    text-shadow: none;
  }
  
  .statistics {
    margin: 20px 0px 0px;
    padding: 10px 0px;
    border-top: 3px solid #1d1f21;
    line-height: 1.3;
    overflow: hidden;
}
  
  /*==================================================================================
    = MISCELANIOUS STUFF LIKE PLAYER CARDS
    ==================================================================================*/ 
   /* hover over a users name */ 
  .userpopup .side, .general_box.pad {
    position: absolute;
    margin-top: -40px;
    background: transparent !important;
  }
  .ipsPad { 
   background: #282a2e !important;
  }
  #pu_____hover___member_inner > DIV > H3 {
    background: #1d1f21;
    border-width: 1px 1px 0px;
    border-style: solid;
    border-color: #282a2e;
    font-size: 16px;
    font-weight: 300;
    text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.3);
    box-shadow: 0px 1px 0px #282a2e inset;
  }
  .popupInner h3 a {
    color: #ad75a8;
  }
  .userpopup dl {
    border-bottom: 1px solid #5E405A;
    padding-bottom: 10px;
    margin-bottom: 4px;
  }
  
  /* USER PROFILE PICTURE */ 
  .ipsUserPhoto {
    padding: 1px;
    border: 1px solid #5E405A;
    background: #ad75a8 none repeat scroll 0% 0%;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.1);
  }
  
  /* BUTTONS ON THE PLAYER CARD */ 
  .ipsButton_secondary {
    height: 22px;
    line-height: 22px;
    font-size: 12px;
    padding: 0px 10px;
    background: transparent -moz-linear-gradient(center top , #383B40 0%, #33363B 100%) repeat scroll 0% 0%;
    border: 1px solid #1d1f21;
    box-shadow: 0px 1px 0px #444851 inset, 0px 1px 0px rgba(0, 0, 0, 0.3);
    border-radius: 3px;
    color: #7B8490;
    display: inline-block;
    white-space: nowrap;
    transition: all 0.2s ease-in-out 0s;
  } 
  .user_controls li a { 
    background: transparent -moz-linear-gradient(center top , #383B40 0%, #33363B 100%) repeat scroll 0% 0%;
    border: 1px solid #1d1f21;
    box-shadow: 0px 1px 0px #444851 inset, 0px 1px 0px rgba(0, 0, 0, 0.3);
    color: #7B8490;
    transition: all 0.2s ease-in-out 0s;
  }
  /* background of the playe card */ 
  #user_link_menucontent { 
   background: #282a2e !important;
  }
  
  #pu_____hover___member_inner > DIV > DIV:last-child > DIV > DL { 
   color: #7B8490 !important;
  }
  
  /* for the message bar on player cards */ 
  .message {
    background: #444851 none repeat scroll 0% 0%;
    padding: 10px;
    border: 2px solid #1d1f21;
    color: #7B8490;
    line-height: 1.6;
    font-size: 12px;
  }
  
  /* for message bar on your own card */ 
  .input_text { 
    background: #444851 none repeat scroll 0% 0%;
    border: 1px solid #1d1f21;
    color: #7B8490;
  }
  
  /* text on your own player card */ 
  #user_profile { 
   color: #ff0000 !important;
  }
  #user_link_menucontent #links li  {
    text-shadow: none !important;
    white-space: nowrap;
    color: #ff0000 !important;
  }
  
  /*===============================
    = MENUS AT THE TOP OF THE SCREEN 
    ===============================*/
  #user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {
    background-position: center bottom;
    background-color: #1d1f21;
    border-radius: 3px 3px 0px 0px;
  }
  .ipsHeaderMenu {
    background: transparent -moz-linear-gradient(center top , #1d1f21 0%, #222428 70%, #1d1f21 100%) repeat scroll 0% 0%;
    padding: 10px;
    border-radius: 0px 0px 6px 6px;
    overflow: hidden;
    width: 340px;
  }
  
  /*================================================================================================================================================================================
    = MESSENGER APP 
    ================================================================================================================================================================================*/
  .ipsBox {
    background: #1d1f21 none repeat scroll 0% 0%;
    box-shadow: #1d1f21 0 0 3px !important;
  }
  
  .post_wrap { 
   background: #282a2e !important;
    border: 1px solid #222428 !important;
  }

  /* Thing that indicates who it is */ 
  .post_block h3 {
    background: #282a2e none repeat scroll 0% 0%;
    padding: 0px 10px;
    height: 36px;
    line-height: 36px;
    font-weight: normal;
    font-size: 16px;
    border: 1px solid #222428 !important;
  }
  
  /* color of text */ 
  .post_body .post {
    color: #7B8490;
  }
  
  /* little line that shouldn't be there */ 
  .post_block {
    background: #1d1f21 none repeat scroll 0% 0%;
    border-bottom: none;
  }
  
  /* little buttons */ 
  #folder_list .total {
    background-color: #282a2e;
    color: #7B8490 !important;
  }
  
  /* various clickables */ 
  ul.post_controls a{
    color: #7B8490;
  }
   ul.post_controls a:hover { 
    color: #ad75a8 !important;
   }
  
  /*================================
    = BUTTONS AT THE BOTTOM 
    ================================*/ 
    .input_submit.alt {
      background: transparent -moz-linear-gradient(center top , #383B40 0%, #33363B 100%) repeat scroll 0% 0%;
      border: 1px solid #1d1f21;
      color: #7B8490;
      box-shadow: 0px 1px 0px #444851 inset, 0px 1px 0px rgba(0, 0, 0, 0.3);
  }
  
  /*===================================
    = various gradient buttons 
    ===================================*/
  .input_submit, .topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton {
    background: transparent -moz-linear-gradient(center top , #383B40 0%, #33363B 100%) repeat scroll 0% 0%;
    border-style: solid;
    border-color: #212121;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    box-shadow: 0px 1px 0px #444851 inset, 0px 1px 0px rgba(0, 0, 0, 0.3);
    color: #7B8490;
    text-shadow: 0px -1px 0px #191919;
    text-align: center;
  }
  
  
  
  
  /*=============
    = SIGNATURES 
    =============*/ 
  .signature {
    clear: right;
    color: #A4A4A4;
    font-size: 0.9em;
    border-top: 1px solid #5E405A;
    padding: 10px 0px;
    margin: 6px 0px 4px;
    position: relative;

  }
  
  /*==================================================================================================================================================================
    = VARIOUS MISC THINGS FOR CLEANING UP 
    ======================================*/ 
  .ipsType_pagetitle, .ipsType_subtitle {
    color: #ad75a8;
  }
  .pagination .back a, .pagination .forward a { 
   color: #ad75a8;
   background: #7B8490 none repeat scroll 0% 0%;
   background: transparent -moz-linear-gradient(center top , #383B40 0%, #33363B 100%) repeat scroll 0% 0%;
  }
  .pagination .pages a:hover {
   background: transparent -moz-linear-gradient(center top , #383B40 0%, #33363B 100%) repeat scroll 0% 0%;
   color: #7B8490;
  }
  .pagination .pages a, .pagejump {
    color: #ad75a8;
  }
  .pagination .pages li.active {
    background: #444851 none repeat scroll 0% 0%;
    background: transparent -moz-linear-gradient(center top , #383B40 0%, #33363B 100%) repeat scroll 0% 0%;
    color: #7B8490;
  }
  p.citation {
    border-left: 2px solid #989898;
    background: transparent -moz-linear-gradient(center top , #383B40 0%, #33363B 100%) repeat scroll 0% 0%;
    border-top: 2px solid #1d1f21;
    border-right: 2px solid #1d1f21;
    border-left: 2px solid #1d1f21; 
    box-shadow: 5px 5px 5px #1d1f21;
  }
  blockquote.ipsBlockquote {
    border-color: #1d1f21 #1d1f21 #1d1f21 #1d1f21;
    background: #282a2e none repeat scroll 0% 0%;
    color: #7B8490 !important;
    box-shadow: 5px 5px 5px #1d1f21;
  }
  .ipsLikeBar_info {
    background: #242529 none repeat scroll 0% 0%;
  }
  .ipsType_sectiontitle { 
  border-color: #282a2e !important;
  }
  
  /*============================================================================================================================================================================
    = THE FORUMS SECTIONS EG. GENERAL DISCUSSION 
    =======================================++++*/
.ipsFilterbar li.active a, .ipsFilterbar.bar.altbar li.active a {

    background: #282a2e none repeat scroll 0% 0%;
    color: #ad75a8;
}
  
.ipsBadge, .ipsBadge_green { 
  color: #7B8490 !important;
  background-color: #444851 !important;
}
  
  ul.mini_pagination li a {
    background: #444851 none repeat scroll 0% 0%;
    border: 1px solid #1d1f21;
    padding: 1px 3px;
}
  
  p { 
   color: #7B8490 !important;
  }
  /*=================================================================================
    = PLAYER PROFILES 
    ======================++*/
  .general_box {
    background: #1d1f21 none repeat scroll 0% 0%;
    /*box-shadow: #171717 0 0 5px !important;*/
  }
  .general_box.clearfix { 
   color: #7B8490 !important;
  }
  .general_box h3 {
    background: #282a2e none repeat scroll 0% 0%;
    color: #7B8490;
    box-shadow: #171717 0 0 5px !important;
  }
  
  /* 
      height: 22px;
    line-height: 22px;
    font-size: 12px;
    padding: 0px 10px;
    background: transparent -moz-linear-gradient(center top , #383B40 0%, #33363B 100%) repeat scroll 0% 0%;
    border: 1px solid #1d1f21;
    box-shadow: 0px 1px 0px #444851 inset, 0px 1px 0px rgba(0, 0, 0, 0.3);
    border-radius: 3px;
    color: #7B8490;
    display: inline-block;
    white-space: nowrap;
    transition: all 0.2s ease-in-out 0s;
  */ 
/*  
green #3b7371  
dark purple #5E405A
light purple #ad75a8 
light pink #c9b7c3
light grey #7B8490
           #444851
tab color dark grey #242529 
