css:
@-moz-document domain("livejournal.com") {
.lj-like {display: none;}

.p-lenta {background: #000;}
.l-flatslide-container {max-width: 1000px !important; margin: 0 auto !important;}

.l-flatslide-aside {display: none !important;}
.l-flatslide-content {margin-right: 50px;}

.entryunit__body {font-size: 125%; line-height: 1.5em;}

.b-lenta .b-sticky-cut {
    left: 0;
    right: 0;
    width: 100%;
    margin: 0;
    z-index: 10;
}

article.entryunit--feedpromo {
	display: none !important;
}

}
