css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  /**************
  * General CSS *
  **************/
  
  body {
    background: url("http://i.imgur.com/XLxMTFt.jpg") bottom left;
  }
  
  .ps-room.ps-room-light {
    background: rgba(0, 0, 0, 0);
    border-color: #11484F;
  }
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(51, 98, 81, 0.6);
  }
  
  .ps-room.ps-room-light.scrollable h3 {
    color: #BBCCC9;
    text-shadow: 0px 0px 2px #11484F;
  }
  
  .ps-room.ps-room-light.scrollable p {
    color: #031012;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .ps-room.ps-room-light.scrollable .button {
    background: #BBCCC9;    
    border: 1px solid #11484F;
    color: #11484F;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .ps-room.ps-room-light.scrollable .button:hover {
    background: #CBE1DD;    
    border-color: #0C2D32;
  }
  
  .chat.mine {
    background: rgba(0, 0, 0, 0);
  }

  .broadcast-blue {
    background: #CD9FC4;
    border: 1px solid #0B1361;
    color: #0B1361;
    text-shadow: 0px 0px 2px #B287A9;
    text-align: center;
    font-family: sans-serif;
    padding: 8px;
    margin: 5px;
  }

  .broadcast-blue a {
    color: #000F98;
  }

  .broadcast-blue button {
    background: #A87C9F;
    border: 1px solid #090F47;
    color: #FFF9FF;
    text-shadow: 0px 0px 2px #090F47;
    padding: 2px 8px;
  }

  .broadcast-blue button:hover {
    background: #9F7697;
  }

  .broadcast-red {
    background: #FFEBF4;
    border: 1px solid #B52C43;
    color: #B52C43;
    text-shadow: 0px 0px 2px #F2DBE5;
    text-align: center;
    font-family: sans-serif;
    padding: 8px;
    margin: 5px;
  }

  .broadcast-red a {
    color: #B3001E;
  }

  .broadcast-green {
    background: #B5D896;
    border: 1px solid #20493B;
    color: #20493B;
    text-shadow: 0px 0px 2px #A4C786;
    text-align: center;
    font-family: sans-serif;
    padding: 8px;
    margin: 5px;
  }

  .broadcast-green a {
    color: #005C3E;
  }
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.35);
  }
  
  /*******************
  * General CSS ~end *
  *******************/
  
  /*************
  * Header CSS *
  *************/
  
  .header {
    background: rgba(17, 72, 79, 0.6) url("http://i.imgur.com/A3wunN6.png?1") no-repeat;
  }
  
  .header .logo {
    opacity: 0;
  }
  
  .maintabbarbottom {
    border-color: #803C6F;
    background: #CD9FC4;
  }
  
  .tabbar a.button.notifying {
    background: #FFE0A7;
    border-color: #803C6F;
    color: #803C6F;
    text-shadow: none;
    font-style: italic;
  }

  .tabbar a.button.notifying:hover {
    background: #F9D990;  
    border-color: #803C6F;
    color: #803C6F;
  }
  .tabbar a.button.subtle-notifying {
    color: #07948F;
  }
 
  .tabbar a.button.subtle-notifying:hover {
    color: #07948F;
  } 
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    border-color: #803C6F;
    background: #CD9FC4;
    color: #803C6F;
    text-shadow: 0px 0px 2px #FCE8F1;
  }
  
  .tabbar a.button {
    border-color: #803C6F;
    background: #F3C5EA;
    color: #803C6F;
    text-shadow: none;
  }
  
  .tabbar a.button:hover {
    background: #E5B6DC;
  }
  
  .tabbar .closebutton {
    color: #803C6F;
  }
  
  .tabbar .closebutton:hover {
    color: #AF3B3B;
  }
  
  .userbar button.icon {
    border: 1px solid #803C6F;
    background: #CD9FC4;
    color: #803C6F;
    text-shadow: 0px 0px 2px #FCE8F1;
  }
  
  .userbar button.icon:hover {
    background: #DBAED2;
  }
  
  .userbar .username {
    text-shadow: 1px 1px 0px #11484F, 1px -1px 0px #11484F, -1px 1px 0px #11484F, -1px -1px 0px #11484F;
  }
  
  /******************
  * Header CSS ~end *
  ******************/
  
  /*****************
  * Roomlist CSS *
  ***************/
  
  .roomlist h2 {
    color: #FEE2B3;
    text-shadow: 0px -1px 0px #11484F;
  }
  
  .roomlist a.ilink, .roomcounters button {
    background: #BBCCC9;    
    border-color: #11484F;
    color: #11484F;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .roomlist a.ilink:hover, .roomcounters button:hover {
    background: #BBCCC9;    
    border-color: #0E3339;
    color: #0E3339;
    text-shadow: none;
  }
  
  /********************
  * Roomlist CSS ~end *
  ********************/
  
  /***********
  * Chat CSS *
  ***********/
  
  .userlist {
    background: rgba(17, 72, 79, 0.6);
    border-color: #11484F;
    color: #11484F;
  }
  
  .userlist li {
    background: rgba(255, 255, 255, 0.65);
    border-color: #11484F;
  }
  
  .userlist li em.group {
    color: #11484F;
  }
  
  .userlist li button:hover, .userlist-minimized .userlist-count:hover {
    background: rgba(255, 255, 255, 0.5);
  }
  
  .infobox {
    border-color: #11484F;
  }
  
  .chat-log {
    background: rgba(255, 247, 251, 0.8) url("http://i.imgur.com/XQqaBOM.png?1") top right no-repeat;
  }
  
  .chat-log .chat small {
    color: #11484F;
  }
  
  .chat-log .chat.mine.chat small {
    color: #0A292E;
  }
  
  .chat.highlighted {
    background: rgba(178, 215, 148, 0.7);
  }
  
  .chat-log-add {
    border-color: #336251;
    background: rgba(51, 98, 81, 0.6);
  }
  
  .chat-log-add .textbox {
    border-color: #336251;
  }
  
  .chatbox label {
    text-shadow: 0px 0px 2px #336251;
  }
  
  .nightclub {
    background: rgba(0, 0, 0, 0.85);
    margin:-3px;
    text-shadow: 0px 0px 2px #000;
    color: #FFF;
  }
  
  /****************
  * Chat CSS ~end *
  ****************/
  
  /*******************
  * Tournaments CSS *
  ******************/
  
  .tournament-title {
    background: rgba(17, 72, 79, 0.6);
    text-shadow: 0px -1px 0px #11484F;
    color: #AFCFD4;
  }
  
  .tournament-status, .tournament-toggle {
    background: rgba(255, 255, 255, 0.65);
    border-color: #11484F;
    text-shadow: none;
    color: #11484F;
  }
  
  .tournament-tools {
    border-color: #11484F;
    background: rgba(51, 98, 81, 0.6);
  }
  
  .tournament-tools .select {
    border-color: #11484F;
    background: #336251;
    color: #D9FCEF;
    text-shadow: 0px -1px 0px #11484F;
  }
  
  .tournament-tools .select:hover {
    background: #3D735F;
  }
  
  .tournament-tools .select:disabled, .tournament-tools .select:disabled:hover {
    background: rgba(51, 98, 81, 0.45);
    color: #11484F !important;
    text-shadow: none !important;
    border-color: #11484F !important;
  }
  
  .tournament-tools button {
    border-color: #11484F;
    background: #336251;
    color: #D9FCEF;
    text-shadow: 0px -1px 0px #11484F;
  }
  
  .tournament-tools button:hover {
    background: #3D735F;
  }
  
  .tournament-wrapper, .tournament-box {
    border-color: #11484F;
  }
  
  .tournament-box {
    background: url("http://i.imgur.com/xQmFRxz.jpg?2");
  }
  
  /***********************
  * Tournaments CSS ~end *
  ***********************/
  
  /**********************
  * Mainmenuwrapper CSS *
  **********************/

  .menugroup {
    border: 1px solid #803C6F;
    background: rgba(128, 60, 111, 0.25);
    color: #FBE7F0;
    text-shadow: 0px 0px 2px #803C6F;
  }
  
  .menugroup .select, .menugroup .button {
    background: #FFE0A7 !important;
		border-color: #803C6F !important;
		color: #803C6F !important;
		text-shadow: none !important;
		font-style: italic !important;
  }
  
  .menugroup .select:hover, .menugroup .button:hover {
    background: #F2CF99 !important;
		border-color: #803C6F !important;
		color: #803C6F !important;
		text-shadow: none !important;
		font-style: italic !important;
  }
  
  .menugroup .select:disabled, .menugroup .select:disabled:hover {
    background: rgba(255, 224, 167, 0.7) !important;
		border-color: #803C6F !important;
		color: #803C6F !important;
		text-shadow: none !important;
		font-style: italic !important;
  }
  
  .pm-window h3 {
    border-color: #336251;
    background: rgba(51, 98, 81, 0.6) url("http://i.imgur.com/39TYOAo.png") bottom right no-repeat;
    background-position: 200px;
    color: #E5FFF6;
    text-shadow: 0px -1px 0px #336251;
  }
  
  .pm-window h3:hover {
    background: rgba(51, 98, 81, 0.6) url("http://i.imgur.com/39TYOAo.png") bottom right no-repeat;
    background-position: 200px;
    color: #CBEBE0;
  }
  
  .pm-window h3 small {
    color: #B2DBCD;
  }
  
  .pm-window.focused h3, .pm-window.focused h3:hover {
    border-color: #336251;
    background: rgba(51, 98, 81, 0.8) url("http://i.imgur.com/29OjC01.png") bottom right no-repeat;
    background-position: 200px;
    color: #E5FFF6;
    text-shadow: 0px -1px 0px #336251;
  }
  
  .pm-window h3:hover .minimizebutton, .pm-window h3 .closebutton, .pm-window h3 .minimizebutton {
    color: #D3F5E8;
  }
  
  .pm-window h3 .closebutton:hover {
    color: #F7A4A4;
  }
  
  .pm-window h3 .minimizebutton:hover {
    color: #F7D0A4;
  }
  
  .pm-window h3 .closebutton:hover + .minimizebutton {
    color: #D3F5E8 !important;
  }
  
  .message-pm {
    color: #46806A;
  }
  
  .pm-window h3.pm-notifying {
    border-color: #803C6F;
    background: #F3C5EA;
    color: #803C6F;
    text-shadow: none;
  }
  
  .pm-window h3.pm-notifying:hover {
    background: #E5B6DC;
  }
  
  .pm-window h3.pm-notifying small {
    color: #A4578F;
  }
  
  .pm-window h3.pm-notifying .closebutton, .pm-window h3.pm-notifying .minimizebutton {
    color: #803C6F;
  }
  
  .pm-window h3.pm-notifying .closebutton:hover {
    color: #AF3B3B;
  }
  
  .pm-window h3.pm-notifying .minimizebutton:hover {
    color: #AF813B;
  }
  
  .pm-window h3.pm-notifying .closebutton:hover + .minimizebutton {
    color: #803C6F !important;
  }
  
  .pm-log, .pm-window.focused .pm-log {
    border-color: #336251;
  }
  
  .challenge {
    border-color: #803C6F;
    background: #F3C5EA;
    color: #803C6F;
    text-shadow: none;
  }
  
  .challenge .select {
    border-color: #803C6F;
    background: #CD9FC4;
    color: #803C6F;
    text-shadow: 0px 0px 2px #FCE8F1;
  }
  
  .challenge .select:hover {
    background: #DBAED2;
  }
  
  .challenge .select:disabled {
    background: rgba(205, 159, 196, 0.5);
  }
  
  .challenge .select.preselected, .challenge .select.preselected:disabled {
    color: #803C6F;
  }
  
  .challenge .buttonbar button {
    border: 1px solid #803C6F;
    background: #CD9FC4;
    color: #803C6F;
    text-shadow: 0px 0px 2px #FCE8F1;
    padding: 2px 5px;
  }
  
  .challenge .buttonbar button:hover {
    background: #DBAED2;
  }
  
  .pm-log-add {
    border-color: #336251;
    background: rgba(51, 98, 81, 0.6);
  }
  
  .pm-window.focused .pm-log-add {
    border-color: #336251;
    background: rgba(51, 98, 81, 0.75);
  }
  
  .pm-log-add .textbox {
    border-color: #336251;
  }
  
  .mainmenufooter a {
    color: #FFE1B3;
  }
  
  .mainmenufooter a:hover {
    color: #F2D19C;
  }
  
  .mainmenufooter {
    color: #F2D19C;
  }
  
  /***************************
  * Mainmenuwrapper CSS ~end *
  ***************************/
  
  /************
  * Popup CSS *
  ************/
  
  .ps-popup {
    background: #BBCCC9;    
    border-color: #11484F;
  }
  
  .ps-popup a, .credits h2, .credits ul {
    color: #11484F;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .ps-popup a:hover {
    color: #173B40;
  }
  
  .ps-popup a.ilink.yours {
    color: #0B282C;
  }
  
  .ps-popup a.ilink.yours:hover {
    color: #04171A;
  }
  
  .ps-popup .buttonbar button {
    background: #17626E;
    border: 1px solid #11484F;
    padding: 2px 5px;
    color: #BBCCC9;
    text-shadow: 0px -1px 0px #11484F;
  }
  
  .ps-popup .buttonbar button:hover {
    background: #1B545C;
  }
  
  .popupmenu h3 {
    color: #11484F;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .popupmenu button.sel {
    border-color: #11484F;
  }
  
  .popupmenu button:hover, .popupmenu button.sel:hover {
    border-color: #11484F;
    background: rgba(255, 255, 255, 0.3);
  }
  
  a.subtle, a.subtle:hover {
    color: #000;
  }
  
  .avatarlist button {
    border-color: #11484F;
  }
  
  .avatarlist button:hover {
    border-color: #11484F;
    background-color: rgba(0, 0, 0, 0.3);
  }
  
  .effect-volume em, .music-volume em {
    color: #062529;
  }
  
  .ps-popup hr {
    border: none;
    border-bottom: 1px solid #062529;
  }
  
  /*****************
  * Popup CSS ~end *
  *****************/
  
  /*************
  * Ladder CSS *
  *************/
  
  .ladder th {
    background: #CD9FC4;
    color: #803C6F;
    text-shadow: 0px 0px 2px #FCE8F1;
  }
  
  .ladder td {
    background: rgba(255, 246, 250, 0.75);
    color: #803C6F;
    text-shadow: 0px 0px 2px #FCE8F1;
  }
  
  .ladder td:hover {
    background: rgba(255, 246, 250, 0.95);
  }
  
  .ladder table, .ladder td, .ladder th {
    border-color: #803C6F;
  }
  
  /******************
  * Ladder CSS ~end *
  ******************/
  
  /******************
  * Teambuilder CSS *
  ******************/
  
  .teamchartbox {
    border-color: #11484F;
  }
  
  .teamlist .team {
    background: #BBCCC9;    
    border-color: #11484F;
    color: #11484F;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .teamlist .team:hover {
    background: #BBCCC9;    
    border-color: #0E3339;
    color: #0E3339;
    text-shadow: none;
  }
  
  .setchart {
    border-color: #175964;
    background: #447F69;
  }
  
  .setchart-nickname {
    background: #336251;
    border-color: #11484F;
  }
 
  .setchart input, .setdetails, .setstats, .statform input.inputform {
    border-color: #175964;
  }
  
  .setchart input:hover, .setdetails:hover, .setstats:hover, .statform input.inputform:hover {
    border-color: #11424A;
  }
  
  .setchart .setdetails label, .setchart .setstats label {
    color: #11424A;
    text-shadow: none;
  }
  
  .setchart label {
    text-shadow: 0px -1px 0px #175964;
    color: #E6FFF7;
  }
  
  .setchart-nickname label {
    text-shadow: 0px -1px 0px #11484F;
    color: #E6FFF7;
  }
  
  .setchart-nickname input {
    border-color: #11484F;
  }
  
  .setchart-nickname input:hover {
    border-color: #0A2B30;
  }
  
  .setchart input.incomplete {
    background: #FFF5FA;
    color: #CE3FB6;
    border-color: #CE3FB6;
  }
  
  .teambuilder-results {
    background: rgba(255, 255, 255, 0.3);
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    border-color: #803C6F !important;
    background: #F3C5EA !important;
    color: #803C6F !important;
    text-shadow: none !important;
  }
  
  .teambuilder-results .firstresult a {
    background: #BBCCC9;    
    border-color: #11484F;
  }
  
  .teambuilder-results .firstresult a:hover, .teambuilder-results .result a:hover {
    background: #CBE1DD;    
    border-color: #0C2D32;
  }
  
  .utilichart b {
    color: #11484F;
    text-shadow: 0px 0px 2px #96BFB8;
    font-style: italic;
  }
  
  .utilichart .movenamecol, .utilichart .namecol, .utilichart .pokemonnamecol {
    color: #062529;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .utilichart .itemdesccol, .utilichart .abilitydesccol, .utilichart .movedesccol, .utilichart .statcol, .utilichart .bstcol, .utilichart .labelcol, .utilichart .widelabelcol, .utilichart .pplabelcol, .utilichart .abilitycol, .utilichart .twoabilitycol {
    color: #11484F;
  }
  
  .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em {
    color: #1B606B;
  }
  
  .utilichart .numcol {
    color: #5B274D;
    text-shadow: 0px 0px 2px #FCE8F1;
  }
  
  .utilichart .typecol img {
    opacity: 0.75;
  }
  
  .teambar button {
    background: #BBCCC9;    
    border-color: #11484F;
    color: #11484F;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .teambar button:hover, .teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    background: #BBCCC9;    
    border-color: #0E3339;
    color: #0E3339;
    text-shadow: none;
  }
  
  .setmenu button {
    color: #5B274D;
    text-shadow: 0px 0px 2px #FCE8F1;
  }
  
  .setmenu button:hover {
    border-color: #803C6F !important;
    background: #F3C5EA !important;
    color: #803C6F !important;
    text-shadow: none !important;
  }
  
  .statform p.suggested button {
    background: #BBCCC9;    
    border: 1px solid #11484F;
    color: #11484F;
    text-shadow: 0px 0px 2px #96BFB8;
    padding: 2px 5px;
  }
  
  .statform p.suggested button:hover {
    background: #CBE1DD;    
    border-color: #0C2D32;
  }
  
  .statform p.suggested a {
    color: #11484F;
  }
  
  .statform .col div label, .statform .col div em, .detailsform label {
    color: #062529;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .statform .col div b {
    color: #031315;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  .jslider .jslider-label {
    opacity: 1;
    color: #062529;
  }
  
  .jslider .jslider-value {
    background: #CBE1DD;    
    border: 1px solid #0C2D32;
    color: #11484F;
    text-shadow: 0px 0px 2px #96BFB8;
  }
  
  /***********************
  * Teambuilder CSS ~end *
  ***********************/
  
  /*************
  * Battle CSS *
  *************/
  
  .ps-room.ps-room-opaque {
    background: rgba(51, 98, 81, 0.6);
  }
  
  .ps-room .battle-controls {
    background: rgba(255, 255, 255, 0.25);
    padding-top: 10px; 
    padding-bottom: 10px;
  }
  
  .battle-log {
    background: rgba(255, 255, 255, 0.9);
    border-color: #803C6F;
  }
  
  .battle-log h2 {
    border-color: #803C6F;
    background: #F3C5EA;
    color: #803C6F;
    text-shadow: none;
    text-align: center;
  }
  
  .battle-log h2:hover {
    color: #642F56;
  }
  
  .battle-log .rated strong {
    border-color: #803C6F;
    background: #F3C5EA;
    color: #803C6F;
    text-shadow: none;
    border: 1px solid #803C6F;
    padding: 3px 6px;
  }
  
  .battle-log .chat strong {
    color: #803C6F;
  }
  
  .battle-log-add {
    border-color: #803C6F;
    background: #F3C5EA;
  }
  
  .battle-log-add .chatbox label {
    text-shadow: 0px 0px #E4B7DB;
  }
  
  .battle-log-add .textbox {
    border-color: #803C6F;
  }
  
  .turnstatus-protect {
    background:url(http://i.imgur.com/RVxCoHz.png);
    opacity: 1 !important;
    height: 103px !important;
