css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("lioden.com") {
body 
{
background:url('http://fc07.deviantart.net/fs24/f/2009/244/a/9/The_Dragon_wallpaper_by_TheOutcast1821.jpg');
background-attachment:fixed;
background-size:100%;
background-position:center;
color:#f38b13;
}

div#lion_image>div
{
background:url('http://static.lioden.com/images/dynamic/backgrounds/RootsOfTheWarriorsBG.png');
}

div.box {
background:#4c361c;
width:700px;
border-radius:15px;
}

h1 {color:#f38b13;text-align:center;}

h2 {color:#f38b13;}

h3 {color:#100800!important;}

table.table {border:1px solid black!important;}

.table>td, .pagination>span {background:#4c361c;color:#100800;border:1px solid #100800;}

td.right, .sub_menu, .right_odd.xsmall, .right_locked, td.right_odd, span.left, .customiser-sortable li:nth-child(2n+1) {background:#4c361c!important;color:#100800;border:1px solid black;}

td.left {background:#4c361c!important;color:#100800;border:1px solid black;}

td.left.b {background:#4c361c!important;color:#100800;border:1px solid black;}

td.right_cub, .sub_menu_item_selected {background:#a6763e!important;color:#100800;}


  .item {border:1px solid #100800;}
  
td.top, .item-header, .item-footer {
background:#885112!important;color:#100800!important;text-align:center;
}

th {
background:#885112!important;color:#100800!important;text-align:center;border:1px solid black
}

td.bottom {
background:#885112!important;color:#100800!important;text-align:center;
}

div.left {background:#4c361c!important;color:#100800;}

a:link {color:#f38b13!important;}    
a:visited {color:#f38b13!important;}  
a:hover {color:#100800!important;}     
a:active {color:#100800!important;}  
 

.sidebar {background:#4c361c!important;}

.panel {background:#21170c!important;color:#f38b13;box-shadow:none!important;border:none!important;}

.xsmall {background:none!important;}

.chat_light {color:black;background:#a6763e;}

.chat_dark {color:#a6763e;background:#21170c;}

.topbar {background:#885112;}

.navbar,.sub_sub_menu {background:#4c361c;}


input[type="submit"] {
background:#100800!important;
color:#f38b13!important;
text-shadow:none!important;
box-shadow:none!important;
border:none!important;
border-radius:10px!important;}

input[type="submit"]:hover {
background:#f38b13!important;
color:#100800!important;
text-shadow:none!important;
box-shadow:none!important;
border:none!important;
border-radius:10px!important;}

input[type="button"] {
background:#100800!important;
color:#f38b13!important;
text-shadow:none!important;
box-shadow:none!important;
border:none!important;
border-radius:10px!important;}

input[type="button"]:hover {
background:#f38b13!important;
color:#100800!important;
text-shadow:none!important;
box-shadow:none!important;
border:none!important;
border-radius:10px!important;}


.navbar-default .navbar-nav > li > a {color:#f38b13!important;}
.navbar-default .navbar-nav > li > a:hover {background:#100800;}

.breadcrumb {background:#885112!important;}
.breadcrumb > .active {color:white;}

.progress-bar-warning {background:#885112!important;}
.progress-bar-danger {background:#885112!important;}
.progress-bar-success {background:#885112!important;}
.progress-bar {background:#885112!important;}
.progress {background-color:#a6763e!important;border:1px solid #f38b13;}
.progress div {color:#f38b13!important;}


input[type="text"]{color:#f38b13;background:#21170c;border:2px solid black;border-radius:10px;}
input[type="password"] {color:#f38b13;background:#21170c;border:2px solid black;border-radius:10px;}
textarea {color:#f38b13;background:#21170c;border:2px solid black;}
select {color:#f38b13;background:#21170c;border:2px solid black;}

div.container.main 
{
background:none;
box-shadow:none;
border:none;
}

div.col-md-9.col-xs-12 {background:none;border:none;}

}
