@namespace url(http://www.w3.org/1999/xhtml);
/* 
MyIntracomm URLs that do not contain the words 
  "Forms", which are ShrePoint libraries, alreadymedia with icons
  Cend/Pages
Test regular expression at 
https://regex101.com/r/cW0gW9/1
https://regex101.com/r/cW0gW9/2
*/
@-moz-document regexp('^https:\/\/myintracomm.ec.europa.eu(?!.*(Forms|forms|Cend\/Pages)).*$')
{

/*  ICONS for special links   */
/*  
  - Credit: https://userstyles.org/styles/504/link-icons-identifiers-of-links 
  - With CSS4 a[href$="pdf" i] i, for insensitive, would be enough but does not work yet
*/   
/* 
  PDF 
*/  
  a[href$="pdf"]::after,a[href$="PDF"]::after
    {
      padding:0 3px; content:url("data:image/gif;base64,R0lGODlhEAAQALMAABQSFAT+BJGPkdfS2FxeXIwKDMC5v/Hx8uVSUvwwNvxqbDIzMuaPkOsJB3x2fPisriH5BAEAAAEALAAAAAAQABAAAwRwMEhHa5V4mr2FGAumKEiZnEkjHEcYKGcjz4XDtm8600IlLI2CcDg8cAwAjGNgGAwQg4OzmZxIo4gb81AN2KKDBEPLVd4UB8aDFe3apIjHo4QYu1nxKMuAvtdvbFRKI1pOTAN3gFeHXQQLj5ALAJOTEQA7");
    }
/* 
  WORD
*/  
  a[href$=".rtf"]::after,a[href$=".RTF"]::after
  ,
  a[href$=".doc"]::after,a[href$=".docx"]::after,a[href$=".docm"]::after
  ,
  a[href$=".DOC"]::after,a[href$=".DOCX"]::after,a[href$=".DOCM"]::after
    {
      padding:0 3px;
      content:url("data:image/gif;base64,");
    }

}
