css:
/* Version 1.06 */
@namespace url(http://www.w3.org/1999/xhtml);
/* 
MyIntracomm URLs that do not contain the words 
  /dg/agri
  /dg/ecfin
  ...
Test regular expression at 
https://regex101.com/r/zN6gZ5/1
*/
@-moz-document regexp('^https:\/\/myintracomm.ec.europa.eu(?!.*(\/dg\/(agri|AGRI|grow|GROW))).*$')
{  
  body h1, body h2, body h3, body h4, body h5
  {
    font-family: Times New Roman !important;

/*  1.01: don't touch font-size
    font-size: x-large !important;
*/
  }
  
  body
  ,
  body #s4-bodyContainer
  ,
  .wrapper
  ,
  .wp-content
  {
    background-color: #fff1e0 !important;  /* FT background */
  }
  .wp-colored-background
  ,
  .wp-white-background
  ,
  .disclaimer
  ,
  body #s4-workspace #s4-bodyContainer .central-wrapper .staff-matters_footer-wrapper
/* 1.06 */
  ,
  .light-background-color
  {
    background-color: #f6e9d8 !important;
  }
  .banner  /* TOP banner, i.e. 2nd row */
  {
    background-color: peru  !important;
    /* chocolate LightSalmon Peru */
  }
  body .colophon .container      /* BOTTOM mega menu */
  ,
  .event .event-day              /* Calendar events */  
  {
    background-color: Sienna  !important;
  }
  .cd-primary-nav .cd-secondary-nav
  ,
  .cd-primary-nav.nav-is-visible
  {
    background-color: Silver  !important;
  }
  .cd-primary-nav.nav-is-visible .has-children > a    /*  Menu links after hamburger pressed, i.e. vertical menu */
  {
    color: SaddleBrown !important;
  }
  body .ec-pagination
  {
    background-color: BurlyWood  !important;
  }
  
  body a
  {
    color: SaddleBrown !important;
  }
  body a:visited
   {
    color: Peru  !important;  
   }
  .banner h1 a, .banner h1 a:visited, .banner h1 a:hover
    {
      color:white !important;
    }
 
  .banner img
  , img  
  {
   -webkit-filter: sepia(100%) !important; /* Chrome, Safari, Opera */
   filter: sepia(100%) !important;
  }
  
/*  1.02: green like buttons
*/  
  
  .btn-like
  ,
  .btn-like-reply
  {
    background-color: green !important;
  }
/*  1.04: green Yes - red No buttons
*/  
  .btn-primary.yes
  {
    background-color: green !important;
  }
  .btn-primary.no
  {
    background-color: red !important;
  }
  .btn-default.btn-post-feedback
  {
    background-color: blue !important;
    background-image: none !important;
    color: white !important;
  }
/*  1.05: fixed white vertical bar between columns
*/  
  body .row.two_columns.vertical_shadow .right-col
  {
    background-image: none !important;
  }
}
