css:
/* Version 1.01 */
@namespace url(http://www.w3.org/1999/xhtml);
/* 
MyIntracomm URLs that do not contain the words 
  /dg/agri
  /dg/ecfin
  ...
Test regular expression at 
https://regex101.com/r/zN6gZ5/1
*/
@-moz-document regexp('^https:\/\/myintracomm.ec.europa.eu(?!.*(\/dg\/(agri|ecfin|ECFIN|grow))).*$')
{  
  
  
  
  .cd-main-header-inner-wrapper
  ,
  .cd-main-content
  ,
  .cd-search .cd-search-container
  ,
  body .colophon
  {
    max-width: none !important;
  }
  
  body .content-page-layout
  {
    padding: 1rem 1rem 0 !important;
  }
  
  body .right-col .wp-content
  {
    padding: 0 !important;
  }
  
  .event .event-date
  {
    margin-right: 0.5rem !important;
  }
  
  body .discussion
  {
    padding-bottom: 0.5rem !important;
  }
  body .discussion h4
  {
    margin-left: 4rem !important;
  }
  
  body .ec-more
  {
    margin-right: 0.5rem !important;
  }
/* 1.01 No indentation of paragraphs */
  .wp-content > .col-xs-12
  {
    padding: 0 !important;
  }

  /* Forum discussions */  
  .col-md-pull-5
  {
    right: unset !important;
  }
  .col-md-7 
  {
    width: 100% !important;
  }
  .left-sidebar-wrapper.affix
  {
    left: 1rem !important;
  }
}
