@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("messenger.com") {
/* =================== SOD =================== */
._1ht1._1ht2 {
    -moz-box-shadow:inset 0px 1px 0px 0px #7a8eb9;
    -webkit-box-shadow:inset 0px 1px 0px 0px #7a8eb9;
    box-shadow:inset 0px 1px 0px 0px #7a8eb9; /* fixed for browser specific prefix*/
    background-color:#7990c3 !important;
}
._1ht1._1ht2 ._1ht6,
._1ht1._1ht2 ._1ht7,
._1ht1._1ht2 ._1htf{
    color: #f5f5f5 !important;
}

/* ==== Sidebar Contacts ==== */
/* ++++ Contacts List ++++ */
._1ht1 {
    background-color: #f7f7f7  !important;
}
._1ht6,
._1ht7,
._1htf{
    font-weight: 300 !important;
}
/* ++++ Unread color ++++ */
._1ht3._1ht1 {
    background-color: rgba(145, 179, 255, 0.12) !important;
    -webkit-transition: background-color .5s;
    -moz-transition: background-color .5s;
    transition: background-color .5s; /* fixed for browser specific prefix*/
}
/* ++++ Contact Search ++++ */	
._5iwm {
    border-bottom: 1px solid #bababa !important;
    background-color: #f0f0f0 !important;
}
/* ========== Top Bar =========== */
._5742, ._36ic{
    background-color: #E3E3E6 !important;
    border-bottom: #bababa 1px solid !important;
    box-shadow: inset 0px 0px 40px 0px rgba(0,0,0,0.03);
}
/* ========== Right Sidebar ========== */
._4_j5 {
    border-left: 1px solid rgba(0,0,0, 0.25) !important;
}
._4u-c {
    background: #f0f0f0 !important;
}/*
._5l37 {
    background-color: #000000 !important;
} /*
/* ========== Conversation Pane ========== */
._1wfr {
    background: #ffffff !important;
}
/* ## Text Input ## */
._4rv3 {
    background-color: #f2f2f2 !important;
}	
/* ++++++ Chat Bubbles ++++++ */
/* ## Outgoing Messages ## */
._nd_ ._hh7 {
    border-radius: 1px !important;
    border: 1px solid rgba(124, 124, 124, 0.65);
    background: rgba(214, 218, 236, 0.86);
    color: #444 !important;
}
/* ## Incoming Messages ## */
._29_7 ._52mr {
    background: rgba(218, 218, 218, 0.82);
    border-radius: 1px !important;
    border: 1px solid rgba(143, 143, 143, 0.46) !important;
}
/* ## Active State ## */
._hh7:active, ._-5k ._hh7 {
/*  background: rgba(194, 208, 255, 0.86) !important; */
    background: rgba(0,0,0,0.07) !important;
}
/* ## Emoji Only Messages ## */
._hh7._2f5r, ._-5k ._hh7._2f5r {
    background-color: transparent !important;
    border: 1px solid transparent !important;
}
/* ## Links ## */
._nd_ ._hh7 a, ._29_7 ._52mr a {
    color: #4f4f4f;
}
/* =================== EOD =================== */
}
