css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://road.cc/node/) {



/* The settings here assume you're 
using your browser full-screen  
and have a screen at least 1400 x 900 pixels*/


/* Make the window for the body copy bigger. 
This sets it to be at last 600 pixels tall  */
.cke_contents { 
    min-height: 600px !important; 
}

/* Hide the summary box completely, as we don't use it */
.text-summary-wrapper {
    display: none;
}  

/* Hide the line that says 'Location' */
.location-wrapper {
    display: none;
}

/* Size the lists for channel, news type and 
product type, making them bigger
 and set the text size larger*/
.form-select {
    font-family: Verdana, sans-serif !important;
	font-size:12px !important;
	height:145px;
}

/* Restore the drop-down menu for 
text format to its original height*/
#edit-body-und-0-format--2 { 
    height:16px; 
}

/* Restore the drop-down menu for 
story weight to its original height*/
#edit-field-story-weight-und { 
    height:16px; 
}

/* Hide the gert block of text explaining html types */
.filter-guidelines {
    display: none;
}  

/* Set the width of the page. 
This prevents the pop-out sidebar
for atoms from overlapping the body
box and obscuring the style control buttons*/
#page {
    width: 845px!important;
}

/* Set text input boxes such as the 
title etc to use large, friendly letters */
input,textarea {
font-family: Verdana, sans-serif !important;
font-size:14px !important;
}    

/* Make the sections titles bold 
so they're easier to pick out*/
label {font-weight:bold!important;}

/*Hide 'Receive comment notifications by email' option*/
.form-item-node-notify-subscribe {display:none;}

/*Make the 'Tags Term' box as wide as possible*/

#edit-field-tags-term-und {width:100%!important;}
}
