css:
@-moz-document domain('forums.digitalspy.co.uk'), url-prefix('http://forums.digitalspy.co.uk') {
 * {font-family: "-apple-system", "Helvetica Neue", "Arial", sans-serif !important}

a {font-size: 13px !important}
a:hover {text-decoration: none !important;}

body {background: #FCFCFC !important; padding: 0 !important}

.boldlink, .bigusername {font-weight: 600 !important;}

nav a {  font-family: 'Gyre' !important}

.main, .container {background: #FCFCFC !important}

.container {padding-left: 32px !important; padding-right: 30px !important; width: auto !important; max-width: 1135px !important;}

.page {width: 100% !important}

.thead, #forumtools, #forumsearch, .vbmenu_control, #threadtools, #threadsearch {background: #234C87 !important; color: #fff !important}

#forumsearch {padding-right: 15px !important; padding-left: 15px !important}

.login_box {background: #eee !important}

th.login_box {padding-top: 30px !important; font-size: 20px;}

.login_box .smallfont {float: right; margin-top: -48px !important; text-align: right !important}

.posthead {font-size: 13px !important;}

.posthead .boldlink {font-size: 14px !important}

.tborder .posthead {font-size: 12px !important}

ul.forum_nav, .nav_buttons {background: #234C87 !important}
ul.forum_nav a {color: #fff !important; text-transform: uppercase; font-size: 11px !important; font-weight: bold; padding: 10px 20px !important}
.forum_head ul.forum_nav li {border: none !important}

.forum_head td, .forum_head table {width: 100% !important; height: auto !important}

.forum_head .smallfont {font-size: 13px !important}
h1 {padding-top: 20px !important;}

.tborder .alt2 {background: #FCFCFC !important; border-right: 1px solid #E6E6E6; padding-top: 15px !important; padding-bottom: 15px !important}

table.pagnavthread td {padding-top: 10px; padding-bottom: 10px;}

.pagnavthread {padding: 0 !important}

.userpost hr {margin-bottom: 15px !important}

.theadp, .posthead {padding-left: 10px !important}

.userpost {padding-right: 5px !important;-webkit-hyphens: auto !important; text-align: justify !important}
.alt2 {padding-left: 10px !important}

.alt2 {font-size: 12px !important;}
.bigusername {font-size: 14px !important;}

.alt2 .smallfont {padding-top: 5px !important}

.alt1 a:link, .tborder a {color: #00568C !important}
.alt1 a:hover {border: none !important; text-decoration: none !important; color: #000 !important}

.tcat, .posthead {background: #132A4B !important; color: #fff !important}
.tcat a, .posthead a, #threadtools a, #threadsearch a, .posthead a, #forumtools a, #forumsearch a, .vbmenu_control a, .theadp a {color: #fff !important}


.pagenav .tborder {width: auto !important}

.forum_head h1 {padding-bottom: 0px !important;}
.body_copy {display: none !important}

td.panelsurround {padding:0 0 15px !important;}
.boximportant {margin-bottom: 20px !important}

#breadcrumb_bar {font-weight: bold;}

.alt1 {font-size: 13px !important}

.alt1 a {font-size: 14px !important}

.userpost div {font-size: 13px !important}

.nowrap a {font-size: 10px !important}

.bluetext {color: #333 !important;}

.tcat {padding: 5px 7px 8px 14px !important;}
.tcat img {display: none !important}

.quotebox {margin-top: 5px !important;}
.userpost .boldlink {text-transform: uppercase !important}
.quotebox .boldlink {text-transform: none !important}

pre, pre * {font-family: "Menlo", "Courier", monospace !important; white-space: pre-wrap !important}

td.posthead.bllink a {color: #fff !important}

.grid-guide-vert-inner, .grid-guide, .grid-guide-horiz, .grid-guide-vert {display: none !important;}

fieldset, .fieldset {border: none !important; padding: 0 !important}
.panel {background: none !important; padding: 10px 0 !important}

.fieldset td {padding-bottom: 5px !important}

.alt1 table td {border-bottom: none !important}

.icon {font-family: "IconFont" !important}

iframe {display: none !important}

.pspoiler {border: none !important; margin: 0 !important; background: #ccc}

.pre-spoiler {text-transform: uppercase !important; font-weight: bold !important; color: #888}

.pre-spoiler span {margin-top: 2px !important}

.pre-spoiler input, input.button {font-size: 13px !important; border: none !important; color: #fff !important; padding: 5px !important; background: #234C87 !important;}

.quotebox {border: none !important; background: #eee !important}

hr {border: none !important; height: 1px !important;}

table {border-radius: 20px !important; border: none !important}

.vbmenu_popup {background: none !important;}

table { border-spacing: 0px !important }

#wrapper div.main .container {padding-bottom: 0 !important}


body #wrapper .main .container div.page div .contentcontain hr {display: none !important}

body #wrapper .main .container div.page div .contentcontain .userpost hr {display: block !important}

.vBulletin_editor {padding: 0 !important}

.vBulletin_editor, .vBulletin_editor * {background: none !important}

.vBulletin_editor textarea {border-color: #ddd}

.imagebutton:hover {background: #eee !important; border: 1px solid #eee !important}

.panel div {padding: 0 !important}
.panel .boximportant {padding: 15px !important}

legend {margin: 0 0 10px !important; padding: 0 !important; font-weight: bold !important; font-size: 13px !important}

p select {margin-top: 20px !important}

td img {max-height: 25px !important}

.nav--primary--link.active {color: #91BFFF !important; box-shadow: 0 -5px 0 0 #91BFFF inset !important}

}
