css:
@-moz-document domain("sonicscanf.org") {
#userbar {
	background: #650000;
	height: 36px;
	line-height: 36px;
	color: #fff;
	font-size: 13px;
}
#menu {
	background: #000000;
	position: relative;
	text-align: center;
	clear: both;
	-webkit-box-shadow: 0 0 5px rgba(0, 0, 0, 0.25);
	-moz-box-shadow: 0 0 5px rgba(0, 0, 0, 0.25);
	box-shadow: 0 0 5px rgba(0, 0, 0, 0.25);
	z-index: 5100;
}

#menu .container>ul {
    margin-right: -250px;
}

.ipsType_sectionTitle {
    background: #650000;
}

body {
    font-family: 'Open Sans',Verdana,Tahoma,Arial,sans-serif!important;
    background: #1F1818;
    background-size: cover;
    padding: 0;
    margin: 0;
    color: #CA2222!important;
}

#footer {
    background: #330909;
    bottom: 0;
    color: #F00;
    height: 64px;
    box-shadow: 0 -2px 5px rgba(0,0,0,0.26);
    position: absolute;
    width: 100%;
    z-index: 2;
}

h1 {
    font-size: 1.7em;
    padding: 7px 15px 3px 15px;
    color: #330000;
    line-height: normal;
    margin: 0;
    font-weight: normal;
}

.ipsBreadcrumb > ul a {
    color: #BF2222;
}

#ipsLayout_contentArea {
    display: table;
    width: 100%;
    border: 1px solid rgba(255, 0, 0, 0.05);
    border-top: 0;
    background: #000000;
}

.block h1 {
    font-size: 1.7em;
    padding: 7px 15px 3px 15px;
    color: #990000;
    line-height: normal;
    margin: 0;
    font-weight: normal;
}

.ipsApp .ipsSubNavigation {
    text-shadow: 0px 1px 0px rgba(255,255,255,0.5);
    padding: 15px 15px;
    background: #000000;
}

#ipsLayout_mainArea {
    padding: 0;
    background-color: #000;
}

.ipsDataItem_main {
    width: 100%;
    margin: 0;
    vertical-align: top;
    background-color: #000;
}

.ipsDataItem_icon {
    padding-right: 0;
    background-color: #000;
}

.ipsDataItem_stats {
    width: 15%;
    min-width: 150px;
    text-align: right;
    list-style: none;
    background-color: #000;
}

.ipsDataItem_lastPoster {
    min-width: 160px;
    background-color: #000;
}

.cForumList .ipsDataItem {
    border-bottom-width: 1px;
    background-color: #000;
}

.ipsApp .ipsButton_important {
    background: #521A1A;
    color: #ffffff;
}

a {
    color: rgb(177, 36, 36);
    text-decoration: none;
}

v.expandable {
    background-color: #000;
    padding-left: 15px;
}

#sidebar .block>.head {
    margin-bottom: 5px;
    background: #000;
}

.block.statuses>.content {
    padding-left: 0;
    padding-right: 0;
    color: #B70000;
}

.block>.content {
    padding: 0 15px 15px 15px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    zoom: 1;
    background-color: #000;
}

.block {
    margin-bottom: 25px;
    background: #000;
    -webkit-box-shadow: 0 0 5px rgba(0,0,0,0.25);
    -moz-box-shadow: 0 0 5px rgba(0,0,0,0.25);
    box-shadow: 0 0 5px rgba(0,0,0,0.25);
    position: relative;
    overflow: hidden;
}

a, .block h1 a {
    color: #B90707;
}

.actions.raised>li a {
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    background: #B91515;
    color: #000;
}

.actions.info {
    color: #A90E0E;
    font-size: 14px;
}

v.expandable.active {
    background-color: #000000;
}

h1 a {
    color: #B90707;
}

.cForumList .ipsDataItem {
    border-bottom-width: 1px;
    color: #B11414;
}

v.expandable.active {
    background-color: #000000;
}

.ipsDataItem {
    border-style: solid;
    border-color: #000000;
}

.ipsAreaBackground_reset {
    background: #000000;
}

.child {
    background: #000000;
}

.ipsBox {
    border-width: 1px;
    border-style: solid;
    border-color: #BB0000;
    border-radius: 3px;
}

.block.statuses>.content>div.expandable.active {
    background-color: #480A0A;
}

.dropdown {
    background: #fff;
    min-height: 30px;
    position: absolute;
    color: #AFB126;
    left: 0;
    line-height: normal;
    font-weight: normal;
    font-size: 13px;
    display: none;
    z-index: 20;
    -webkit-box-shadow: 0 0 5px rgba(0,0,0,0.5);
    -moz-box-shadow: 0 0 5px rgba(0,0,0,0.5);
    box-shadow: 0 0 5px rgba(0,0,0,0.5);
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    cursor: auto;
    background-color: #000;
}

.block h1 {
    font-size: 1.7em;
    padding: 7px 15px 3px 15px;
    color: #980000;
    line-height: normal;
    margin: 0;
    font-weight: normal;
}

.cForumList .ipsDataItem {
    background-color: #000;
}

.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):nth-child(even) {
    background: #000000;
}

.block>.head, .block h1 {
    font-size: 1.7em;
    padding: 7px 15px 3px 15px;
    color: #990000;
    line-height: normal;
    margin: 0;
    font-weight: normal;
}

.ipsAreaBackground_light {
    background: #000000;
}

.ipsBox:not {
background-color: #000

}

.ipsType_light {
    color: #8E0303;
}

.cSearchActivity_info {
    border-left: 4px solid #460303;
    padding: 0 0 0 15px;
    margin: 10px 10px 0 10px;
}

.ipsTabs {
    background: #000000;
    padding: 10px 10px 0 10px;
    margin: 0;
    list-style: none;
}

body {
    font-family: 'Open Sans',Verdana,Tahoma,Arial,sans-serif!important;
    background: #1F1818;
    background-size: cover;
    padding: 0;
    margin: 0;
    color: #C12525!important;
}

.ipsPad, .ipsApp ul.ipsPad, .ipsApp ol.ipsPad {
    padding: 15px;
    background-color: #000;
}

.block.statuses>.content>div.expandable .dropdown {
    margin-top: 10px;
    padding-top: 10px;
    border-top: 1px solid #000;
    padding-left: 15px;
}

.block>.head a, .block h1 a {
    color: #990000;
}

.ipsCommentCount {
    padding: 5px 10px;
    border-radius: 3px;
    background: #D40707;
    display: inline-block;
    font-size: 12px;
    text-transform: uppercase;
    position: relative;
    text-align: center;
    line-height: 15px;
    color: #333;
    margin-bottom: 3px;
}

.ipsCommentCount:after {
    right: 100%;
    border-color: transparent #000000 transparent transparent;
}

body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 13px;
    line-height: 18px;
    color: #525252;
    height: 100%;
    background-color: #000000;
}

.block.updates>.content>div.expandable:hover, .block.statuses>.content>div.expandable:hover, .block.updates>.content>div.expandable.active, .block.statuses>.content>div.expandable.active {
    background-color: #000000;
}

html[dir="ltr"] .ipsCommentCount:after {
    right: 100%;
    border-color: transparent #000000 transparent transparent;
}

.ipsTabs_item {
    display: block;
    float: left;
    white-space: nowrap;
    line-height: 37px;
    font-size: 14px;
    color: #777;
    padding: 0 15px;
    text-overflow: ellipsis;
    max-width: 100%;
    overflow: hidden;
}

.ipsBox:not( .ipsBox_transparent ) {
    background-color: #000;
}

.ipsType_sectionHead {
    font-size: 18px;
    color: #E20C0C;
    line-height: 24px;
    font-weight: 400;
    display: inline-block;
    margin: 0;
}

.cSearchActivity_info {
    border-left: 4px solid #840606;
    padding: 0 0 0 15px;
    margin: 10px 10px 0 10px;
}

.ipsTabs_activeItem {
    background: #1F1515;
    color: #333;
    font-weight: bold;
}

.ipsApp .ipsButton_light {
    background: #008E53;
    border-color: rgba(0,0,0,0.03);
    color: #333333;
}

.statuses textarea, .statuses input[type="text"], .statuses input[type="password"] {
    font-family: inherit;
    border-top: 0!important;
    border-left: 0!important;
    border-right: 0!important;
    border-bottom: 1px solid #FF0000;
    resize: none;
    padding: 5px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    -webkit-border-radius: 0!important;
    -moz-border-radius: 0!important;
    border-radius: 0!important;
    -moz-background-clip: padding!important;
    -webkit-background-clip: padding-box!important;
    background-clip: padding-box!important;
    vertical-align: top;
    font-size: 14px;
    margin-bottom: 1px;
    color: #000;
}

.ipsApp .ipsField_autocomplete, .ipsApp textarea, .ipsApp input[type="text"], .ipsApp input[type="password"], .ipsApp input[type="datetime"], .ipsApp input[type="datetime-local"], .ipsApp input[type="date"], .ipsApp input[type="month"], .ipsApp input[type="time"], .ipsApp input[type="week"], .ipsApp input[type="number"], .ipsApp input[type="email"], .ipsApp input[type="url"], .ipsApp input[type="search"], .ipsApp input[type="tel"] {
    background-color: #2B2B2B;
    border-width: 1px;
    border-style: solid;
    border-color: #a6a6a6 #d9d9d9 #d9d9d9 #a6a6a6;
    border-radius: 3px;
    width: 100%;
    max-width: 350px;
    padding: 7px;
}

.ipsMenu_item > a {
    display: block;
    padding: 8px 15px;
    color: #D40000;
    white-space: nowrap;
    background-color: #000;
}
}
