css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("phoenixframework.org") {
  body {
    background-color: #333 !important;
    color: #aaa;
  }
  .subheader h4 {
    color: #fff;
  }
  .docs-content {
    color: #aaa;
  }
  .theme-solid .main-content .docs-body a {
    color: #fff !important;
  }
  pre {
    color: #999;
    background-color: #000;
    border: none;
  }
  code {
    background-color: #000;
    color: #aaa;
  }
  .theme-line .sidebar-nav ul li a, .theme-solid .sidebar-nav ul li a {
    color: #777;
  }
  .border-left {
    box-shadow: 1px 0px 0px 0px #444 inset;
  }
  .border-right {
    box-shadow: 1px 0px 0px 0px #444;
  }
  .docs-content hr {
    border-color: #444;
  }
  blockquote {
    border-left-color: #777;
  }
  .theme-line .docs-header h1, .theme-solid .docs-header h1 {
    color: #ccc;
  }
}
