@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hkgolden.com") {
    body {
        background: #444444 !important;
        color: #cccccc !important;
    }
    .TopMenuPanel,
    .PageMiddleBox {
        background: transparent !important;
    }
    input:not([type="checkbox"]),
    select {
        -moz-appearance: none !important;
        border-color: #111111 !important;
        border-style: solid !important;
        border-width: .02em !important;
        background: #222222 !important;
        color: #cccccc !important;
    }
    option[style$="background-color: #F1F2F3;"] {
        -moz-appearance: none !important;
        border-color: #111111 !important;
        border-style: solid !important;
        border-width: .02em !important;
        background: #333333 !important;
    }
    /* global link */
    
    a {
        color: #cccccc !important;
    }
    a:visited {
        color: #888888 !important;
    }
    a:hover {
        color: #6495ed !important;
    }
    /* logo */
    
    img#ctl00_TopBarHomeImage,
    img[src$="images/logo.jpg"] {
        width: 0 !Important;
        padding-right: 220px !important;
        height: 115px !important;
        background: url("data:image/png;base64,") !important;
        background-repeat: no-repeat !important;
    }
    p,
    td {
        color: #cccccc !important;
    }
    .FooterPanel .txt_11pt_1A3448 {
        color: lightblue !important;
    }
    .noscript-show {
        display: none !important;
    }
}

/* login */
@-moz-document regexp("(http)://(forum[^\.]*?|search|archive)\.hkgolden\.com/login\.aspx.*") {
    td{
        background-color: #333333 !important;
    }
}

@-moz-document regexp("(http)://(forum[^\.]*?|search|archive)\.hkgolden\.com/.*?\.(aspx|htm).*") {
    /* General */
    form > table,
    .PageMiddleFunctions,
    #ctl00_ContentPlaceHolder1_view_form > table,
    #ctl00_ContentPlaceHolder1_view_form > div td[style="background-color: #F3F2F1; height: 100%; border: solid 1px #111111;"]:first-child,
    #PageMiddlePanel + br,
    #PageMiddlePanel + br + div,
    #PageMiddlePanel + br + div + div,
    #PageMiddlePanel + br + div + div + div,
    #ctl00_ContentPlaceHolder1_PMForumRules{
        display: none !important;
    }
}
    
@-moz-document regexp("(http)://(forum[^\.]*?|search|archive)\.hkgolden\.com/(topics|[Ss]earch|tags).*?\.(aspx|htm).*") {
    
    /* topics */
    .ContentPanel table[width="954px"],
    .ContentPanel table[style="border-collapse: collapse; margin-left: auto; margin-right: auto; border-color:#111111;"],
    .ContentPanel table + br,
    #ctl00_ContentPlaceHolder1_HotPeoples,
    .DivBoxContainer,
    script + br,
    div[style="text-align: right; margin-top: 20px;font-size:0.7em;"],
    #secondHand_list,
    #secondHand_list + br,
    #ctl00_ContentPlaceHolder1_votingThreadMsg,
    #mainTopicTable tbody tr:nth-child(1),
    #ctl00_ContentPlaceHolder1_lb_HitSearchs,
    #ctl00_ContentPlaceHolder1_nominateLabel,
    #forum_list + br,
    #getSerachType,
    #getSerachType + input,
    #getSerachType + input + a,
    #getSerachType + input + a + br,
    td[colspan="5"]{
        display: none !important;
    }
    
    .Topic_FunctionPanel{
        margin: unset;
    }
    
    #forum_list,
    #monthST,
    #searchstring,
    .Topic_FunctionPanel a[href="javascript: Search()"]{
        float: left;
    }
    
    #secondHand_list + br + a img,
    .Topic_FunctionPanel img[src="images/btn-search.gif"]{
        height: 20px !important;
    }

    .HitSearchText {
        color: #cccccc !important;
    }
    
    
    .Topic_FunctionPanel img[src="images/btn-postnew.gif"]{
        padding-left: 73px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 73px;
        height: 20px;
        background: url(data:image/png;base64,) no-repeat;
    }

    .Topic_FunctionPanel img[src="images/btn-search.gif"]{
        padding-left: 73px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 73px;
        height: 20px;
        background: url(data:image/png;base64,) no-repeat;
    }

    /* table */
    .Topic_ForumInfoPanel td,
    .BlockedTR td
    {
        background: #333333 !important;
    }

    /* topics table 
    .ContentPanel td[style="width: 4%; background-color: #F8F8F8; text-align: center;"],
    .ContentPanel td[style="width: 52%; background-color: #F8F8F8;"],
    .ContentPanel td[style="width: 14%; background-color: #F8F8F8; text-align: center;"],
    .ContentPanel td[style="width: 18%; background-color: #F8F8F8; text-align: center;"],
    .ContentPanel td[style="width: 22%; background-color: #F8F8F8; text-align: center;"],
    .ContentPanel td[style="width: 8%; background-color: #F8F8F8; text-align: center;"],
    .ContentPanel td[style="width: 6%; background-color: #F8F8F8; text-align: center;"]*/
    .ContentPanel > table:nth-of-type(1) table tr:not(:nth-of-type(1)):nth-of-type(odd) td,
    #mainTopicTable tr:nth-of-type(odd) td
    {
        background: #333333 !important;
    }
    /*  .ContentPanel td[style="width: 4%; background-color: #FFFFFF; text-align: center;"],
    .ContentPanel td[style="width: 52%; background-color: #FFFFFF;"],
    .ContentPanel td[style="width: 14%; background-color: #FFFFFF; text-align: center;"],
    .ContentPanel td[style="width: 18%; background-color: #FFFFFF; text-align: center;"],
    .ContentPanel td[style="width: 22%; background-color: #FFFFFF; text-align: center;"],
    .ContentPanel td[style="width: 8%; background-color: #FFFFFF; text-align: center;"],
    .ContentPanel td[style="width: 6%; background-color: #FFFFFF; text-align: center;"]*/
    .ContentPanel > table:nth-of-type(1) table tr:not(:nth-of-type(1)):nth-of-type(even) td,
    #mainTopicTable tr:nth-of-type(even) td
    {
        background: #444444 !important;
    }

    /* reformat date */
    .ContentPanel td span[style="color: #800000"] {
        color: #008000 !important;
    }

    /* bottom nav bar */
    .ContentPanel td[style="width: 100%; background-color: #F7F3F7;"] {
        background: #333333 !important;
    }
    
    /* next / prev */
    .ContentPanel td[style="width: 100%; background-color: #F7F3F7;"] > table p > a:nth-child(2){
        display: inline;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOBAMAAADtZjDiAAAAKlBMVEUAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAKE86IAAAADXRSTlMAArYW+XQZ0aF5b1cn7Z30UQAAADtJREFUCNdjYORuY2QAAoHjlwwEQAxJX2WIQAhEgJHVVw0iEHspAayC664yI4yGiUPVwfRBzUGYC7MHAF8tD2HIQQDcAAAAAElFTkSuQmCC) no-repeat;
    }
    .ContentPanel td[style="width: 100%; background-color: #F7F3F7;"] > table td > a:nth-child(1){
        display: inline;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOBAMAAADtZjDiAAAAIVBMVEUAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAABt0UjBAAAACnRSTlMAAtIuKNgE/qsfJFUR1wAAAEJJREFUCNdjAAIBCwMBIMXIoqXCCKQTjFYFA2k2Zq1FDgJAWaNVIWxAWWatpWCu6/JIoCyUhovD1MH0wcyBmQuzBwAvAw6hLt/vkAAAAABJRU5ErkJggg) no-repeat;
    }
    .ContentPanel img[src="images/button-next.gif"],
    .ContentPanel img[src="images/button-prev.gif"]{
        visibility: hidden;
    }
    
    .ContentPanel div[style="width:300px; "] br{
        display: none !important;
    }
}
    
@-moz-document regexp("(http)://(forum[^\.]*?|search|archive)\.hkgolden\.com/(view|[Pp]ost|SendPM)\.(aspx|htm).*") {
    
    /*in-post*/
    
    /* user */
    /* man */
    .repliers_left a[style="font-weight:bold;color: #0066FF;"] {
        color: #5E97F1 !important;
    }
    /* most likely also a man */
    .repliers_left a[style="font-weight:bold;color: #FF0066;"] {
        color: #B6554F !important;
    }
    
    .repliers_left img[src="labels/1.gif"],
    .repliers_right tr:last-child img[src="images/quote.gif"],
    #ctl00_ContentPlaceHolder1_view_form img[src="images/btn-postnew.gif"],
    #ctl00_ContentPlaceHolder1_view_form img[src="images/btn-reply.gif"],
    iframe[src^="mtg/"]{
        display: none;
    }
    
    .repliers_left img[src="labels/4.gif"]{
        padding-left: 86px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 86px;
        height: 20px;
        background: url(data:image/png;base64,) no-repeat;
        /*  block colour background: url(data:image/png;base64,) no-repeat;*/
    }
    
    .repliers_right [id^=laumingHref] img[src="images/lauming.gif"],
    #ctl00_ContentPlaceHolder1_QuickReplyTable img[src="images/btn-lauming.gif"]{
        padding-left: 73px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 73px;
        height: 18px;
        background-size: contain !important;
        background: url(data:image/png;base64,) no-repeat;
    }
    
    .repliers_right tr:last-child img[src="images/report.gif"]{
        padding-left: 73px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 73px;
        height: 18px;
        background-size: contain !important;
        background: url(data:image/png;base64,) no-repeat;
    }
    
    .repliers_right tr:last-child img[src="images/fastquote.gif"],
    #ctl00_ContentPlaceHolder1_QuickReplyTable img[src="images/quote.gif"],
    #ctl00_ContentPlaceHolder1_PMMsgTable img[src="images/quote.gif"],
    .repliers tr:last-child tr:last-child img[src="images/quote_reply.gif"]{
        padding-left: 73px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 73px;
        height: 18px;
        background-size: contain !important;
        background: url(data:image/png;base64,) no-repeat;
    }
    
    #ctl00_ContentPlaceHolder1_btn_Submit,
    #ctl00_ContentPlaceHolder1_btn_Send{
        padding-left: 73px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 73px;
        height: 18px;
        border: 0px !important;
        background-size: 73px !important;
        background: url(data:image/png;base64,) no-repeat !important;
        /* original size, exist for input type background: url(data:image/png;base64,) no-repeat !important;*/
    }

    /* top table */
    #mainTopicTable > div:nth-child(2),
    #ctl00_ContentPlaceHolder1_view_form div[style="background-color: #F7F3F7; padding: 18px 5px 18px 5px;"]/*,
    td[style="width: 150px;background-color: #F3F2F1;border-right: 1px solid #111111;"],
    td[style="background-color: #F3F2F1; height:100%;"]*/
    {
        background: #333333 !important;
    }

    /* main content */
    
    .repliers_left{
        width: unset;
        min-width: 177px;
    }
    .repliers_header{ 
        width: unset;
        min-width: 177px;
        padding: 7px 7px 7px 7px;
    }
    .repliers_header + .repliers_header{ 
        min-width: 740px;
    }
    .repliers_left + td {
        max-width: 740px;
    }
    .repliers_right,
    .ContentGrid{
        width: 740px !important;
    }
    
    a[target="_blank"] > img{
        width: unset !important;
        height: unset !important;
        max-height: 100% !important;
        max-width: 100% !important;
    }
    
    #DivMarkThread{
        background: transparent;
    }
    
    .BlockedTR{
        border: unset !important;
    }
    
    .repliers tr:last-child > td/*,
    .BlockedTR td*/{
        background: #333333 !important;
    }
    
    .repliers_left_user_details{
        margin-left: auto;
        margin-right: auto;
        position: unset;
        background-color: #444 !important;
    }
    
    .video-container {
        position: relative;
        padding-bottom: 56.25%;
        padding-top: 30px; height: 0; overflow: hidden;
    }

    .video-container iframe,
    .video-container object,
    .video-container embed {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
    }
    
    /* highlighted main content */
    /* Supports both original and HKGT */
    #ctl00_ContentPlaceHolder1_view_form td[style*="background-color: #E9EC6C;"],
    #ctl00_ContentPlaceHolder1_view_form td[style*="background-color: #E9EC6C;"] > table > tbody > tr:last-child > td,
    #ctl00_ContentPlaceHolder1_view_form .highlightedReply > td,
    #ctl00_ContentPlaceHolder1_view_form .highlightedReply > td > table > tbody > tr:last-child > td,
    #ctl00_ContentPlaceHolder1_view_form .an-highlighted > td{
        background: #454620 !important;
    }

    /* quote */
    .repliers_left + td blockquote > div {
        color: #6699cc !important;
    }

    /* fast reply + new post */
    #ctl00_ContentPlaceHolder1_QuickReplyTable table table td,
    #ctl00_ContentPlaceHolder1_PMMsgTable table td/*,
    #ctl00_ContentPlaceHolder1_SystemMessageBoard tbody tr td table tbody tr td table tbody tr:nth-child(2) td,
    */{
        background-color: #444 !important;
    }
    #ctl00_ContentPlaceHolder1_messagetext,
    #ctl00_ContentPlaceHolder1_messagesubject{
        background: #444444 !important;
        color: #cccccc;
    }
    #ctl00_ContentPlaceHolder1_QuickReplyTable span[style="background-color:Red; color:White;"],
    #ctl00_ContentPlaceHolder1_QuickReplyTable span[style="background-color:Red; color:White;"] + br,
    #ctl00_ContentPlaceHolder1_QuickReplyTable td[style="width: 85%; background-color: #F3F2F1; text-align:center;"]{
        display: none !important;
    }
    
    .noscript-show{
        visibility: hidden;
    }
    
    /* next / prev */

    div[style="background-color: #F7F3F7; padding: 18px 5px 18px 5px;"] img[src="images/button-next.gif"]{
        padding-left: 14px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 14px;
        height: 14px;
        background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOBAMAAADtZjDiAAAAKlBMVEUAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAKE86IAAAADXRSTlMAArYW+XQZ0aF5b1cn7Z30UQAAADtJREFUCNdjYORuY2QAAoHjlwwEQAxJX2WIQAhEgJHVVw0iEHspAayC664yI4yGiUPVwfRBzUGYC7MHAF8tD2HIQQDcAAAAAElFTkSuQmCC) no-repeat;
    }

    div[style="background-color: #F7F3F7; padding: 18px 5px 18px 5px;"] img[src="images/button-prev.gif"]{
        padding-left: 14px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 14px;
        height: 14px;
        background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOBAMAAADtZjDiAAAAIVBMVEUAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAABt0UjBAAAACnRSTlMAAtIuKNgE/qsfJFUR1wAAAEJJREFUCNdjAAIBCwMBIMXIoqXCCKQTjFYFA2k2Zq1FDgJAWaNVIWxAWWatpWCu6/JIoCyUhovD1MH0wcyBmQuzBwAvAw6hLt/vkAAAAABJRU5ErkJggg) no-repeat;
    }

}

/* error page */
@-moz-document regexp("(http)://(forum[^\.]|search|archive)\.hkgolden\.com/error\.html") {

    body > table td.bg_top[valign="top"][height="144"] {
        background: transparent !important;
    }
}

/* profile page */
@-moz-document regexp("(http)://(forum[^\.]*?|search|archive)\.hkgolden\.com/ProfilePage\.aspx.*?") {
    td[height="52"]{
        display: none !important;
    }
    tr[style="background-color: #E9EC6C"]{
        background-color: #454620 !important;
    }
    #ctl00_ContentPlaceHolder1_lb_website,
    #ctl00_ContentPlaceHolder1_lb_reli,
    tr[style="background-color: #F8F8F8"],
    tr[style="background-color: #f8f8f8"]{
        background-color: #333 !important;
    }
    tr[style="background-color:#ffffff"],
    tr[style="background-color: #ffffff"],
    tr[style^="background-color: #FFFFFF"],
    .ListPMText{
        background-color: #444 !important;
    }
    .mp,
    .ppClass,
    td[bgcolor="#CCDDEA"]{
        background-color: #5c94bd !important;
    }
    .mp:hover,
    .title,
    .bgClass,
    .pp{
        background-color: #4a88b5 !important;
    }
    td span[style="color: #800000"] {
        color: #008000 !important;
    }
    span[id^="ctl00_ContentPlaceHolder1_mp"] a,
    span[id^="ctl00_ContentPlaceHolder1_pp"] a{
        color: #eee !important;
    }
    
    img[src="./faces/beer.gif"]{
        padding-left: 16px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 16px;
        height: 16px;
        background-size: contain !important;
        background: url("data:image/png;base64,") no-repeat;
    }
    
    img[src="../images/close_button.png"]{
        padding-left: 13px;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 13px;
        height: 13px;
        background-size: contain !important;
        background: url("data:image/png;base64,") no-repeat;
    }
}
    
