css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://may.2chan.net/b/res/") {
  body > table,
  tbody {
      margin: 0;
      padding: 0;
  }
  body > table {
      border-top: 1px solid #666;
  }
  body > table > tbody > tr > td:first-child {
      display: none;
  }
  body,
  body > table,
  tbody,
  tr {
      background: #FFF;
  }
  blockquote {
      color: #666;
  }
  a[onclick*="sd"],a[onclick*="del"]{
      font-weight: bold !important;
      color:#E00;
  }
  img {
      opacity: 1.0!important;
  }
  table {
      width: 100%;
  }
  body,
  body > form > table {
      background: #EDEDED;
  }
  body > form table > tbody > tr > td:nth-child(1):not(.ftdc) {
      display: none;
  }
  td.rtd {
      background: #FFF;
      border: 1px solid #EDEDED
  }
  iframe:not(#del_window) {
      display: none
  }
  body>div:first-child,
  div.tue,
  div#ufm+div,
  div#res_menu + hr + div {
      display:none;
  }
}

@-moz-document url-prefix("http://futalog.com/") {
  body > table,
  tbody {
      margin: 0;
      padding: 0;
  }
  body > table > tbody > tr > td:first-child {
      display: none;
  }
  body,
  body > table,
  tbody,
  tr {
      background: #EDEDED;
  }
  blockquote {
      color: #666;
  }
  img {
      opacity: 1.0!important;
  }
  table {
      width: 100%;
  }
  body {
      background: #EDEDED;
  }
  body > form table > tbody > tr > td:nth-child(1) {
      display: none;
  }
  body > table> tbody > tr > td:nth-child(2) {
      background: #FFF;
      margin: 0
  }
  iframe {
      display: none
  }
}

@-moz-document url-prefix("http://imgnolog.net/thread/") {
  body,
  body > table,
  tbody,
  tr {
      background: #EDEDED;
  }
  .comment-box {
      background: #FFFFFF;
  }
  .comment-head {
      font-size: 14px;
      color: #BBB;
  }
  .comment-wrapper,
  .comment-box {
      width: 100%;
  }
  .comment-body {
      margin-top: 10px;
      margin-bottom: 5px;
  }
}

@-moz-document url("http://www.2chan.net/index2.html") {
  body {
      background-image: none!important;
  }
  a {
      color: #BBB;
  }
}

@-moz-document url-prefix("http://may.2chan.net/b/") {
  td.rtd {
      background-color: #FFF;
      color: #BBB;
  }
  td.rtd blockquote {
      color: #800000!important;
  }
  td.rtd a {
      color: #999!important;
  }
  td.rtd b {
      color: #888!important;
  }
}
