css:
@-moz-document domain("rpggeek.com"), domain("boardgamegeek.com"), domain("videogamegeek.com") {
.geekcollection_pager > span > a {
    color: white !important;
}
.media-module__body > div:nth-child(odd) {
    background-color: #DCDCDF !important;
}
.media-module__body > div:nth-child(even) {
    background-color: #F5F5FF !important;
}
.legacy .collection_version {
    white-space: normal;
}
a.ng-binding,
gg-sidebar > div > div > div > gg-quickbar > div > gg-quickbar-list > div > div > ul > li > gg-shortcut > div > a,
gg-sidebar > div > div > div > gg-recently-viewed > ul > li > a,
gg-sidebar > div > div > div > gg-hotness > gg-hotness-items > ul > li > a > div > div.hotness-item-title,
ul.global-secondary-nav-links > li > a {
    font-size: 8pt !important;
}
a.stretched-link {
    font-size: 9pt !important;
}
body {
    background-color: #80808f !important;
    font-family: Verdana, sans-serif !important;
    font-size: 9pt !important;
}
.btn.stretched-link {
    font-size: 8pt !important;
}
btn-group-xs > .btn {
    padding: 0pt;
}
dd {
    border-width: 3px !important;
}
dd.right {
    line-height: 1.33em;
    width: 50em !important;
}
div {
    text-shadow: none !important;
}
div.article,
div.btn-group {
    margin-left: 100px;
}
gg-sidebar,
div.global-secondary-nav {
    width: 175pt !important;
    padding-right: 15pt !important;
}
div.header {
    background-color: #f5f5ff !important;
    border-color: #abc;
}
div.hotness-item-title.ng-binding {
    font-size: 8pt !important;
}
div.js-rollable.article {
    border-width: 3px;
}
div.js-rollable.article.new,
div.js-rollable.article.subbed,
div.js-rollable.article.subbed_selected {
    border-color: #ff0000 !important;
}
div.legacy {
    background-color: #f5f5ff;
}
div.menu_login {
    padding-top: 0px;
}
div.post-body {
    width: 50em !important;
}
div.owned,
div.preordered,
div.wanted,
div.wanttobuy,
div.wishlist,
div[id^='results_geekitem_version'] > div.geekitem_name {
    font-size: 8pt !important;
    font-weight: normal !important;
}
div.quote {
    font-size: 8pt !important;
    background-color: #dadaea !important;
    border-color: black !important;
}
div.rating > span,
div.results_hotitems {
    width: 100%;
}
div[id^='link'] > a,
div.sf > a[href^='javascript:'] {
    font-size: 9pt !important;
}
div.wiki#editdesc {
    font-size: 9pt !important;
    width: 60em !important;
    margin-left: 1em;
    margin-right: 1em;
}
dl {
    border-width: 3px !important;
}
footer.global-footer.row {
    background-color: #f0f0ff;
}
img.mbimg {
    padding-top: 2pt;
}
li.list-group-item {
    padding: 5px;
}
span.pull-left {
    font-size: 9pt !important;
    width: 200px !important;
    overflow: visible !important;
}
span.smallerfont {
    font-size: 7pt !important;
}
table#collectionitems.collection_table > tbody > tr > td > div > div > div > a,
table.geekitem_module > tbody > tr > td > div > div > table > tbody > tr > td > div > table > tbody > tr > td > div > div > div > a,
table.geekitem_module > tbody > tr > td > div > div > table > tbody > tr > td > div > table > tbody > tr > td > div > div > div > div > div > a,
table.innermoduletable > tbody > tr > td > div > a,
div.geekitem_name,
div.disambiguatedlink > div[id^='link'] > a,
table.geekitem_version_table,
div[id^='status_name_version_'],
div[id^='results_name_version_'],
div[id^='status__version_'],
div[id^='results__version_'],
div[id^='status_yearpublished_version_'],
div[id^='results_yearpublished_version_'],
div[id^='status_format_version_'],
div[id^='results_format_version_'],
div[id^='status_productcode_version_'],
div[id^='results_productcode_version_'],
div[id^='status_isbn10_version_'],
div[id^='results_isbn10_version_'],
div[id^='status_isbn13_version_'],
div[id^='results_isbn13_version_'],
div[id^='status_pagecount_version_'],
div[id^='results_pagecount_version_'],
div[id^='status_dimensions_version_'],
div[id^='results_dimensions_version_'],
div[id^='status_weight_version_'],
div[id^='results_weight_version_'] {
    font-size: 8pt !important;
    font-weight: normal !important;
}
table.innermoduletable > tbody > tr > td > table.geekitem_infotable > tbody > tr > td > b,
table.innermoduletable > tbody > tr > td > table.geekitem_infotable > tbody > tr > td > div > a,
table.innermoduletable > tbody > tr > td > table.geekitem_infotable > tbody > tr > td > div > div > a {
    font-size: 9pt !important;
}
table.innermoduletable,
table.moduletable > tbody > tr > td > div[id^='module_1'] > table.innermoduletable.sf > tbody > tr > td > div,
table.moduletable > tbody > tr > td > div[id^='module_1'] > table.innermoduletable.sf > tbody > tr > td > div > a,
table.moduletable.recentitems,
table.sf > tbody > tr > td > table > tbody > tr > td {
    font-size: 8pt !important;
}
table.moduletable > tbody > tr > td > div[id^='module_1'] > table.innermoduletable.sf > tbody > tr > th,
table.moduletable > tbody > tr > td > table.innermoduletable.geekmodule > tbody > tr > td > a.ng-binding {
    font-size: 9pt !important;
}
td.collection_bggrating,
td.collection_commands,
td.collection_comment.editfield,
td.collection_geekitem_version.editfield {
    font-size: 8pt !important;
    font-weight: normal !important;
}
td.collection_objectname > div[id^='results_objectname'] {
    font-size: 8pt !important;
    font-weight: bold !important;
}
td.collection_objectname > div[id^='results_objectname'] > div {
    font-size: 7pt !important;
    font-weight: normal !important;
}
td.collection_acquisitiondate,
td.collection_postdate,
td.collection_wishlistcomment.editfield {
    font-size: 8pt !important;
}
textarea {
    font-family: Consolas, "Courier New", Courier, monospace !important;
}
th[class^='collection'] {
    font-size: 9pt !important;
}
tr[bgcolor^='#ECEDEE'] {
    background-color: #DCDCDF;
}
tr[bgcolor^='#FFFFFF'] {
    background-color: #F5F5FF;
}
div.geekitem_name {
    font-size: 9pt !important;
}
td.collection_wishlistcommentmod,
ul.collectionstatus > li,
td[id='edit_name'] > div[id^='results_name_version_'] > div[id^='name_'] > div.geekitem_name,
div.sf,
div.rating > span,
div[id^='results_version'],
div[id^='results_version'] > div.geekitem_name,
div[id^='results_ownership'] > table.collectiontable_ownership {
    font-size: 8pt !important;
}
.collection_table td,
.collectionmodule_table td {
    font-size: 8pt !important;
}
}
