css:
@-moz-document domain("memecenter.com") {
.content{
	background: #353535;
}
.content, .content-seperator {
    border: 1px solid #000;
}
.buttons .comment, .buttons .tweet, .buttons .share, .buttons .like {
    background: #4C4C4C;
    border: 1px solid #000000;
    color: #000000;
    transition: .5s;
}
.content-right .content-title a {
    color: #FFF;
}
.content-user {
    background: 0 0 rgba(76, 76, 76, 0);
}
.content-user .user-name a {
    color: #FFF;
}
body {
    background: #1F2024;
}
.home-weekly-list{
    background: #353535;
    border: 1px solid #000;
}
.home-weekly-list .wubar {
    background: rgba(255, 255, 255, 0.16);
}
.home-weekly-list .weekly-item {
    border-bottom: 1px solid #1F2024;
}
.home-weekly-list .weekly-item .w-u-right a.username {
    color: #FFF;
    font-size: 13px;
    font-weight: 700;
}
.home-weekly-list .hw-title {
    border-bottom: 1px solid #1F2024;
    color: #1877B9;
}
.home-weekly-list .weekly-item .w-u-right .weekly-rank {
    color: #1877B9;
}
.nf-p-content .content-title h1, .cont-main-c .cmc-left .content .content-title h1 {
    color: #FFFFFF;
}
.comments .comment-title a {
    color: #FFFFFF;
}
html, button, input, select, textarea {
    color: #FFF;
}
.cright-box .cr-top a.cr-next, .cright-box .cr-top a.cr-prev {
    background: #353535;
    color: #FFFFFF;
}
.cright-box {
    border: 1px solid #000;
    background-color: #353535;
}
.cright-box .cr-top a.cr-prev {
    border-right: 1px solid #000;
}
.cright-box .cr-top {
    border-bottom: 1px solid #000;
}
.cright-box .crn-item {
    border: 1px solid #000;
}
.g-plus-box, .social-box {
    background: #353535;
}
.social-box {
    border: 1px solid #000;
}
}
