css:
@-moz-document url-prefix(http://www.transfermarkt.de) {
.werbung-rectangle1, .werbung-rectangle2 {
    display: none;
}

.werbung-skyscraper-container {
    display: none;
}

#werbung_superbanner {
    display: none;
}

#adv_textlogoad {
    display: none;
}

/* Werbung in Artikeln unter "Anzeige" (ganz unten) ausblenden */
.box.werbung.mobile-werbung-inrow {
    display: none;
}

div#adition_textlink_news {
    display: none;
}

/* Sportwetten Icon + Link entfernen */
.large-12.columns.small-12 .zentriert a img {
    display: none;
}

/* Social-Media Bereich (Twitter, Facebook, Snapchat) ausblenden */
#social {
    display: none;
}

/* Werbung bei Wettbewerb, oben */
.werbung-billboard {
    display: none;
}

/* Videowerbung in Artikeln */
p[itemprop="articleBody"] {
   display: none;
}


.adform-adbox {
   display: none;
}

/* entfernt den Bereich "DAS KÖNNTE SIE AUCH INTERESSIEREN" komplett -> 50% Werbung */
.OUTBRAIN {
    display: none;
}


div#recommender {
    display: none;
}

div#adition_Home_Textlink_Wettbewerb_L1 {
	display: none;
}
div#adition_Home_Textlink_Wettbewerb_L2 {
	display: none;
}

/* Video-Werbung */
.teads-inread.sm-screen {
    display: none;
}

div[id^="sas_inRead"] {
    display: none;
}


/*#main div[style] { height: 0px !important; }*/

}
