css:
@-moz-document domain("www.youtube.com") {
/* 
   Warning: The following CSS was just thrown together without any organization whatsoever
   
   Read at your own risk!
*/

#upload-btn, .yt-uix-button-icon-bell, .search-btn-component, .gsok_a, .yt-uix-button-icon-appbar-guide {
    -webkit-filter: invert();
    filter: invert();
}

#upload-btn {
    border: none;
    background: url(https://s.ytimg.com/yts/imgbin/www-hitchhiker-vfl2H8TvN.webp) -228px -188px !important;
}

#upload-btn * {
    display: none;
}

#yt-masthead-user .yt-uix-button-icon-bell {
    opacity: 1;
}

#yt-masthead-container {
    background: #e62117;
    border: none;
}

#masthead-search {
    background-color: #c51109;
}

#masthead-search-term::-webkit-input-placeholder {
    color: #ea8989;
}

#masthead-search-term::-moz-placeholder {
    color: #ea8989;
    opacity: 1;
}

#masthead-search-terms {
    background-color: #c51109;
    border: none;
    box-shadow: none;
}

#masthead-search-terms td {
    border: none;
}

.show-guide #appbar-guide-menu {
    transform: translateX(0);
}

#appbar-guide-menu {
    box-shadow: 5px 10px 15px 5px rgba(0,0,0,.1);
    will-change: transition;
    transition: transform 0.5s;
    transform: translateX(-250px);
    visibility: visible;
    opacity: 1;
    left: 0;
}

.show-guide #appbar-guide-menu::backdrop {
    position: fixed;
    top: 0px;
    right: 0px;
    bottom: 0px;
    left: 0px;
    background: rgba(0, 0, 0, 0.5);
}

button#search-btn {
    background: none;
    border: none;
}

#masthead-search .search-btn-component .yt-uix-button-content {
    opacity: 1;
    background: url(data:image/svg+xml;base64,);
    background-size: contain;
    background-repeat: no-repeat;
    width: 75%;
    height: 75%;
    margin-left: auto;
    margin-top: 4px;
}

body {
    background-color: #FAFAFA;
}

.yt-uix-button-icon-appbar-guide {
    background: no-repeat url(data:image/svg+xml;base64,PHN2ZyBmaWxsPSIjMDAwMDAwIiBoZWlnaHQ9IjI0IiB2aWV3Qm94PSIwIDAgMjQgMjQiIHdpZHRoPSIyNCIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4gICAgPHBhdGggZD0iTTAgMGgyNHYyNEgweiIgZmlsbD0ibm9uZSIvPiAgICA8cGF0aCBkPSJNMyAxOGgxOHYtMkgzdjJ6bTAtNWgxOHYtMkgzdjJ6bTAtN3YyaDE4VjZIM3oiLz48L3N2Zz4=) !important;
    background-size: contain !important;
    width: 20px !important;
    height: 20px !important;
}

.yt-card, #alientube, #player-api {
    box-shadow: 0 0 3px black;
}

.user-photo, img[src^="https://yt3.ggpht.com"] {
    border-radius: 50%;
    background: white;
}

.channel-header-profile-image-container {
    background-color: transparent;
}

.comments .paginator {
    padding: 7px;
    border: none;
    border-radius: 0;
    box-shadow: 0 0 5px black;
    transition: box-shadow 0.5s, transform 0.5s;
}

.comments .paginator:hover {
    box-shadow: 0 0 10px black;
}

.comments .paginator {
    display: block;
    width: fit-content; 
    margin: 15px auto;
}

.yt-thumb {
    background-color: white;
}

#yt-masthead #logo-container .logo {
    background: url(data:image/png;base64,) no-repeat center;
    background-size: 136%;
}

#yt-masthead #logo-container span.content-region {
    color: white;
    font-weight: bold;
    margin-left: -4px;
}

#masthead-search-term {
    color: white;
}

#yt-masthead-user .sb-notif-on .yt-uix-button-content {
    border-radius: 50%;
    background: #403F3F;
    border: none;
}
.ytp-chrome-bottom {
    transition: opacity .25s cubic-bezier(0.0,0.0,0.2,1), transform .25s ease-out !important;
    transform: translateY(0px);
    opacity: 1 !important;
}

.ytp-autohide .ytp-chrome-bottom {
    transform: translateY(60px);
}

.ytp-chrome-top, .ytp-cards-button {
    transition: opacity .25s cubic-bezier(0.0,0.0,0.2,1), transform .25s ease-out !important;
    transform: translateY(0px);
    opacity: 1 !important;
}

.ytp-autohide .ytp-chrome-top, .ytp-autohide .ytp-cards-button {
    transform: translateY(-60px);
}

.vk-box {
    border: none;
    background: red;
}

.vk-btn {
    background: white;
    border: none;
    border-radius: 0 !important;
    box-shadow: 0 0 3px black;
    transition: box-shadow 0.5s;
}

.vk-sf-a {
    background: darkgrey !important;
    box-shadow: none !important;
}

.vk-btn.vk-sf-h {
    border: none;
    box-shadow: 0 0 16px black;
}
}
