css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {
  /* home page */
  * { color: #777 !important; } /**/
  .site-wrapper--home { background-color: #000C17 !important; } /* home page bg color */
  .tag-home__nav__wrap:hover { background-color: #000C47 !important; } /* dotted scroller */
  .tag-home__nav__close:hover { color: #00ACAF !important; } /* dotted scroller close btn */
  .badge-link { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  
  /* side nav bar */
  .nav-menu--slideout { /* side nav bar bg color */
    background-color: #000C17 !important;
    border-style: solid !important;
    border-width: 4px 0px 0px 1px !important;
    border-top-color: #777 !important;
    border-left-color: #00ACAF !important;
  }
  .nav-menu__heading span { color: #00ACAF !important; } /* side nav bar headers */
  .nav-menu__item > a { color: #777 !important; } /* side nav bar link colors */
  .nav-menu__item > a:hover { background-color: #000C47 !important; }
  .header__button--menu { color: #00ACAF !important; } /* side nav bar menu button color */
  .header__button--menu:hover { background-color: #000C47 !important; } /* side nav bar menu button color on hover */
  .nav-menu__close { color: #00ACAF !important; padding: 0.5em 0.5em 0.5em !important; } /* side nav bar close btn */
  .nav-menu__close:hover { background-color: #000C47 !important; } /* side nav bar close btn on hover */
  
  /* Backgrounds */
  .site-wrapper-border { background-color: #00ACAF !important; } /* colored bar up top */
  html, body, .has-zcm, .body--serp { background-color: #000C17 !important; } /* bg colors */
  .header, .header::after, .header-wrap, .header-wrap::after { background-color: #000C17 !important; } /* header bg colors */
  
  /* hard to get m-er-f-er */
  .zci::before, .header-wrap::before, .zcm__menu, .zcm__link::before, .zcm__link::after { background-color: transparent !important; }
  
  /* search box */
  .search--adv { background-color: #000C17 !important; border-color: #00ACAF !important; } /* search box bg and border */
  .search__input--adv { color: #00ACAF !important; } /* search box text color */
  .search__clear { color: #00ACAF !important; } /* clear search btn "x" color */
  .search__clear:hover { background-color: #000C47 !important; } /* clear search btn "x" color on hover */
  .search__button { background-color: #000C17 !important; color: #00ACAF !important; }
  .search__button:hover { background-color: #000C47 !important; }
  
  .acp-wrap { background-color: #000C17 !important; } /* search suggestion drop-down */
  .search__autocomplete { border-color: #00ACAF !important; } /* search suggestion drop-down border */
  .acp strong { color: #777 !important; } /* search suggestion strong/b accent */
  .acp--highlight { background-color: #000C47 !important; color: #00ACAF !important} /* search suggestion highlightl */
  
  /* duckbar pointer */
  .sep--before::before { border-left: 1px solid #00ACAF !important; } /* duckbar separator */
  .zcm__link.is-active { color: #00ACAF !important; } /* duckbar active page */
  .zcm__link:hover { color: #00ACAF !important; background-color: #000C47 !important; }
  li.zcm__item > a.is-active { background: #000C47 url("http://i.imgur.com/2Ty80Oq.png") no-repeat center bottom !important; } /* pointer arrow */
  .header::after, .header-wrap::before, .header-wrap::after, .zcm { box-shadow: 0px 1px 0px #00ACAF !important; } /* under line for zero click pane */
  
  /* zero click pane */
  .zci-wrap { background-color: transparent !important; } /* bg color */
  .zci { background-color: transparent !important; border-style: solid !important; border-color: #00ACAF !important; } /* bg and bottom line */
  /* metabar pane */
  .metabar { background-color: #000C17 !important; } /* metabar bg */
  .metabar__primary-text { color: #00ACAF !important; } /* metabar primary text */
  .btn--icon { border: 1px solid #00ACAF !important; color: #00ACAF !important; background-color: #000C17 !important; } /* meta bar view mode btn */
  .btn--icon:hover { background-color: #000C47 !important; } /* meta bar view mode btn on hover */
  
  .c-info__img-wrap__in { background-color: #000C47 !important; border: 1px solid #00ACAF !important; }
  /* replace meta bar exact match image
  .c-info__img { z-index: -20 !important; }
  .c-info__img-wrap__in { background-image: url("http://i.imgur.com/0yD7O03.png") !important; }
  /*
  
  /* metabar info & popout */
  .attribution--link__icon { color: #00ACAF !important; } /* metabar info icon */
  .popout--bottom::after { border-bottom-color: #00ACAF !important; } /* popout arrow pointer */
  .popout { background-color: #000C17 !important; border: 1px solid #00ACAF !important; } /* popout bg color */
  .tx-clr--slate { color: #00ACAF !important; } /* popout text */
  .attribution__hr { border-color: #00ACAF !important; } /* hr color */
  .popout__body a.btn { background-color: #000C17 !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; } /* popout btn */
  /* metabar info tile */
  .tile--info { border: 1px solid #00ACAF !important; } /* info tile border */
  .tile__foot--news { background-color: #000C17 !important; } /* footer news bg color */
  .tile__expand { background-color: #000C17 !important; color: #00ACAF !important; border-top-color: #00ACAF !important; } /* info tile expand btn */
  .tile__expand:hover { background-color: #000C47 !important; } /* info tile expand btn on hover */
  .info.one-line:hover { background-color: #000C47 !important; }
  div.tile--info > div.info { border-top-color: #00ACAF !important; } /* info tile line borders / dividers */
  div.tile--info > div:first-child { border-top-width: 0px !important; } /* info tile - clean up first un-needed line separator */
  /* metabar tiles */
  .tile, .tile--m.is-disabled { background-color: #000C17 !important; border: 1px solid #00ACAF !important; } /* tile bg & border color */
  .tile-nav.can-scroll { background-color: #00ACAF !important; } /* tile scroller */
  .tile-nav.can-scroll::after { background-color: #00ACAF !important; } /* tile scroller count */
  .tile__title { color: #00ACAF !important; } /* tile title */
  .tile--pr__sub { color: #777 !important; } /* tile sub text */
  .tile__media--pr { background-color: transparent !important; } /* tile pic bg */
  .tile--m--mob { color: #00ACAF !important; } /* show more tiles btn */
  .tile--m--mob:hover { background-color: #00ACAF !important; color: #000C17 !important; }
  /* about pane */
  .c-info__title { color: #00ACAF !important; } /* title text */
  .c-info__title__sub { font-style: italic !important; } /* sub title italics */
  .zci { color: #777 !important; } /* description text */
  /* meanings pane */
  .zcm__link--sub:hover { color: #00ACAF !important; background-color: #000C47 !important; }
  /* search filters */
  .search-filters-wrap::after, .search-filters-wrap::before { background: none !important; }
  .metabar__dropdowns-wrap::after, .metabar__dropdowns-wrap::before { background: none !important; }
  .metabar__grid-btn, .metabar__grid-btn:hover { background-color: #000C67 !important; }
  /* ======== settings page ======== */
  .cloudsave { background: none !important; border: 1px solid #00ACAF !important; }
  .set-reset, .set-bookmarklet { border-color: #00ACAF !important; }
  .set-head { border-color: #00ACAF !important; }
  .frm__hr { border-color: #00ACAF !important; }
  /* controls */
  .button:hover, .butt:hover, .btn:hover, .btn:hover, .button:hover { background-color: #000C67 !important; }
  .btn, .button, .btn--icon, .btn--top, .header__button, .header__button--menu {
    background-color: transparent !important;
    border-color: #00ACAF !important; 
  }
  .btn.is-active, .button.is-active { background-color: #000C67 !important; }
  .set-head__menu .btn:nth-child(2), .set-head__menu .btn:nth-child(3), .set-head__menu .btn:nth-child(4) { border-left: none !important; }
  .set-head__menu .btn:nth-child(1), .set-head__menu .btn:nth-child(2), .set-head__menu .btn:nth-child(3) { border-right: none !important; }
  .frm__select { background: #000C17 none !important; border-color: #00ACAF !important; }
  
  .set-main-footer { border-color: #00ACAF !important; }
  
  /* ============ popups ============ */
  /* modal box */
  .modal__box { background-color: #000C17 !important; color: #777 !important; border: 1px solid #00ACAF !important; }
  .btn--full { background-color: #000C17 !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  .btn--full:hover { background-color: #000C47 !important; }
  .modal__box::before { color: #00ACAF !important; }
  .modal__box::after { color: #000C17 !important; }
  /* lang box */
  .modal--popover--dk.is-showing { background: rgba(0,0,0,0.6) none repeat scroll 0% 0% !important; }
  .region__body { background-color: transparent !important; }
  .region__header__section--current { border-left-color: #00ACAF !important; }
  .modal__close:hover { color: #00ACAF !important; background-color: #000C47 !important; }
  img.region-flag__img { height: 0 !important; width: 0 !important; padding-left: 20px !important; padding-top: 20px !important; background: url("http://i.imgur.com/6H9v9eM.png") no-repeat !important; background-size: contain !important; }
  .region-switch__label { color: #00ACAF !important; }
  
  /* white bg for icons */
  /* github only */
  .zci__more-at__icon[src="//icons.duckduckgo.com/ip2/github.com.ico"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  /* wikipedia only */
  .zci__more-at__icon[src="/assets/icons/favicons/wikipedia.png"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  /* amazon only */
  .zci__more-at__icon[src="/assets/icons/favicons/amazon.png"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  /* ebay only */
  .zci__more-at__icon[src="//icons.duckduckgo.com/ip2/rover.ebay.com.ico"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  /* new york times only */
  .zci__more-at__icon[src="//icons.duckduckgo.com/ip2/topics.nytimes.com.ico"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  
  /* back to top btn */
  .btn--top { color: #00ACAF !important; background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  .btn--top:hover { background-color: #000C47 !important; }

  /* results */
  .about-info-box__toggle--hover .about-info-box__toggle__chevron, .about-info-box__toggle--hover .about-info-box__toggle__chevron--collapse
  { background-color: #000C67 !important; }
  
  .results--sidebar { left: unset !important; right: -290px !important; position: absolute !important; }
  .module--about { background-color: #000C27 !important; }
  .zci pre { border-color: #00ACAF !important; }
  pre, tt, code { background-color: transparent !important; color: #777 !important; }
  #entities * { color: #777 !important; }
  
  .feedback-btn { background-color: #000C17 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  .feedback-btn:hover { background-color: #000C47 !important; }
  .feedback-btn__send { color: #00ACAF !important; }
  
  .cw, .results { max-width: 78% !important; } /* results width adjustment */
  .msg--serp { color: #777 !important; } /* result msgs */
  .msg--bang__bang-name { color: #00ACAF !important; background-color: transparent !important; border-color: #00ACAF !important; } /* bang cmd box */
  .msg--bang__bang-name:hover { color: #000C17 !important; background-color: #00ACAF !important; } /* bang cmd box on hover */
  .badge--official { background-color: #000C17 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; } /* official site badge */
  .badge--official:hover { background-color: #000C47 !important; } /* official site badge on hover */
  
  .result, .results_links_deep, .highlight_d { background-color: transparent !important; } /* bg for results */
  .highlight { background-color: #000C47 !important; } /* result on hover */
  
  .result__check { color: #777 !important; } /* result check color */
  .result__check:hover { color: #00ACAF !important; } /* result check color on hover */
  .result__a, .result__title a b { color: #00ACAF !important; } /* result title color */
  .result__snippet { color: #777 !important; } /* result snippit color */
  .result__snippet b { color: #999 !important; } /* result snippit bold color */
  
  .result__url { color: #00AC6F !important; } /* result url */
  /* white bg for icons */
  /* github only */
  .result__icon__img[src="//icons.duckduckgo.com/ip2/github.com.ico"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  /* wikipedia only */
  .result__icon__img[src="/assets/icons/favicons/wikipedia.png"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  /* amazon only */
  .result__icon__img[src="/assets/icons/favicons/amazon.png"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  /* ebay only */
  .result__icon__img[src="//icons.duckduckgo.com/ip2/rover.ebay.com.ico"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  /* new your times only */
  .result__icon__img[src="//icons.duckduckgo.com/ip2/topics.nytimes.com.ico"] { background: transparent url("http://i.imgur.com/m0VnZ3T.png") no-repeat left !important; }
  
  /* load more results */
  .btn--alt { background-color: #000C17 !important; color: #00ACAF !important; border-color: #00ACAF !important; }
  .btn--alt:hover { background-color: #000C47 !important; }
}
