css:
@-moz-document domain(facebook.com) {

/* Hide Suggested and Sponsored Posts */
div[data-ft*="ei"] { display: none; }

/* Hide Suggested Pages and Crap */
div#rightCol[aria-label*="ads"] { display: none; }
div[id^="PageAdsPagelet"] { display: none; }

/* Hide Copyright Information */
#pagelet_rhc_footer { display: none; }

/* Wide Body */
html body._5vb_ #globalContainer,
.fbx #globalContainer
{ 
    width: 100% !important;
    min-width: 981px;
    box-sizing: border-box;
}

/* Newsfeed Wide Content */
html ._5vb_._5vb_.hasLeftCol .hasRightCol div#contentArea
{
    width: 100% !important;
    min-width: 496px;
    box-sizing: border-box;
}
._5nb8 {
    width: 100%;
    margin-right: -338px;
   padding-right: 338px;
    box-sizing: border-box;
}

/* Centered Buttons */
._2pi4, ._5pcp { text-align: center; }

/* Sized and Centered Pages, Pictures, and Videos */
._3x-2 .mtm { width: 470px; margin: 0 auto; }
._4-eo { margin: 0 auto; }

/* Post Titles */
._3dp > div > ._6a { width: 100%; box-sizing: border-box; }
._3dp > div > ._6a > :nth-child(2) { width: 100%; }

/* Header */
._uaw, ._5vb_ ._uaw { width: 100%; margin: 0 10px; box-sizing: border-box; }
.sidebarMode ._uaw { padding-right: 20px; }

/* Timeline Content */
#pagelet_main_column_personal {
    box-sizing: border-box;
    padding: 0 15px;
}
._4lh .fbTimelineTwoColumn[data-type="s"],
.fbTimelineUnit.fbTimelineTwoColumn .timelineUnitContainer.fbTimelineComposerUnit
{ width: 100%; }

/* Timeline Sticky Header */
.fbTimelineStickyHeader .stickyHeaderWrap,
.fbTimelineStickyHeader .back
{ width: 100vw; }
.fbTimelineStickyHeader .actions { padding-right: 15vw; }

/* Timeline Content */
.timelineLayout #contentArea {
    width: 100% !important;
    margin-left: -130px;
    padding-left: 130px;
    box-sizing: border-box;
}

/* Timeline Cover Picture Resize */
#fbProfileCover .coverWrap,
._58gc
{
    height: 29vw;
    min-height: 313px;
}

/* Page Content */
._2gqw { 
    width: 100%;
    box-sizing: border-box;
}
._2gqw > .clearfix > :first-child {
	width: 100%;
    min-width: 826px;
    box-sizing: border-box;
    margin-left: -168px;
    padding-left: 168px;
}
._2gqw > .clearfix > :last-child {
    float: right;
}

/* Page Posts */
._37_4 ._2uor {
    width: 100%;
    box-sizing: border-box;
    margin-left: -324px;
	padding-left: 332px;
}

/* Unofficial Page Content */
._4bmm {
    width: 100%;
    min-width: 926px;
    box-sizing: border-box;
}
._2w33 ._2w34 {
    width: 100%;
    min-width: 510px;
    box-sizing: border-box;
    margin-right: -336px;
    padding-right: 336px;
}

/* Unofficial Page Cover */
._jlw {
    width: 100%;
    box-sizing: border-box;
    min-width: 844px;
    height: 18.5vw;
    min-height: 200px;
}
._jl- {
    height: 15.8vw;
    min-height: 156px;
}
._jm0 {
    width: 100%;
    min-width: 644px;
    box-sizing: border-box;
    margin-left: -200px;
    padding-left: 200px;
}

/* Messages Ads */
._4rw #rightCol {
    display: none !important;
}

/* Messages Content */
.hasRightCol #contentArea {
    width: 100%;
    min-width: 714px;
    box-sizing: border-box;
}
._4rw .hasRightCol #contentArea, ._4rw #contentArea {
    width: 100%;
    min-width: 851px;
    box-sizing: border-box;
}
._2nb {
    width: 100% !important;
    min-width: 540px;
    box-sizing: border-box;
    margin-right: -308px;
    padding-right: 308px;
}
._2nc .uiScrollableAreaBody, ._2nc {
    width: 100% !important;
}

/* Lists Header */
._5vb_.hasLeftCol #headerArea {
    width: 100%;
    min-width: 786px;
    box-sizing: border-box;
}

/* Group Cover */
#mainContainer .groupLitestandClassicJumpLayout div#headerArea {
    width: 100%;
    min-width: 786px;
    box-sizing: border-box;
    margin-right: -295px;
    padding-right: 295px;
}
.groupJumpLayout #fbProfileCover .coverWrap {
    height: 23.3vw;
}

/* Group & FriendsList Posts */
html ._5vb_._5vb_.hasLeftCol .groupJumpLayout div#contentArea,
html ._5vb_._5vb_.hasLeftCol .friendListFilter div#contentArea
{
    width: 100% !important;
    min-width: 496px;
    box-sizing: border-box;
    margin-right: -295px;
    padding-right: 295px !important;
}

/* Group Suggestions */
#GroupsRHCSuggestionSection {
    display: none;
}
.groupSkyAux.pagelet.groups_rhc > :nth-child(2),
.groupSkyAux.pagelet.groups_rhc > :nth-child(3),
.groupSkyAux.pagelet.groups_rhc > :nth-child(6) {
    display: none;
}
}
