css:
@-moz-document domain(deezer.com) {
/**
 *    @section ?? basics
**/

body { color: #555; }
body,
.page-main { background: #191919!important; }

.logo { opacity: .5; }
.logo:hover { opacity: 1; }


/**
 *    @section ?? buttons
**/

/* buttons - default */
.btn-default,
.btn-telco,
.btn-slide,
.btn-app,
.btn-group>.btn-label,
html input[type="button"].btn { color: #555; background: none; border-color: #333; }

/* buttons - hover */
.btn-default:hover,
.btn-telco:hover,
.btn-upload:hover .btn,
.btn-slide:hover,
.btn-app:hover,
.btn-group>.btn-label:hover,
.selectbox.opened .dropdown-toggle.btn-default,
.selectbox.opened .dropdown-toggle.btn-telco,
.selectbox.opened .dropdown-toggle.btn-slide,
.selectbox.opened .dropdown-toggle.btn-app,
.selectbox.opened .btn-group>.dropdown-toggle.btn-label { color: #777; background: #111; }

.btn-group>.btn.btn-default.active,
.btn-group>.btn.active.btn-telco,
.btn-group>.btn.active.btn-slide,
.btn-group>.btn.active.btn-app,
.btn-group>.btn.active.btn-label { background: none; }

/* button - icon */
.btn-default .icon,
.btn-telco .icon,
.btn-slide .icon,
.btn-app .icon,
.btn-group>.btn-label .icon { color: inherit !important; }

/* button - link */
.btn-link { color: #444; }

/* primary */
.btn-primary,
.btn-premiumplus { color: #555; background: none; border-color: #333; }
.btn-primary:hover,
.btn-premiumplus:hover { color: #fff; border-color: transparent; }

.btn-danger { color: #DC3434; background: none; border-color: #DC3434; }
.btn-danger:hover { color: #111; border-color: #BD2D2D; }

.btn-primary-flow { color: #444; background: #111; }
.btn-primary-flow:hover { background: #000; }

/* tags */
.btn-tag { color: #555; background: #222; }

/* nano searchbox */
.naboo-searchbar { border-color: #333; }
.naboo-searchbar .heading-3:after { color: inherit; }

/**/
.btn-info-light { color: inherit; background: none; }
.btn-info-light:hover { color: #007FEB; background: none; }

/* more */
.btn-view-more,
.btn-view-more-inverse,
.btn-view-more:hover,
.btn-view-more-inverse:hover { background: #222; }
.btn-view-more .label,
.btn-view-more-inverse .label { color: #666; }
.btn-view-more:hover .label,
.btn-view-more-inverse:hover .label { color: #666; }

/* share button */
.btn-share { overflow: hidden; background: none; }
.btn-share:before { opacity: .5; }
.btn-share:after { background-color: #333; border-radius: 0; }
.btn-share:hover:before { opacity: 1; }
.btn-share:hover:after { background-color: #555; }


/**
 *    @section ?? sidebar
**/

/* sidebar */
.page-sidebar { background: #222; box-shadow: 0 0 30px rgba(0,0,0,.25); }
.page-sidebar .nav-link { padding: 10px; color: #777; background: #222; border-radius: 3px; }
.page-sidebar .nav-main .nav-link { margin: 1px 20px; line-height: 26px;}
.page-sidebar .nav-link.active-link { /*color: #777;*/ background: none; border-color: transparent; }
.page-sidebar .nav-link:hover { background: none; }
.page-sidebar .nav.panel-opened>li>a.active-link { border-color: transparent; }
.page-sidebar .nav-link.active-panel { background: #191919; }

/* profile */
.page-sidebar .profile .profile-settings { color: #444; }

/* sidebar - search */
.page-sidebar .search-control { background: #111; }

/* sidebar - controls */
.page-sidebar .player-controls,
.page-sidebar .player-controls:hover { background: #222; }
.page-sidebar .player-controls button { opacity: .2; }
.page-sidebar .player-controls button:hover { opacity: .75; }

/* search */
.search-history .nano-card-item:hover { background: #111; }


/* panel */
.page-panels .panel-header { background: #111; border-color: #222; }
.page-panels .panel-header .heading-3 { color: #444; }
.page-panels .panel-header .form-control { background: #222; border-color: #333; }
.page-panels .panel-body { background: #111; }
.page-panels .panel-overlay { background: #000; }

.panel-settings .slider-bar { background: #222; }
.panel-settings .slider-bar-handler { background: #999; border-color: transparent; }
.panel-settings .checkbox-label:before { border-color: #333; }
.panel-settings .btn-logout { border-color: #333; }
.panel-settings .icon { opacity: .5; }

.panel-apps .app-suggest { background: none; }

.page-notifications>.notification>h2 { color: #999; }

.panel-notifications .notifications-item { background: none; }
.panel-notifications .notifications-item:hover { background: #000; }
.panel-notifications .panel-navbar-link:hover,
.panel-notifications .panel-navbar-link.active { background: #333; }
.panel-notifications .panel-navbar-link:hover { color: #999; }
.panel-notifications .panel-navbar-item+.panel-navbar-item { border-left-color: #333; }
.panel-notifications .panel-navbar { border-bottom-color: #333; }
.panel-notifications .notifications-placeholder-text,
.panel-notifications .notifications-placeholder-title { color: #555; }

.nano-card-picture { border: none; }
.nano-card-item { background: #111; }
.nano-card-item.has-contextmenu,
.nano-card-item:hover { background: #000; }
.nano-card-link { background: none; }
.nano-card-link:hover { background: #000; }
.nano-card-options a,
.nano-card-link>.heading-4 .icon { color: #555; }
.nano-card-options a:hover { color: #777; }
.nano-card-options .option { color: #444; }
.nano-card-link>.heading-4 .icon-rounded { color: #111; }
.nano-card-plus .nano-card-picture { color: #007FEB; background: none; border: 1px solid #007FEB; }
.nano-card-list li { margin-bottom: 15px; }

/* scroll bar */
.page-sidebar .navigation>.nano-pane>.nano-slider,
.nano>.nano-pane>.nano-slider { background: #fff; opacity: .1; }

/* tooltip */
.tooltip { background: #000; }
.tooltip-bottom:after { border-bottom-color: #000; }

/**
 *    @section ?? content
**/

/* content - header */
.naboo-catalog .catalog-header { background: none; }

.naboo-catalog .catalog-header .infos { border-color: #333; }
.naboo-catalog .catalog-header .list-infos>li { color: #444; border-color: #333; }
.naboo-catalog .catalog-header .list-infos .info-link { color: inherit; }
.naboo-catalog .catalog-legal-notice { color: #444; }

.page-naboo-search .search-header,
.page-naboo-search .search-suggest { background: none; }
.page-naboo-search .suggest-column .nano-card-item { background: #111; }
.page-naboo-search .suggest-column .nano-card-info { border-color: transparent; }
.page-naboo-search .datagrid-container { background: none; }
.page-naboo-search .suggest-column .nano-card-item:hover { background: #222; }

.bg_white { background: #111; }
.inapp-add-favorite { background: none; border-color: #222; }
#w_popup_login .header_premium,
#w_popup_login .foot_step2,
.autorisation #w_popup_login .foot_step2{ text-shadow: none; background: #111; border-color: #222; }
.autorisation #w_popup_login { border-color: #222; }
#w_popup_login .header_premium .encoche { display: none; }
#w_popup_login dd { color: #444; }

#page_naboo_playlist .playlist-description,
#page_naboo_playlist .playlist-type { color: #444; }
#page_naboo_playlist .playlist-type .playlist-option+.playlist-option { border-color: #333; }

.navbar { background: none; }
.navbar-nav>li>a { color: #444; }
.navbar-nav>li>a:hover,
.navbar-nav>li.active>a { color: #666; border-width: 0 0 1px; }

.thumbnail-bordered { border-color: transparent; }
.thumbnail-caption { background: none; border-color: transparent; }
.thumbnail .options-overlay { background: #222; opacity: .5; }
.thumbnail .options-overlay:hover { opacity: 0; }
.thumbnail-wrapper { background: #222; }
.thumbnail-caption .caption-released { color: #444; font-size: 12px; }
.thumbnail:hover { box-shadow: 0 0 30px rgba(0,0,0,.5); }

/* content - */
.page-scroll {background: #191919; }

/* content - catalog */
.naboo-catalog .catalog-content { border-color: #333; }
.datagridle { background: none; }
.datagrid-table thead th { color: #555; text-shadow: none; background: none; }
.datagrid-table tbody tr { border-bottom: 1px solid rgba(255,255,255,.01); }
.datagrid-table th,
.datagrid-table td { color: #444; }
.datagrid-table tbody tr.active td,
.datagrid-table tbody tr.contributors td,
.datagrid-table tbody tr.contributors td:hover,
.datagrid-table tbody tr:hover.active td,
.datagrid-table tbody tr:hover td { background: #111; }
.datagrid-table tbody tr td a { color: inherit; }
.datagrid-table tbody tr td.track .featuring>a { color: #444; font-style: oblique; }
.datagrid-table tbody tr td.position .number { color: #555; }
.datagrid input[type="checkbox"] { opacity: .2; }
.datagrid-table tbody tr td.popularity .note, .datagrid-ghost .cell-popularity .note { opacity: .2; }
.datagrid-table tbody tr.caption td,
.datagrid-table tbody tr.caption td:hover { background: none; }
.datagrid-table tbody tr.unavailable td,
.datagrid-table tbody tr.unavailable td a { color: #333; }

.catalog-tab-tracks .legal { color: #444; }

.icon-datagrid { color: #333; }

/* content - comments */
.naboo-catalog .catalog-tab-box { background: none; }
.catalog-tab-comment .comment-message.post .form-control { background: rgba(0,0,0,.2); border: none; }
.catalog-tab-comment .comment-message.post .submit,
.catalog-tab-comment .comment-message.post .sharer,
.catalog-tab-comment .comment-sharer,
.catalog-tab-comment .comment-submit { color: #444; }
.catalog-tab-comment .comment-message { background: rgba(0,0,0,.2); }
.catalog-tab-comment .comment-message .message-date { color: inherit; }

.comment-box .user-comment .message { background: #1b1b1b; }
.comment-box .user-comment:hover .message { background: #222; }

.comment-box { color: #444; }

.catalog-tab-discography>.heading>.cell-line>.line { background: #333; }
.catalog-tab-discography>.album-row .album-more { border-color: #333; }
.catalog-tab-discography>.album-row .album-more:hover { background: #111; border-color: #333; }
.catalog-tab-discography>.album-row .album-legal { color: #444; }
.catalog-tab-discography>.album-row .album-release { color: #666; }

.sticker-gray { background: #000; }
.thumbnail-caption .release { color: #444; }

/* #feed */
#page_feed .divider .divider-line,
#page_feed .divider .divider-text { color: #555; background: #333; }
#page_feed .divider .divider-line, #page_feed .divider .divider-text { color: #777; }
#page_feed .card { background: #111; }
#page_feed .card-edito-track .visual { border-color: transparent; }
#page_feed .card-edito-track .progress_bar,
#page_feed .card-trending .progress_bar { opacity: .1; }
#page_feed .footer { background: none; border-top: 1px solid #222 }

/* profile */
#page_profile .user-profil .user-section,
#page_profile .user-profil .user-info { background: none; border-color: #333; }
#page_profile .user-profil .user-info .user-message .profile_page_message_placeholder { color: #444; }
#page_profile .user-profil .user-info .user-message .input-message { color: #444; background: none; }
#page_profile .user-profil .user-info.active { box-shadow: none; }
#page_profile .user-profil .user-section .user-section-suggestion { background: none; }
#page_profile .user-profil .user-section .user-section-suggestion .user-arrow-down { display: none; }
#page_profile hr.line-suggestion,
#page_profile hr.line-episodes { background: #333; }
#page_profile .user-personal-song { background: #111; border-color: #333; }
#page_profile .user-personal-song>span { opacity: .25; }
#page_profile .create-playlist-container { border-color: #333; }
#page_profile .create-playlist-container:hover { background: #111; }
#page_profile .create-playlist-icon { background: none; border: 1px solid #007FEB; }
#page_profile .create-playlist-icon .icon { color: #007FEB; }
#page_profile .profile-header .user-background .user-background-position .user-header .user-header-info .user-ligne { opacity: .75; }
#page_profile .user-section { border-color: #222; }

#page_account_share .social-music .social-music-item { background: none; border-color: #333; }
#page_account_share .list-options-item,
#page_account_share .list-options-item:nth-child(2n){ background: none; }
#page_account .account-wrapper { background: #222; }
#page_account .heading-2 { color: #555; }
#page_account .account-delete { background: #222; border-top: 1px solid #333; }
#page_account .account-delete .link-remove { color: #555; }
#page_account .account-social .social-cell { color: #777; background: #333; border-color: #222; }
#page_account .account-social .social-cell .btn { color: #777; }
#page_account .account-social .social-options-item,
#page_account .account-social .social-options-item:nth-child(2n) { background: none; }
#page_account .account-social .social-platform .social { opacity: .5; }
#page_account .account-social .social-platform .social:hover { opacity: 1; }

#page_feed .card-trending .visual,
#page_feed .card-auto-promo .visual,
#page_feed .card-edito-playlist .visual,
#page_feed .card-release-album .visual,
#page_feed .card-radio .visual,
#page_feed .card-push-album .visual,
#page_feed .card-algo-playlist .visual,
#page_feed .card-edito-album .visual,
#page_feed .footer>.btn-link+.btn { border: none; }
#page_feed .info-down .heading-5,
#page_feed .info-down .heading-5-sub,
#page_feed .info-down .heading-5-sub>a,
#page_feed .footer .time,
#page_feed .card-push-album .info .message { color: #444; }
#page_feed .card-push-album .info ul>li,
#page_feed .card-push-album .info .message-line,
#page_feed .card-release-album .details ul>li+li { border-color: #222; }
#page_feed .card-push-album .info .message-line .arrow { background: #222; border-color: transparent; }
#page_feed .title_separ { color: #555; border-color: #222; }
#page_feed .info-down .heading-5>a,
#page_feed .info-down .bloc-ellipsis>a.more { color: #555; }
#page_feed .card-edito-playlist .info-track .tracks ul>li+li,
#page_feed .card-auto-promo .logo-deezer-sessions,
#page_feed .card-radio .info-track .tracks .info-down,
#page_feed .info-down .heading-5.heading-bordered{ border-color: #222; }
#page_feed .info-down .thumbnail { opacity: .5; }
#page_feed .info-down .thumbnail:hover { opacity: 1; }
#page_feed .heading-2,
#page_feed .heading-2 a { color: #777!important; }

#page_naboo_artist .content-left { background: #1b1b1b; }
#page_naboo_artist .content-right { border-color: #333; }

.table_device { border-color: #333; }

/* settings */
#setting_index,
#setting_offer,
#setting_settings,
#setting_devices,
#setting_myapps { background: none; }
#setting_index ul.info li input[type=text],
#setting_index ul.info li input[type=password],
#setting_index ul.info li select { background: none; border-color: #333; }
.setting_title2 { border-color: #333; }
#setting_index #profil_avatar img { border-color: transparent; }
#unsubscribe_title { display: block; height: 50px; margin: -21px -20px; padding: 0!important; line-height: 48px; text-align: center; background: #333; }
#setting_offer :first-child { background: none!important; }

/* live menu */
#livebar_menu,
#livebar_menu a { background: #111; border-color: #222; }
#livebar_menu .tabs { opacity: .25; }
#livebar_menu .tabs:hover { opacity: .5; }

#livebar .search_filter { border-color: transparent; }

#livebar_content { background: #222; }
.no_friends { text-shadow: none; }

/* modal */
#modal_sharebox .share-followers,
#modal_sharebox .modal-footer { background: #222; }
#modal_sharebox .share-nav { background: #111; }
#modal_sharebox .share-nav-tabs { border-radius: 0; }
#modal_sharebox .share-nav-tabs.selected,
#modal_sharebox .share-nav-tabs:hover { color: #72727d; background: #222; }
#modal_sharebox .share-friends-form { background: none; border-color: #333; }
#modal_sharebox .share-friends-filter:after { background-color: #333; }
#modal_sharebox .zeroclipboard-is-hover { background: #222; }

/* headlines */
.heading-1,
.heading-2,
.heading-3,
.heading-4,
.heading-5 { color: #666; }

.heading-1 a,
.heading-2 a,
.heading-3 a,
.heading-4 a,
.heading-5 a,
.heading-1 a:hover,
.heading-2 a:hover,
.heading-3 a:hover,
.heading-4 a:hover,
.heading-5 a:hover { color: inherit; }

.heading-1-sub,
.heading-2-sub,
.heading-3-sub,
.heading-4-sub,
.heading-5-sub { color: #555; }
.heading-1-sub a,
.heading-2-sub a,
.heading-3-sub a,
.heading-4-sub a,
.heading-5-sub a,
.heading-1-sub a:hover,
.heading-2-sub a:hover,
.heading-3-sub a:hover,
.heading-4-sub a:hover,
.heading-5-sub a:hover { color: inherit; }

.heading-bordered { border-color: #333; }

.page-contextmenu .menu-item { color: #666; }

/* form */
.form-control { color: #777; background: none; border-color: #333; }
.form-control:focus { color: #777; }
::-webkit-input-placeholder {
   color: #555!important;
}
::-moz-placeholder {  /* Firefox 19+ */
   color: #555!important;
}

:-ms-input-placeholder {  
   color: #555!important;
}

.form-inline .form-group-divider { background-color: #444; }

/* icons */
.icon-stack-circle .icon { opacity: .5; }
.icon-stack-circle:hover .icon { opacity: 1; }

/* dropdown */
.dropdown .dropdown-menu { border-color: #333; }
.dropdown .dropdown-menu>li>a { color: #444; background: #111; }
.dropdown .dropdown-menu>li>a:hover { color: #555; background: #000; }

/* context menu */
.page-contextmenu .menu-item { background: #111; }
.page-contextmenu .menu-item:hover { color: #999; background: #000; }
.page-contextmenu .menu-item:hover > .icon { color: #999; }
.page-contextmenu .menu-item.link { background: #222; }
.page-contextmenu .menu-item.link:hover { background: #000; }
.page-contextmenu .heading-5 { border-color: #222; }
.page-contextmenu .heading-5>a,
.page-contextmenu .heading-5-sub { color: #555; }


/* popover */
.popover { color: #555; background: #111; border-color: #111; box-shadow: 0 0 30px rgba(0,0,0,.5); }
.popover .popover-header { border-color: #222; }
.popover-left:before,
.popover-bottom:before,
.popover-top:before,
.popover-right:before,
.popover-left-bottom:before{ display: none; }
.popover-left:after { border-left-color: #111; }
.popover-bottom:after { border-bottom-color: #111; }
.popover-right:after { border-right-color: #111; }
.popover-top:after { border-top-color: #111; }
.popover-left-bottom:after { border-left-color: #111; }
.popover-right-top:before,
.popover-right-top:after { border-right-color: #111; }

/* modal */
.modal-dialog { color: #444; background: #111; }
.modal-header { color: #444; border-color: #222; }
.modal-footer,
.modal-playlist-assistant .modal-footer { background: none; }
.modal-playlist-assistant .modal-footer:before { border-color: #333; }
.modal-playlist-assistant .icon { color: #999; }
.modal-playlist-assistant .playlist-option input[type="checkbox"] { opacity: .5; }
.modal-playlist-assistant .playlist-option,
.modal-playlist-assistant .playlist-option-help { color: inherit; }
.modal-backdrop { background: none; }

/* nav bar */
.navbar,
.navbar-nav>li.dropdown>a { border-color: #333; }
.navbar-nav>li.dropdown:hover>a { background: #333; border-color: transparent; }

/* home nav */
.home-title .heading-3:before,
.home-title .heading-3:after { background-color: #333; }
.home-nav .nano-card-item,
.home-tops .nano-card-item,
.home-mix .thumbnail-infos { background: #222; border: 1px solid #333; }
.home-nav .nano-card-item:hover,
.home-tops .nano-card-item:hover,
.home-mix .thumbnail-infos:hover {  background: #000; }
.home-nav .nano-card-item:hover .heading-4 { color: #777; }
.home-nav .icon,
.home-tops .caption { color: #555; }

/* placeholder - title */
.placeholders-title { color: #444; }
.placeholders-img.recommendation { opacity: .25; }

/* sticker */
.sticker-default { background: #333; color: rgba(255,255,255,.25); }
.sticker-blue-light { background: #222; opacity: .5; }
.sticker-red,
.sticker-purple,
#page_feed .sticker { opacity: .5; }

/* links */
a.link-primary { opacity: .5; }

.color-gray { color: #444; }
.bg-gray { background: #191919; }

/* social buttons */
.social { background: none; }
.social>.icon { color: #555; }
.social>.icon:hover,
.social.social-small:hover .icon { color: #fff; }

.btn-facebook,
.btn-twitter,
.btn-googleplus { opacity: .5; background: none; }
.btn-facebook:hover,
.btn-twitter:hover,
.btn-googleplus:hover { opacity: 1; }

a.link-gray-dark { color: #444; }

/* Hörbücher */
.block_cover { text-shadow: none; }

.tinyscroll_thumb { opacity: .1; }

/* app */
.fc_000 { color: inherit; }
#page_appstudio_header { color: #444; background: none; border-color: #222; }
#page_appstudio h2 { color: inherit; }
.showcase .apps_container .applist_container .application { background: #222; border: none; }
.showcase .app_cat_menu .menu_item { background: #222; }
.showcase .apps_container .applist_container .application .app_img,
.showcase .meta_triangle { opacity: .5; }
.showcase .apps_container.app_big { border-color: #222; }
.showcase .app_cat_menu { border-color: transparent; }

/* ads */
.page-ads,
.ads.ads-bottom,
.ads.ads-top { background: #1b1b1b; border-color: #222; }
.page-ads > *,
.ads.ads-bottom > *,
.ads.ads-top > * { opacity: .25; }
.page-ads:hover > *,
.ads.ads-bottom:hover > *,
.ads.ads-top:hover > * { opacity: 1; transition: opacity .5s; }

/* adblock banner */
.abp-banner { background-color: #222; }
.abp-banner-visual { opacity: .5; }
.abp-banner .heading-2,
.abp-banner .heading-3,
.abp-banner-cta button a,
.abp-banner-link { color: #555; }
.abp-banner-close-icon:hover,
.abp-banner-cta button:hover a { color: #fff; }

/** { border-radius: 0 !important; }*/

/* start page (not logged in) */
.page-login:after,
.page-signup:after { content: ''; position: absolute; top: 0; right: 0; bottom: 0; left: 0; background-color: rgba(0,0,0,.5); pointer-events: none; }
.page-login .index-container,
.page-signup .index-container { position: relative; z-index: 2; }

.page-login .btn-facebook,
.page-login .btn-googleplus,
.page-signup .btn-facebook,
.page-signup .btn-googleplus { opacity: 1; }

.page-login .btn-facebook,
.page-signup .btn-facebook { background-color: #2c4372; border-color: #2c4372; }
.page-login .btn-googleplus,
.page-signup .btn-googleplus { background-color: #dedee3; border-color: #dedee3; }

.page-login .btn-primary,
.page-signup .btn-primary { background-color: #dedee3; }
.page-login .btn-primary:hover,
.page-signup .btn-primary:hover { color: #333; }

/* new */
.catalog-header .header-info .heading-link,
.catalog-header .header-info-list { color: #444; }

.catalog-tab-comment .post-comment .form-control,
.datagrid-divider .datagrid-row,
.naboo-catalog .has-alternate-bg,
.carousel-control,
.slide .slide-background,
.datagrid,
.naboo-catalog .has-white-bg,
.banner-header {
	background: none;
}

.datagrid-header .datagrid-row:focus,
.datagrid-header .datagrid-row:hover,
.datagrid-row:focus,
.datagrid-row:hover { background: rgba(255,255,255,.025); }

.datagrid-header .datagrid-row::before,
.datagrid-row::before { background-color: #222; }
.datagrid-row.unavailable .datagrid-label { color: #333; }

.carousel-control,
.checkbox::before,
.datagrid-toolbar .toolbar-search .form-control {
	border-color: rgba(255,255,255,.1);
}
.carousel-control:hover:not(.disabled) { background: none; }

a,
.datagrid-note,
.datagrid-label,
.datagrid-label-main {
	color: #555;
}

.datagrid-label-sub,
.datagrid-action { color: #444; }
.datagrid-action:hover { background: #333; color: #666 !important; }

.datagrid-row { height: auto; padding: 5px 0; border-radius: 0; }
.datagrid-header .datagrid-row::before,
.datagrid-row::before { left: 0; right: 0; }

.large-card .view-more { color: #666; }
.large-card-inner-infos .heading-5,
.large-card-inner-infos .heading-label { color: #444; }

/* end of stylesheet */
}
