css:
@-moz-document domain("androidpolice.com") {
.band.content.main .container .primary.column {
	width: 930px !important;
}

.band .container {
	max-width: 1250px !important;
}

div.header {
    height: 36px !important;
    padding: 0px !important;
}

h1.logo {
    background: url(data:image/png;base64,) no-repeat !important;
    height: 56px;
    width: 200px;
    margin: 0px;
}

h1.logo:before {
	content: "AndroidPolice";
}

a.post-thumb {
    max-height: 80px !important;
}

.post-thumb img {
    max-width: 80px !important;
}

div.nav, div.sub-nav, li#aprecentpostswidget-2, li#aprecentpostswidget-5, li#aprecentpostswidget-4, div.article-nav, div.single-post-widget, div.ap-hottest-content-wrapper {
    display: none !important;
}

}
