css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("w3schools.com") {
  /* body background */
  body, .w3-col, .w3-example, .w3-row, .w3-white, .w3-light-grey { background-color: #000C17 !important; color: #777 !important; border-width: 0px !important; box-shadow: none !important; }
  #main { background-color: transparent !important; }
    
  /* -=-=-=-=-=-=-=-=- Image replacements */
    /* try it yourself */
  img[src="/images/tryitimg.gif"] { display: none !important; }
    /* w3 cert */
  .w3-row > .w3-third.w3-container.w3-padding-24 { width: 350px !important; }
  img[src="/images/w3cert.gif"] { height: 0 !important; width: 0 !important; padding-left: 350px !important; padding-top: 100px !important; background: url("http://i.imgur.com/4q7IekL.png") no-repeat !important; background-size: contain !important; }
  .tutintro > img[src="/images/w3cert.gif"] { padding-top: 150px !important; }
    /* w3 footer */
  img[src="/images/w3schoolscom_gray.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 28px !important; background: url("http://i.imgur.com/DssOxD5.png") no-repeat !important; background-size: contain !important; }
    /* lamp icon */
  img[src="/images/lamp.jpg"] { height: 0 !important; width: 0 !important; padding-left: 32px !important; padding-top: 32px !important; background: url("http://www.w3schools.com/jsref/ontesting.png") no-repeat !important; background-size: contain !important; }
    /* color picker */
  img[src="/images/colorpicker.gif"] { height: 0 !important; width: 0 !important; padding-left: 80px !important; padding-top: 68px !important; background: url("http://www.w3schools.com/images/colorpicker.png") no-repeat !important; background-size: contain !important; }
    /* javascript */
  img[src="/images/w3javascript.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/eOvpUan.png") no-repeat !important; background-size: contain !important; }
    /* css img */
  img[src="/images/w3css.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/0CJcZgv.png") no-repeat !important; background-size: contain !important; }
    /* jquery */
  img[src="/images/w3jquery.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/wLncmDl.png") no-repeat !important; background-size: contain !important; }
    /* sql */
  img[src="/images/w3sql.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/nHMvoQo.png") no-repeat !important; background-size: contain !important; }
    /* jquery mobile */
  img[src="/images/w3jquerymobile.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/aoZxUa4.png") no-repeat !important; background-size: contain !important; }
    /* svg */
  img[src="/images/w3svg.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/wUiNTwU.png") no-repeat !important; background-size: contain !important; }
    /* angular JS */
  img[src="pic_angular.jpg"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/mHBQNrQ.png") no-repeat !important; background-size: contain !important; }
    /* XML */
  img[src="/images/w3xml.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/AN74BiL.png") no-repeat !important; background-size: contain !important; }
    /* HTML */
  img[src="pic_html5.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/g3FRHYB.png") no-repeat !important; background-size: contain !important; }
    /* JSON (html) */
  img[src="/images/w3html.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/DlJS0gh.png") no-repeat !important; background-size: contain !important; }
    /* XSLT */
  img[src="/images/w3xslt.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/rCfJD1u.png") no-repeat !important; background-size: contain !important; }
    /* php */
  img[src="/images/w3php.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/nLB20tB.png") no-repeat !important; background-size: contain !important; }
    /* asp */
  img[src="/images/w3asp.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/1NdhsJD.png") no-repeat !important; background-size: contain !important; }
    /* asp.net */
  img[src="/images/w3aspnet.gif"] { height: 0 !important; width: 0 !important; padding-left: 150px !important; padding-top: 150px !important; background: url("http://i.imgur.com/1jJIy3t.png") no-repeat !important; background-size: contain !important; }
  
  /* ================= unique elements ================= */
  div[style="width:100%;border:1px solid gray;padding:8px;background:#e1efbb url('img_flwr.png') no-repeat bottom right"] { background-color: transparent !important; }
  /* Web Building demo */
  nav#nav01 > ul#menu li a { background-color: #000C47 !important; border-color: #00ACAF !important; color: #00ACAF !important; }
  nav#nav01 > ul#menu li a:hover { background-color: #00ACAF !important; color: #000C17 !important; }
  table tr { background-color: transparent !important; }
  table tr:hover { background-color: #000C47 !important; }
  
  /* all links */
  a:hover { color: #00ACAF !important; }
  
  /* language menu */
  .goog-te-menu2 { background-color: #000C17 !important; color: #00ACAF !important; }
  
  /* top nav bar */
  #topnav { background-color: #000C47 !important; }
  #topnav a.active { background-color: #000C67 !important; color: #00ACAF !important; }
  #topnav a:hover { background-color: #000C87 !important; }
  #nav_tutorials { background-color: #000C17 !important; }
  .w3-theme { background-color: #000C37 !important; }
  .w3-topnav, .top { background-color: #000C27 !important; color: #777 !important; }
  .w3-topnav a:hover { background-color: #000C47; color: #00ACAF !important; border-bottom-color: #00ACAF !important; }
  .w3-hover-white:hover { background-color: #000C67 !important; color: white !important; }
  a.active { background-color: #000C47 !important; }
  h3 + a { color: #777 !important; }
  .topnavlinks, #menubtn { color: #00ACAF !important; }
  
  hr { border-color: #000C67 !important; background-color: #000C17 !important; border-width: 5px 0px 0px !important; }
  
  /* drop nav */
  #nav_references, #nav_examples, #nav_search, #nav_translate { background-color: #000C17 !important; }
  .w3-dropnav {
    border-style: solid !important;
    border-width: 1px 0px 1px 0px !important;
    border-color: #00ACAF !important;
    background-color: #000C27 !important;
  }
  /* translate menu */
  .goog-te-gadget-simple { background-color: transparent !important; }
  .goog-te-gadget-simple a { color: #00ACAF !important; }
  .goog-te-menu2, .goog-te-menu2-item-selected span.text, .goog-te-menu2-item-selected span.indicator { background-color: #000C17 !important; color: #777 !important; }
  .goog-te-menu2-item div { background-color: transparent !important; color: #777 !important; }
  .goog-te-menu2-item:hover div {background-color: #000C47 !important; color: #00ACAF !important }
  
  /* intro info */
  .w3-info { background-color: #000C27 !important; border-left-color: #000C37 !important; }
  .w3-border-green, .w3-hover-border-green:hover { border-color: #00ACAF !important; }
  
  /* notes */
  .w3-panel { border: 1px solid #00ACAF !important; }
  .w3-note { background-color: #000C57 !important; border-color: #00ACAF !important; border-left-width: 6px !important; }
  .w3-note p { color: #00ACAF !important; }
  
  .w3-warning { background-color: #000C37 !important; }
  
  /* examples */
  .w3-code, code { background-color: #000C27 !important; }
  .w3-code {
    border-style: solid !important;
    border-color: #00ACAF !important;
    border-width: 1px 1px 1px 4px !important;
  }
  table.lampe, table.lamp {background-color: #000C47 !important; border-color: #00ACAF !important; }
  table.lampe th, table.lampe td { background-color: transparent !important; }
  table.lampe td, table.lamp td { color: #00ACAF !important; }
  table.lamp img[style="height:32px;width:32px"] { width: 27px !important; }
  
  /* syntax highlighting adjustment */
  span[style="color:mediumblue"], span[style="color:blue"] {
    color: #00ACAF !important;
  }
  .highVAL { color: #00ACAF !important; }
  span[style="color:black"] { color: #777 !important }
  
  /* side menu */
  #leftmenuinnerinner { background-color: #000C27 !important; color: #777 !important; }
  div#leftmenuinnerinner > a.active { color: #00ACAF !important; }
  #leftmenu a:hover { background-color: #000C47 !important; color: #00ACAF !important; }
  nav#sidemenu > h2, .w3-closebtn:hover { color: #00ACAF !important; }
  .w3-sidenav a:hover { background-color: #000C47 !important; color: #00ACAF !important; }
  .w3-sidenav { background-color: #000C27 !important; }
  
  .w3-hide-small, h2, h3, h1, h4 { color: #00ACAF !important; }
  
  /* buttons */
  .w3-btn, .btn {
    border-style: solid !important;
    border-width: 1px !important;
    border-color: #00ACAF !important;
  }
  .w3-btn, .btn, .seeResult { background-color: #000C47 !important; color: #00ACAF !important; }
  .w3-btn:hover, .seeResult:hover { background-color: #00ACAF !important; color: #000C47 !important; }
  button { border-color: #00ACAF !important; background-color: #000C17 !important; color: #00ACAF !important; }
  
  /* search btn BG, text */
  input#entercolor, #googleSearch, .gsc-input-box, .gsc-input { background-color: #000C17 !important; color: #00ACAF !important; }
  .gsib_a {
    padding-right: 0px !important;
    padding-left: 0px !important;
  }
  
  /* search btn BG img, border */
  #googleSearch, .gsc-input { background-color: transparent !important; }
  #gsc-i-id1 { background: transparent url("http://i.imgur.com/aH3OnYp.png") no-repeat !important; margin-left: 8px !important; background-color: transparent !important; }
  #gsc-i-id1:focus { background: none !important; }
  #gic-i-id1, div#gsc-iw-id1, .seeResult { border-color: #00ACAF !important; }
  
  /* search btn in top nav bar */
  .gsc-search-box tr:hover { background-color: transparent !important; }
  .gsc-search-button, .gsc-search-button-v2 { border-color: #00ACAF !important; background-color: transparent !important; }
  input.gsc-search-button:hover { background-color: #000C47 !important; }
  input[src="https://www.google.com/uds/css/v2/search_box_icon.png"] { height: 0 !important; width: 0 !important; padding-left: 20px !important; padding-top: 20px !important; background: url("http://i.imgur.com/DzRqSe5.png") no-repeat center !important; background-size: contain !important; }
  
  /* images */
  p.intro img { margin-top: -7px !important; }
  div.tutintro { display: block !important; }
  div.tutintro > img[style="height:132px;width:100px"] { height: 150px !important; width: 150px !important; }
  img[alt="Examples"] { height: 40px !important; width: 40px !important; }
  
  /* next / prev links */
  a.chapter {
    background-color: #000C47 !important;
    color: #00ACAF !important;
    padding: 16px !important;
    border-style: solid !important;
    border-width: 1px 1px 1px 1px !important;
    border-color: #00ACAF !important;
  }
  
  .fa-globe::before, .fa-search::before, .fa-home::before { color: #00ACAF !important; }
  
  /* info tables - browser */
  table.browserref th { background-color: #000C47 !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; padding-bottom: 3px !important; }
  table.browserref tr { background-color: #000C17 !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  table.browserref td { border: 1px solid #00ACAF !important; }
  table.browserref tr:hover { background-color: #000C47 !important; }
  
  /* browser icons */
  .bsChrome { background: transparent url("http://i.imgur.com/0yD7O03.png") no-repeat center !important; background-size: contain !important; }
  .bsEdge { background: transparent url("http://i.imgur.com/U5cg7Wu.png") no-repeat center !important; background-size: contain !important; }
  .bsIE { background: transparent url("http://i.imgur.com/gkNB1eG.png") no-repeat center !important; background-size: contain !important; }
  .bsFirefox { background: transparent url("http://i.imgur.com/nanrnCN.png") no-repeat center !important; background-size: contain !important; }
  .bsSafari { background: transparent url("http://i.imgur.com/awpdOc1.png") no-repeat center !important; background-size: contain !important; }
  .bsOpera { background: transparent url("http://i.imgur.com/PEMrFb7.png") no-repeat center !important; background-size: contain !important; }
  
  /* most tables */
  table.w3-table-all th:first-child { background-color: transparent !important; color: #00ACAF !important; }
  table.w3-table-all tr:first-child { background-color: #000C47 !important; color: #00ACAF !important; } /* top row */
  table.w3-table-all tr, table.w3-table-all th { background-color: transparent ! important; }
  table.w3-table-all tr:hover { background-color: #000C47 !important; }
  table.w3-table-all, table.w3-table-all tr, table.w3-table-all th, table.w3-table-all td { border-color: #00ACAF !important; }
  .w3-table-all > tbody > tr > td > i > a:hover { color: #00ACAF !important; }
  
  /* tech spec table */
  table.tecspec > tbody > tr > th { color: #00ACAF !important; }
  table.tecspec > tbody > tr > th, table.tecspec > tbody > tr > td { border-top: 1px solid #000C47 !important; }
  table.tecspec > tbody > tr:hover { background-color: #000C47 !important; }
  table.tecspec { border-color: #000C47 !important }
  
  /* Try It Windows */
  /* iframe#iframeResult > html > body { background-color: none !important; } */
  div#tryitLeaderboard { background-color: #000C17 !important; color: #00ACAF !important; } /* */
  textarea#textareaCode, textarea.code_input { background-color: #000C17 !important; color: #00ACAF !important; border-width: 20px 1px 1px 1px !important; border-style: solid !important; border-color: #000C37 !important; }
  .footerText a { color: #00ACAF !important; text-decoration: none !important; }
  .footerText a:hover { text-decoration: underline !important; }
  /* try it scissors */
  .tryit_ex img { width: 30px !important; height: 30px !important; margin-top: 14px !important; }
  /* play it */
  #container { box-shadow: none !important; background-color: #000C37 !important; }
  .playit, #demoDIV, #styleDIV { background-color: transparent !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  #enlargecssprop { color: #000CC7 !important; }
  
  #topTD, #topTD2 { background-color: #000C17 !important; }
  #codeTD, #codeTD2 { background-color: #000C37 !important; }
  
  #exercisemenu { background-color: #000C47 !important; border-right: 1px solid #555 !important; }
  #exercisemenu > div:first-child { background-color: #000C47 !important; border-right: 1px solid #555 !important; }
  #exercisemenu div:nth-child(2) { background-color: #000C47 !important; }
  .exercisemenuinner { background-color: #000C17 !important; }
  .menubtn { color: #00ACAF !important; }
  .exercisemenu a.exbtn { color: white !important; background-color: #000C27 !important; }
  .exercisemenu a.exbtn:hover { background-color: #000C47 !important; border-bottom-color: #00ACAF !important; }
  div.exmenulinks a.active { background-color: #000C67 !important; border-bottom-color: #00ACAF !important; }
  .exmenuitemheader { background-color: #000C17 !important; border-top-color: #00ACAF !important; }
  .exmenuitemheader:hover { color: #00ACAF !important; }
  
  /* search window results */
  .gsc-modal-background-image { background-color: #000 !important; } /* opaque bg */
  .gsc-results-wrapper-overlay { border: 1px solid #00ACAF !important; }
  .gsc-results-wrapper-overlay, table.gsc-resultsHeader + div.gsc-webResult { background-color: #000C17 !important; } /* background color */
  .gsc-result-info, td.gcsc-branding-text { color: #00ACAF !important; } /* header text */
  .gsc-above-wrapper-area { border-bottom: 1px solid #00ACAF !important; } /* border separator */
  .gcsc-branding { background-color: #000C17 !important; } /* branding */
  .gsc-webResult { background-color: #000C17 !important; color: #00ACAF !important; } /* results bg */
  .gsc-webResult:hover { background-color: #000C47 !important; } /* results bg hover */
  a.gs-title, a.gs-title b { color: #00ACAF !important; } /* result header font color */
  a.gs-title:hover { background-color: transparent !important; } /* result header highlight bg fix */
  .gs-snippet { color: #777 !important; } /* result snippit */
  /* result page numbers */
  .gsc-cursor-page { background-color: transparent !important; margin: 5px !important; color: #00ACAF !important; }
  .gsc-cursor-page:hover { background-color: #00ACAF !important; color: #000C17 !important; }
  .gsc-cursor-current-page { background-color: #00ACAF !important; color: #000C17 !important; }
  /* search result borders */
  .gsc-webResult { border-color: #00ACAF !important; } 
  /* bg opaque */
  .gsc-modal-background-image-visible { opacity: 0.2 !important; }
  
  /* bottom graphic w3 logo
  img[src="/images/w3schoolscom_gray.gif"] { z-index: -200 !important; }
  div.footer > a { background-image: url("http://www.w3schools.com/images/w3schools.png") !important; background-size: contain !important; } */
}
