css:
/* Removes youtube features unrelated to browsing videos.
   Use/Abuse/Distribute at will
*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {

/* Colors the masthead grey */
div#yt-masthead-container{background-color:#f4f4f4;}

/* Notification bell */
div.notifications-container{display:none;}

/* Upload button */
a#upload-btn{display:none;}

/* Account picture */
span#yt-masthead-account-picker{display:none;}

/* Alert or message box */
div.alerts-wrapper{display:none;}

/* Colors guide box grey */
div#guide-container{background: #f2f2f2; }

/* Colors the page grey */
div#page{background-color:#f2f2f2;}

/* Removes outline of description and title boxes */ 
div#watch-header{background-color:#f2f2f2;}
div#action-panel-details{background-color:#f2f2f2;}
div#watch-header.yt-card.yt-card-has-padding{display:inline;}
div#action-panel-details.action-panel-content.yt-uix-expander.yt-uix-expander-collapsed.yt-card.yt-card-has-padding{display:inline;} 

/* Uploader picture thumbnail */
span.yt-thumb-square{display:none; pointer-events:none;}

/* Subscription button */ 
span#watch7-subscription-container{display:none;}

/* View info */
div#watch8-sentiment-actions{visibility:hidden;}

/* Comment section */
div#watch-discussion{display:none;}

/* Removes box around video recommendations */
div#watch7-sidebar-contents{display:inline; background-color:#f2f2f2;}
h4.watch-sidebar-head{visibility:hidden;}

/* View count on related videos */
span.stat.view-count{display:none;}

/* Autoplay label */
label{display:none;}
span.autoplay-hovercard.yt-uix-hovercard{display:none;}

/* Page footer */
div#footer-container{display:none;}

/* Style for home page selection buttons */
div#appbar-content{display:inline; background-color:#f2f2f2;}

/* Style for home page feed */
div#feed{background-color:#f2f2f2;}

/* Video title background color fix */
a.yt-uix-sessionlink.yt-ui-ellipsis.yt-ui-ellipsis-2.spf-link{background-color:#f2f2f2;}

/* Style for search page column */
div.yt-card.clearfix{display:inline;}
div.yt-lockup-description.yt-ui-ellipsis.yt-ui-ellipsis-2{background-color:#f2f2f2;}
}
