css:
/* Ver 2020-08-03 */

@-moz-document domain('news.yahoo.co.jp'),
domain('headlines.yahoo.co.jp') {

    :root {
        --size-pc-width-sideColumn: none !important;
        --size-pc-width-content: none !important;
        --size-width-content-pc: none !important;
        --size-width-mainColumn-pc:none !important;
    }

    div#main {
        width: 100% !important;
    }
    div#yjnMain
    {
        /*flex: unset !important;*/
        /*width: 100% !important;*/
    }

    div#wrapper,
    div#header,
    div#contentsHeader,
    div#contentsBody,
    div#contentsFooter {
        min-width: unset !important;
        width: unset !important;
    }
    p.text {
        font-size: 1.1em !important;
        font-family: sans-serif !important;
    }
    #yjnSub,
    #sub,
    .SubMod,
    SubRanking,
    span#comment-count-00,
    p.bnr,
    p.ynCobrandBanner,
    nav.liveStream_list,
    section#liveStream,
    div#mhd_prem_header_sp,
    div#msthdPointArea,
    /* 画面最下部の広告ドロワー */
    div#overlayAd,
    div#yjSLink,
    div#loading
    {
        display: none !important;
        /* visibility: hidden !important; */
        width: 0 !important;
        height: 0 !important;
    }
    div#lrec {
        display: none !important;
        /* visibility: hidden !important; */
    }

    /* 画面最上部の広告ドロワー */
    div[style^="position: fixed;width: 100%;"],
    div[style^="position: relative; height: 180px; transition: height 500ms ease 0s"],
    div#posENM,
    div.headerMat,
    div.mediaHeader,
    div[id^="comment-"]
    {
        display: none !important;
        background:none !important;
        height:0px !important;
    }

    /* 記事下の余計なメニューを削除 */

    div#closeModules,
    section#comModule,
    section.mediaDetail,
    sectionsection.subListMod,
    div#taboola-below-article-thumbnails-p4,
    div#taboola-below-article-thumbnails-p4-sc,
    div.trc_rbox_container,
    div.posMMON,
    div.adMod,
    div.bnrMod,
    div#themeSticker,
    div.cfNavColumn,
    div.adUltraBody,
    div.adUltraTitle,
    div.themeList,
    div#msthdtp,
    div.infoMsg,
    section.infoEmg,
    div#sub,
    div.provInfo,
    div.promotionMod,
    ul.relCate,
    div.ynProviderInfo,
    div.ephd,
    div[id^="ad"],
    ul#timeline,
    section.listPaneltype,
    dt#yjSLinkTtl,
    ul#yjSLinkLst,
    div#peron,
    div.footerNavigation_external,
    div.fbComment,
    aside.articleTimeLine,
    aside#newsFeed,
    section#uamods-also_read,
    aside[class^="sc-"],
    div[class^="sc-kr"],     /* 画面左外のユーザコメントポップアップ */
    div[class^="sc-hXRMBi eDi"], /* 記事右下の評価ボタン */
    div[class^="sc-jt"],
    div[id^="yjSlink"],
    div.middleNotice,
    div.adWrap,
    div[class^="sc-iu"],
    div[id^="inf_"],
    aside[class^="sc-Ju"],
    section[class^="sc-b"],
    footer[id^="yjn"]
    {
        display: none !important;
        /* visibility: hidden !important; */
        height: 0 !important;
    }
    /* ソーシャルメディア削除 */

    body#device_spn,
    div.snsButton,
    div.ynCtsSelectNav,
    div#commentTabContents,
    div.socialMod,
    div.bottomLogoWrap {
        display: none !important;
        visibility: hidden !important;
        height: 0 !important;
    }
    /* Java無効警告表示削除 */

    div.alertBox {
        visibility: hidden !important;
        height: 0 !important;
    }
    /* 記事リンク 左右サムネイル写真削除 */

    div.thum,
    div.thumb,
    div.thumbnail,
    span.thumb,
    div.imageRight,
    div.imageLeft,
    div.PhotosContainerR.FixedPhotCon,
    div.PhotosContainerL.FixedPhotCon,
    div.newsFeed_item_thumbnail,
    p.topics_photo,
    span.thumbnail,
    /* 記事中の写真 */
    div.articleMainImage,
    div[class^="sc-cp"],
    div.paragraphPhoto
    {
        display: none !important;
        /* visibility: hidden !important; */
        width: 0 !important;
        height: 0 !important;
    }
    div.topics_item_thumbnail::before
    {
    }
    div.topics_item_thumbnail span
    {
        background-image: unset !important;
    }
    dl.mc {
        width: 100% !important;
    }
    /* 記事本文フォントをブラウザ標準に（環境により太文字化するのを阻止） */

    article.articleDefault {
        font-size: 0.9em !important;
        font-family: sans-serif !important;
    }
    /* iframe無効 */

    iframe {
        display: none !important;
        visibility: hidden !important;
        height: 0 !important;
    }
    div#contentsFooter,
    div#closeModules,
    section.subListMod,
    /**
    ul.listModBox,
    li.listModBoxWrap,
    li.listModBoxWrapLarge,
    **/
    div.listMod {
        visibility: hidden !important;
        height: 0 !important;
    }

    /* 宣伝リンクの高さをゼロにしてページ長さを適正化 */
    div.listMod ul.listModBox,
    div.listMod li.listModBoxWrap,
    div[id^="yads"]
     {
        height: 0 !important;
    }



    /* 宣伝リンク削除 */

    a[href*="https://ard.yahoo"],
    a[href*="purchase/promo"] {
        visibility: hidden !important;
        width: 0 !important;
        height: 0 !important;
    }

}
