css:
@-moz-document domain("ing-diba.de") {

body{
  background:black;
  color:white;
}
hr.gap-bottom-1{
  background:grey;
}
h1, h2, h3, h4, h5, h6, .heading{
  color:#0040FF;
}
a{
  color:white;
}

a.link-primary, a.footer-group__title{
  color: #0040ff;
}

div.grid-w6{
  color:black;
}

form.header-quicksearch{
  background:black;
}

header.header{
  background: black;
}

.nav.nav--ibbr{
  margin-top: 5px;
  border-color:grey;
  border-radius: 10px;
}

nav.nav--ibbr{
  border-color: grey;
  background:#2E2E2E;
}

nav.nav{
  margin-bottom: 5px;
  background:#2e2e2e;
