  width: 960px;
  padding-top: 5px;
  padding-right:12px;
}

input.input-field{
  color:black;
}

textarea.textarea{
  color:black;
}


.diba-keypad__display{
  background: #848484;
}

.function-panel{
  background: #2e2e2e;
  border: 1px solid grey;
}

[data-session-timer]{
  color:black;
}

.link-function-panel{
  color:#0040ff;
}

span.account-row__type{
  color:#0040ff;
}
select.select{
  color:black;
}
