css:
@-moz-document url("http://www.estel-fm.ru/chat/") {
body{background:#000}
footer,nav,img,#btnNewOrder,#btnToForum,button[data-page="archive"]{display:none}

header{margin-top:0px}
.content{width:100%;}
.header_wrap,.header_wrap_middle,#lblStatus{
    background:#333;
    color:#fff;
}
label,#btnPlayer{
    border:none;
    background:#fff;
    color:#000;
    border:0px;
}
.chanels input:checked + label{
	background:#DDD;
    color: #333;
    border: none;
}

.status{
	background:#333;
     width:100%;
}

.header_wrap{
	border:0px;
    border-radius:0px;
    width:100%;
    height: 55px;
}
.volume {
    top: 7px;
    right: 5px;
    position: absolute;
}
.knob,.knob *,.knob .top{
    width: 40px;
    overflow: hidden;
    height: 40px;
}

.knob .top {
    background-size: contain;
    background:white;
    border-radius:100%;
}

.knob .top:after{
    content: '';
    width: 5px;
    height: 5px;
    background-color: #333;
    position: absolute;
    top: 50%;
    left: 5px;
    margin-top: -5px;
    border-radius: 50%;
    cursor: pointer !important;
    box-shadow: none;
}

.status span{
	color:#fff;
    top:-20px;
}
#lblVolume{
	top:-22px;
    right:60px;
    background: #333;
    color:#fff;
    left: auto;
}
.status .stat {
    left: 150px;
    position: absolute;
    top: -20px;
    overflow: hidden;
    width: auto;
    right: 125px;
}


#btnPlayer {
    top: 7px;
    left: 5px;
    position: absolute;
    width: 65px;
    height: 40px;
}

label[for="rDance"], label[for="rLife"]{
	top: 7px;
    left: 75px;
    position: absolute;
    width: 50px;
    height: 17px;
    line-height: 17px;
    border: 0px;
    padding: 0px;
    text-align: center;
    font-size: 10px;
}
label[for="rLife"]{
    top: 30px;
}

.header_wrap{
	opacity:1;
}

#content {
    margin-top: 0px;
    border: 0px;
    border-radius: 0px;
    width: 100%;
    min-height: 0px;
    background: #000;
    position: absolute;
    padding: 10px;
    box-sizing: border-box;
    top: 53px;
    bottom: 0px;
    padding: 0px;
}

iframe{    height: 100% !important;}
}

@-moz-document url("http://chatadelic.net/frame.php?chat=42231") {
#chatRoot,.chatTab .body,#topPanel .bg,.chatFooter{
    xborder:0px none #000 !important;
    border-color:#000 !important;
}
.chatSendButton{
	display:none;
}
.chatStatusPanel {
    position: absolute;
    top: 4px !important;
    right: 135px !important;
}

#exitButton {
    top: 3px;
    right: 220px;
}

input#chatInputMessageText {
    position: fixed;
    width: 100%;
    left: 0%;
    height: 35px;
    line-height: 1em;
    padding: 0px 10px;
    box-sizing: border-box;
    bottom: 0px;
}
}
