css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("picarto.tv") {
  
  .msgContentIdentifier
  {
    font-size: 14px;
  }
  
  .msgContent
  {
    line-height: 16px;
  }
  
  .globalli, li .update, .messageli, .mymessageli
  {
    margin-top: 0;
    margin-bottom: 2px;
    margin-right: 16px;
    margin-left: 2px;
    padding: 0;
    border-radius: 0;
  }
  
  .infoContent, .my_infoContent
  {
    border-radius: 0;
    display:block;
    max-width: 100%;
  }
  
  .msgUsername, .msgAdminUsername
  {
    font-weight: bold;
  }
  
  .infoContent, .my_infoContent
  {
    border-bottom: none;
  }
  
  .my_infoContent
  {
    background-color: #404040;
  }
  
  .my_infoContent .theSndMsg
  {
    border-top: 1px dashed #555555;
  }
  
  .chat_usr_ava, .my_chat_usr_ava
  {
    display: none;
  }
  
  .stricon:before, .modicon:before
  {
    font-size: 11px;
    line-height: 14px;
    border-radius: 14px;
    background-color: #F00;
    text-align: center;    
    display: inline-block;
    width: 14px;
    height: 14px;
  }
  
  .modicon:before
  {
    background-color: #66F;
    content: "M";
  }
  
  .stricon:before
  {
    background-color: #F00;
    content: "S";
    text-indent: -2px;
  }
  
  .premicon:before
  {
    font-size:30px;
    vertical-align: bottom;
    line-height: 12px;
    color: #FF0;
    border-radius: 16px;
    padding: 2px;
    content: "*";
  }
  
  .stricon img, .premicon img, .modicon img
  {
    display:none;
  }
}
