css:
/* Version 1.04 */
@namespace url(http://www.w3.org/1999/xhtml);
/* 
MyIntracomm URLs that do not contain the words 
  /dg/agri
  /dg/ecfin
  ...
Test regular expression at 
https://regex101.com/r/zN6gZ5/1
*/
@-moz-document regexp('^https:\/\/myintracomm.ec.europa.eu(?!.*(\/dg\/(agri|ecfin|ECFIN|grow))).*$')
{  
  body h1, body h2, body h3, body h4, body h5
  {
    font-family: Times New Roman !important;

/*  1.01: don't touch font-size
    font-size: x-large !important;
*/
  }
/* 
1.02: EC LOGO grayed 
*/
  .banner img
  , img  
  {
   -webkit-filter: grayscale(100%) !important;
   filter: grayscale(100%) !important;
  }
/* MAIN BACKGROUND COLOUR */  
  
  body
  ,
  body #s4-bodyContainer
  ,
  .wrapper
  ,
  .wp-content
  {
    background-color: Gainsboro  !important;  /* Kind of gray */
  }
/* TOP and BOTTOM*/
  .cd-primary-nav /* TOP when full screen */
  ,
  .cd-main-header .cd-main-header-inner-wrapper /* TOP when width reduced */
  ,
  body .colophon .container .row.blue-bottom
  {
    background-color: black   !important; /* DarkSlateBlue dark violet */
  }
  
/* BANNER */  
  .banner  /* TOP banner, i.e. 2nd row */
  {
    background-color: Gray  !important;
    /* chocolate LightSalmon Peru */
  }
  
/* BACKGROUND of FRAMES */  
  .wp-colored-background
  ,
  .wp-white-background
  ,
  .disclaimer
  ,
  body #s4-workspace #s4-bodyContainer .central-wrapper .staff-matters_footer-wrapper
  {
    background-color: LightGray !important;
  }

  body .colophon .container      /* BOTTOM mega menu */
  ,
  .event .event-day              /* Calendar events */  
  {
    background-color: Gray  !important;
  }
  .cd-primary-nav .cd-secondary-nav
  ,
  .cd-primary-nav.nav-is-visible
  {
    background-color: Silver  !important;
  }
  .cd-primary-nav.nav-is-visible .has-children > a    /*  Menu links after hamburger pressed, i.e. vertical menu */
  {
    color: Black !important;
  }
  body .ec-pagination
  {
    background-color: BurlyWood  !important;
  }
  
  body a
  ,
  body .content-page-layout h1
  {
    color: Black !important;
  }
  
  body a:visited
  ,
  .wp-details
  ,
  .wp-details span  
   {
    color: DimGray  !important;  
   }
  .banner h1 a, .banner h1 a:visited, .banner h1 a:hover
    {
      color:white !important;
    }
/* 
  BUTTONS 
1.03: grayed buttons
1.04: grayed more buttons  
*/
  .btn
  ,
  body .ec-more-btn
  {
   -webkit-filter: grayscale(100%) !important;
   filter: grayscale(100%) !important;
   
  }

}
