css:
@-moz-document domain("iichan.hk") {
/* Реколор Futaba */
html, body, .news, .category, .reply, .header, .postblock, .passvalid, .theader, .managehead, .row1, .row2, .highlight, .nothumb {
	background-color: hsla(0, 0%, 0%, 0);
}

hr, .highlight, .nothumb {
	border-color: hsl(0, 0%, 0%);
}

table, tr, td {
	border-color: hsl(0, 0%, 0%) !important;
}

html, body, a, a:visited, hr, form > div label, .prohibition, .reflink, .filesize, .filetitle, .replytitle, .postername, .commentpostername, .adminname, .postertrip, .abbrev, .omittedposts, .unkfunc {
	color: hsl(0, 0%, 0%) !important;
}

a:hover {
	color: hsl(0, 0%, 100%) !important;
}

.unkfunc {
	color: hsl(0, 0%, 25%) !important;
}

* {
	text-shadow: 0px 0px 3px;
}

.banner, .thumb, .greeting > img, .catthread > img {
	box-shadow: 0px 0px 4px;
	filter: grayscale(100%);
}

#postform img {
	filter: grayscale(100%);
}

html, body {
	background: fixed url(http://i.imgur.com/XnInvXd.jpg);
	background-size: cover;
}

.news, .category, .header, .passvalid, .theader,
form td.reply, .hightlight, .adminbar {
	background: fixed url(http://i.imgur.com/Va5gE4X.jpg) !important;
	background-size: cover !important;
}

form td.reply, .hightlight {
	border-radius: 3px;
	box-shadow: 0px 0px 8px hsla(0, 0%, 0%, 0.5);
}

.spoiler, .spoiler:hover {
	background-color: hsla(0, 0%, 0%, 0);
	filter: invert(100%);
}

::-moz-selection {
	background-color: hsla(0, 0%, 0%, 0.2);
	color: hsl(0, 0%, 100%) !important;
}


/* Формы */
textarea, input[type='text'], input[type='password'] {
	background-color: hsla(0, 0%, 100%, 0.2);
	border: 1px solid hsla(0, 0%, 50%, 0);
	border-radius: 3px;
	box-shadow: 0px 0px 4px hsla(0, 0%, 50%, 1);
	color: inherit;
}

button, input[type='button'], input[type='submit'], input[type='reset'], input[type='checkbox'], input[type='radio'], input[type='file'], select {
	box-shadow: 0px 0px 4px hsla(0, 0%, 50%, 1);
	filter: grayscale(100%) contrast(80%);
	margin: 1px;
}


/* Dollchan Extension Tools */
::before, .de-img-pre, .de-video-player, .de-vocaroo, .de-post-btns {
	filter: grayscale(100%);
}

#de-panel {
	background-color: hsla(0, 0%, 25%, 0.5) !important;
	filter: grayscale(100%);
}

[class*='de-win'] .de-win-body, #de-popup .reply, .de-imgmenu {
	background: fixed url(http://i.imgur.com/Va5gE4X.jpg) !important;
	background-size: cover !important;
}

.de-pview {
	background: fixed url(http://i.imgur.com/Va5gE4X.jpg) !important;
	background-size: cover !important;
}

.de-win-head {
	background-color: hsla(0, 0%, 50%, 0.9) !important;
	box-shadow: 0px 0px 2px hsl(0, 0%, 50%) !important;
	border-radius: 3px 3px 0px 0px;
}

.de-fav-inf-old, .de-fav-inf-new, .de-post-btns > span, .de-post-counter:after {
	color: hsl(0, 0%, 0%) !important;
}

.de-imgmenu .de-menu-item:hover {
	background-color: hsl(0, 0%, 0%) !important;
}

.de-new-post {
	box-shadow: 1px 1px 2px hsl(0, 0%, 0%);
}

.de-post-removed .reply, .de-post-removed .highlight {
	filter: opacity(50%);
}

.de-selected {
	background-color: hsl(0, 0%, 0%);
	box-shadow: none;
}


/* Главная */
.list a:hover {
	background: fixed url(http://i.imgur.com/Va5gE4X.jpg) !important;
	background-size: cover !important;
	color: hsl(0, 0%, 0%) !important;
	filter: invert(100%);
}


/* Фиксы */
.logo, .logo a, .sublogo, .sublogo a, .greeting, .news, .header, .list div a, .postblock, .reply, .highlight, .thumbnailmsg, .spoiler:hover, .managehead, .row1, .row2 {
	color: inherit;
}

.list {
	border: none;
}

.spoiler {
	color: hsla(0, 0%, 0%, 0.0);
}
}
