@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

#searchForm div.form {
    background:transparent url(data:image/gif;base64,) no-repeat !important;
}

#searchForm input.ibutton {
    background:transparent url(data:image/gif;base64,) no-repeat 100% 0 !important;
}

#searchForm ul.select li a {
    background:transparent url(data:image/gif;base64,) no-repeat scroll !important;
    color:#2A3737!important;
}

#searchForm input.itext {
    background:transparent url(data:image/gif;base64,R0lGODlhrgAWAKIAAEBTSkBSSXuNf2t9b4KVhgAAAAAAAAAAACH5BAAAAAAALAAAAACuABYAAAODCLrc/jDKSau9OOs1uv9gKI5kaZ5oqq5s6wlwLM90bd94ru987//AGGFILBqPyKRyyWw6n9CodEqtWq/YrHbL7Xq/4LB4TC6bz+i0es1uu9/wuHxOr9vv+Lx+z+/7/4CBgoOEhYaHiImKi4yNjo90AZKTlJWWl5iZmpucnZ6foKGikwkAOw==) no-repeat scroll !important;
}

#searchForm input.empty:focus {
    background:transparent url(data:image/gif;base64,R0lGODlhrgAWAKIAAEBTSkBSSXuNf2t9b4KVhgAAAAAAAAAAACH5BAAAAAAALAAAAACuABYAAAODCLrc/jDKSau9OOs1uv9gKI5kaZ5oqq5s6wlwLM90bd94ru987//AGGFILBqPyKRyyWw6n9CodEqtWq/YrHbL7Xq/4LB4TC6bz+i0es1uu9/wuHxOr9vv+Lx+z+/7/4CBgoOEhYaHiImKi4yNjo90AZKTlJWWl5iZmpucnZ6foKGikwkAOw==) no-repeat !important;
}

#searchForm input.empty {
    background:transparent url(data:image/gif;base64,)  no-repeat !important;
width:333px!important;
}

#searchForm ul.select li a:hover {
    background-position:0 -60px !important;
}

#searchForm input.ibutton:hover {
    background-position:100% -60px !important;
}


#searchForm ul.select li a:active {
    background-position:0 -30px !important;
	background-repeat:no-repeat!important;
}

#searchForm input.ibutton:active {
    background-position:100% -30px !important;
	background-repeat:no-repeat!important;
}

div.popup2-search ul {
    margin:0 !important;
    padding:0 !important;
    background:#829586 !important;
    border:1px solid #343E3C !important;
    font-size:10pt !important;
margin-top:-1px!important;
margin-right:-1px!important;
}

div.popup2-search li {
    list-style:none !important;
    display:block !important;
}
div.popup2-search a {
    padding:3px 8px !important;
    display:block !important;
    color:#2A3737 !important;
    text-decoration:none !important;
}

div.popup2-search a:hover,
div.popup2-search a:focus {
    color:#DCE7DC !important;
    background:#40534A !important;
    text-decoration:none !important;
}

/* ----------- On the right ----------- */
#searchForm {
float:right!important;
margin-top:7px!important;
}

#moremenu {
margin-left:170px!important;
width:auto!important;

}

#moremenu-h {
padding-right:10px !important;
margin-right:20px!important;
}
}
