css:
@namespace url(http://www.w3.org/1999/xhtml);
body,html {
	background-color: #000000;
	color:white;
	font-size:14px;
}
DIV[style="background-color:white"] {
	background-color: #000000 !important;
}

a {
	color:#AAAAAA;
	text-decoration:none;
}
a:visited {
	color:#999999;
	text-decoration:none;
}
a:hover {
	text-decoration:underline;
}
/* but prevent stupid non-closing <a> spanning whole pre*/
a[name="alku"] {
	color:white;
	text-decoration:none;
}
a[name="alku"]:hover {
	color:white;
	text-decoration:none;
}
/* some color substitutions to match teletxt */
font[color="#000000"] {
	color:white;
}
font[color="#0080FF"] {
	color:#04FEFC;
}
font[color="#008000"] {
	color:#04FE04;
}
font[color="#FF0080"] {
	color:#FCFE04;
}
font[color="#0000FF"] {
	color:#0402FF;
}
/* "beef" - preformatted area */
pre a {
	color:inherit;
	text-decoration:none;
}
pre a:visited {
	color:inherit;
	text-decoration:none;
}
pre a:hover {
	text-decoration:underline;
}
pre {
	font-family: "Lucida Console" ,monospace;
	font-size:16px;
	font-weight:500;
    background-color: #000000;
}
pre big {
	font-family: "Lucida Console" ,monospace;
	font-size:17px;
	letter-spacing: -1px;
	font-weight:600;
}
