css:
@-moz-document domain("fleep.io") {
body > div,
div.view_conversation-header,
div.menu,
div.chat_main_area,
div.bmOwQa,
div.conversations .conversation.selected,
div.conversations .conversation.selected .label,
div.view_conversation-list-header,
input.edit-field,
div.conv_list_rows_wrapper,
div.conv_list_row,
div.conv_list_row,
div.conv_list_archive_wrapper,
div.conv_list_scroller,
div.conv_list_scroller .row,
div.team-management,
div[data-reactid*='conversation_list'],
div.right_pane_wrapper,
div.items > div,
div.chat_main_area > div > div > div > div input {
    background-color: #2F343A !important;
    border: 0;
}

div.conversations .conversation.selected .label:after {
    background-image: none;
}
div.entry-buttons-area {
    background-color: #2F343A !important;
    border-radius: 5px 5px 5px 5px !important;
    border: solid 1px #000 !important;
}
div.popup-menu a[download],
textarea,
div.chat_main_area > div > div > div > div > div > div div,
div.chat_main_area > div > div > div > div {
    background-color: #2F343A;
}
div.popup-menu a[download] .text,
span.label {
    color: #ABC2CD !important;
}
div.name,
div.title,
span.title,
span.jQhHGA,
div.view_conversation-header .text,
div.view_conversation-list-header span,
div.flow-message span {
    color: #FFFFFF !important;
}
div[title] > div {
    background-color: #3B4149 !important;
    color: #FFFFFF !important;
}
div.url-preview,
div.conversation-right-pane .menu .icon {
    background-color: #1a1d21 !important;
    border-radius: 10px 10px 10px 10px !important;
    border: solid 1px #000 !important;
    padding: 10px;
}
div.menu .item.active {
    background-color: #1a1d21 !important;
    border-radius: 10px 10px 10px 10px !important;
    border: solid 1px #000 !important;
    padding: 10px;
}
div.conversation div.chat_area_wrapper .contact-name,
div.conv_list_scroller div.conv_list_topic,
div.conv_list_scroller .row .topic,
header{
    color: #F2FFF0 !important;
}
div.conversation .content_area p,
div.conversation .content_area msg,
input.edit-field,
div.message-content {
    color: #ABC2CD !important;
}
div.conversation .pinboard .contact-name,
div.conversation #taskboard-container .contact-name,
div.conversation .pin span.contact-name,
div.conversation .pin p {
    color: #000000 !important;
}
div.conversation pre,
.task,
.message_entry,
.content_area .central_message,
.tb-container,
input[type="text"],
div.chat_main_area > div > div {
    background-color: #3B4149 !important;
    border-color: #1a1d21 !important;
    color: #ABC2CD !important;
}
div.conversation .label,
div.conversation .line {
    background-color: #3B4149 !important;
    color: #ABC2CD !important;
}
div.conversation .line {
    background-color: #3B4149 !important;
}
.flow .items .flow-header.editing,
.flow .items .flow-header.menu-open,
.flow .items .flow-message.editing,
.flow .items .flow-message.editing-in-entry,
.flow .items .flow-message.menu-open {
    background-color: #1F2227 !important;
}
}
