css:
@-moz-document domain("archivesofnethys.com") {
/* fix border and random bar */

body.light #page,
body.dark #page,
body.dark #main-wrapper,
body.light #main-wrapper {
	border: none;
	background: none;
}


/* light theme */

body.light,
body.light .title h1 {
	color: #000;
	background: #ddd;
}
body.light h2.title,
body.light h1.title {
	color: #ddd;
	background: #111;
}
body.light .external-link {
	color: #000;
	text-decoration: none;
	font-style: normal;
}
body.light .external-link:hover {
	color: #000;
	text-decoration: underline;
	font-style: normal;
}
body.light table.inner tr:nth-child(even) {
	background: #CCC;
}
.light tr {
	background: #ddd;
}


/* dark theme */

body.dark,
body.dark .title h1 {
	color: #ddd;
	background: #000;
}
body.dark h2.title,
body.dark h1.title {
	color: #000;
	background: #ccc;
}
body.dark .external-link {
	color: #ddd;
	text-decoration: none;
	font-style: normal;
}
body.dark .external-link:hover {
	color: #ddd;
	text-decoration: underline;
	font-style: normal;
}
body.dark table.inner tr:nth-child(even) {
	background: #111;
}
.dark tr {
	background: #000;
}


/* larger body text */

body {
	font-size: 16px;
}
#main {
	line-height: 22px;
}
i,
cite,
em,
var,
address,
dfn {
	font-style: normal;
}


/* good looking tables */

body.light table.inner,
body.light .inner td,
body.dark table.inner,
body.dark .inner td {
	border: none;
	font-size: 14px;
	text-align: left;
}
body.light .inner td,
body.dark .inner td,
th {
	padding: 5px 5px 0px 5px;
}
table, body, tr, td {
	border: none !important;
}


/* Better Headers */

h1.title,
h2.title {
	padding: 6px 10px 7px 10px;
	margin: 10px -10px 10px -10px;
	letter-spacing: 1.5px;
	font-weight: bold;
}
h1.title {
	font-size: 24px;
}
h1,
h2 {
	font-variant: normal;
	margin-top: 10px;
}
h2 {
	font-size: 16px;
	letter-spacing: 1px;
	letter-spacing: .5px;
	font-weight: normal;
}
hr {
	border: none;
	margin: 0px;
}
h3.framing {
	border: none;
}


/* Sidebar & Padding */
#main-menu {
	padding: 192px 20px 0 58px;
	background-position: -20px -10px;
}
#main-wrapper {
	margin-left: 170px;
	padding-left: 217px;
	padding-right: 10px;
	padding-bottom: 40px;
	min-height: 779px;
}
#wrapper {
	width: 100%;
}
}
