    color: black !important;
  }

  .cke_top, .cke_reset_all, .cke_top>*>*>*>*, .cke_reset_all>*>*>*>*, .cke_wysiwyg_frame, .cke_reset {
    background-color: gray !important;
    box-shadow: none !important;
  }

  .tipBox {
    background-color: darkblue !important;
  }

  .vocabList_head {
    background-color: #661a33 !important;
  }

  .color_FF02D9 {
    color: #b30098 !important;
  }

