  }

  .featureBox {
    background-color: #4d4d4d !important;
    border-left-color: #404040 !important;
  }

  .theme_clean blockquote {
    background-color: #262626 !important;
  }

  .question {
    background-color: #13532e !important;
  }

  .answer {
    background-color: #094d00 !important;
  }

  .selfcheck:hover {
    border-color: #00660a !important;
  }

  .thinkabout_head {
    background-color: #653d01 !important;
  }

  .thinkabout_questions {
    background-color: #492e04 !important;
  }

  .thinkNclick {
    background-color: #4c0080 !important;
    border-color: #5b0099 !important;
  }

  .vocabDefine, .vocabSection div.vocabList_head dt:hover {
    background-color: #4d000b !important;
  }

  .vocabSection:hover {
    border: none !important;
  }

  .cncSection {
    background-color: #906709 !important;
    color: darkgray !important;
  }

  .toc {
    background-color: #166483 !important;
  }

  .toc ul li:hover {
    background-color: #0f4357 !important;
  }

  .excerptSection {
    border: 1px solid black !important;
    background-color: rgb(29, 29, 29) !important;
    box-shadow: 4px 4px 0 rgb(29, 29, 29), 5px 5px 0 rgb(29, 29, 29), 9px 9px 0 rgb(29, 29, 29), 10px 10px 0 rgb(29, 29, 29) !important;
  }

  .excerptSection > * > * {
    color: gray !important;
  }

  .checkList {
    background-color: #2c5154 !important;
  }

  .label {
    background-color: #0a2e52 !important;
    border-left: #101936 10px solid !important;
    border-right: #101936 10px solid !important;
  }

  .label.important {
    background-color: #68091c !important;
    border-left: #420000 10px solid !important;
    border-right: #420000 10px solid !important;
  }

  .label.new {
    background-color: #2f3e0f !important;
    border-left: #001f00 10px solid !important;
    border-right: #001f00 10px solid !important;
  }

  .dropdown-menu>li>a {
    color: gray !important;
  }

  .dropdown-menu>li>a:hover {
    background-color: #333333 !important;
  }

  .optionContent>img, .itemImageContainer>img, #assessmentBody img, .assessmentItemContainer img, embed, *[for*="quiz"], .questionHeader img {
    -webkit-filter: invert(100%) hue-rotate(180deg) !important;
  }

  .ui-widget-content {
    background: none !important;
  }

  .alert-info {
    color: gray !important;
    background-color: #123e54 !important;
  }

  .list-group-item.disabled {
