css:
@-moz-document domain("www.7cups.com") {
.navbar-fixed-top {
    background-color: #173665;
    background-image: none;
}
body {
    background-color: #0D2433;
}
navbar, .navbar a {
    color: #D6FF00;
}
.navbar .specialnav {
    border: .15rem solid #198EA0;
}
.chatBox .chatInput #Comment {
    background-color: #274B80;
    border: 0 solid #274B80;
}
.bio-expand, .chat-expand {
    background-image: url(http://oi58.tinypic.com/14nhe05.jpg);
}
.chatBox .card-title {
    color: #D4D9E6;
    border-bottom: solid .0625rem #274B80;
}
.card-block .card-title {
    color: #D4D9E6;
}
.form-control {
	color: #D6FF0C;
    background-color: #D6FF00;
    border: 1px solid #274B80;
}
.card {
    background-color: #274B80;
}
#actionButtons {
	background-color: #274B80;
}
.list-group-item {
	background-color: #274B80;
}
.chatBox .youWrap {
    color: #D6FF00;
    background-color: #216D9E;
}
.chatBox .details {
	color: #FFFFFF;
}
.nav-tabs.chat-tabs .nav-link.active {
    color: #257EF6;
    border-bottom: solid .0625rem #216D9E;
}
.nav-tabs .nav-item.open .nav-link, .nav-tabs .nav-item.open .nav-link:focus, .nav-tabs .nav-item.open .nav-link:hover, .nav-tabs .nav-link.active, .nav-tabs .nav-link.active:focus, .nav-tabs .nav-link.active:hover {
	background-color: #173665;
}
.card {
	color: #84BBE8;
}
a.list-group-item, button.list-group-item {
	color: #81BFEC;
}
.note, .note-free, .progress[value], .stats .note-free {
    color: #D6FF0C;
}
.btn-simple {
    background-color: #173665;
}
.btn-simple.btn-default {
    color: #E6D468;
    border: .0625rem solid #E6D468;
}
.dropdown-menu {
    background-color: #274B80;
}
.chat-list a.list-group-item.activeConv, .chat-list a.list-group-item.activeConv:hover {
    background-color: #173665;
}
.chat-list-downbar, .downbar {
    border-top: solid .0625rem #216D9E;
}
.chatBox .details a {
    color: #6DA9DB;
}
.card {
    color: #8A90A3;
}
.activeConv .chatIndicator {
    background-image: url('http://oi57.tinypic.com/5035tu.jpg');
}
.chat-list .chatIndicator {
    background-image: url('http://oi57.tinypic.com/5035tu.jpg');
}
.chatBox .meWrap {
	color: #CAFF09;
    background-color: #216D9E;
}
.btn-simple {
    background-color: #0D2433;
}
.btn-simple.btn-default {
    color: #1C8AF2;
    border: .0625rem solid #1C8AF5;
}
a {
    color: #EFA45B;
}
.chat-list a.list-group-item.newMsg, .chat-list a.list-group-item.newMsg:hover {
    background-color: #173665;
}
.chat-list a.list-group-item.activeConv, .chat-list a.list-group-item.activeConv:hover {
    background-color: #173665;
}
a.list-group-item:focus, a.list-group-item:hover, button.list-group-item:focus, button.list-group-item:hover {
	background-color: #173665;
}
.form-control {
    color: #D6FF0C;
    background-color: #173665;
    border: 1px solid #274B80;
}
.userName {
    color: #D6FF0C;
}
#bottomStatus,
.bottomStatus {
    color: #274B80 !important;
    background-color: #173665 !important;
}
#bottomStatus.heavyLoad {
    color: #274B80 !important;
    background-color: #274B80 !important;
    background: #173665!important;
}
}
